DROP DATABASE IF EXISTS myPokeDataBase;
CREATE DATABASE myPokeDataBase;
USE myPokeDataBase;
CREATE TABLE Utilisateur(pseudo varchar(255) PRIMARY KEY, mdp TEXT, nom varchar(255), prenom varchar(255), mail varchar(255), birth date);
CREATE TABLE Tchat(id INT PRIMARY KEY NOT NULL AUTO_INCREMENT, pseudo varchar(255), message TEXT, `date` DATETIME);
CREATE TABLE Types(id INT NOT NULL PRIMARY KEY, nom TEXT NOT NULL, normal INT NOT NULL, combat FLOAT NOT NULL, vol FLOAT NOT NULL, poison FLOAT NOT NULL, sol FLOAT NOT NULL, roche FLOAT NOT NULL, insecte FLOAT NOT NULL, spectre FLOAT NOT NULL, acier FLOAT NOT NULL, feu FLOAT NOT NULL, eau FLOAT NOT NULL, plante FLOAT NOT NULL, electrik FLOAT NOT NULL, psy FLOAT NOT NULL, glace FLOAT NOT NULL, dragon FLOAT NOT NULL, `ténèbres` FLOAT NOT NULL, `fée` FLOAT NOT NULL);
CREATE TABLE Talent (id INT PRIMARY KEY, nom TEXT, description TEXT);
CREATE TABLE Attaque (id INT PRIMARY KEY, nom TEXT, `precision` TEXT, puissance TEXT, pp TEXT, type TEXT, categorie TEXT, description TEXT);
CREATE TABLE Pokemon (id INT PRIMARY KEY, id_national INT, nom TEXT, taille FLOAT, poids FLOAT, description TEXT, pv INT, attaque INT, defense INT, attaque_spe INT, defense_spe INT, vitesse INT, type1 TEXT, type2 TEXT, talent1 TEXT, talent2 TEXT, attaque_lv TEXT, attaque_CS TEXT, formes TEXT);
