INSERT INTO Talent VALUES (6, 'Agitation', 'Augmente votre attaque de 50% mais baisse votre précision de 20%. La probabilité rencontrer des Pokémon du plus haut niveau est augmentée de moitié si placé en tête d''équipe.');
