INSERT INTO Talent VALUES (8, 'Air Lock', 'Ignore les effets du climat.');
INSERT INTO Talent VALUES (9, 'Alerte Neige', 'Quand ce pokémon arrive dans le combat, la tempête de neige se déclenche.');
INSERT INTO Talent VALUES (10, 'Âme Vagabonde', 'Lorsque le Pokémon est directement touché par une attaque, il échange son talent avec celui de l''assaillant.');
INSERT INTO Talent VALUES (11, 'Amour Filial', 'Parent et enfant attaquent ensemble. Permet un deuxième coup infligeant 25% des dégâts du premier.');
INSERT INTO Talent VALUES (12, 'Analyste', 'Augmente de 30% le pouvoir de la capacité si le Pokémon attaque en dernier.');
INSERT INTO Talent VALUES (13, 'Animacoeur', 'Augmente l’Attaque Spéciale du Pokémon lorsqu’un autre Pokémon est mis K.O.');
INSERT INTO Talent VALUES (14, 'Annule Garde', 'Toutes les attaques ont une précision de 100% tant que ce Pokémon est en combat. Augmente de 50% les chances de rencontrer des Pokémon sauvages.');
