INSERT INTO Talent VALUES (16, 'Anticipation', 'Alerte lorsque le Pokémon adverse possède une attaque super efficace ou OHKO.');
INSERT INTO Talent VALUES (17, 'Aquabulle', 'Réduit les dégâts des capacités de type Feu subies par le Pokémon. Il est également immunisé contre les brûlures.');
INSERT INTO Talent VALUES (18, 'Armumagma', 'Ne peut pas être gelé. Les Œufs éclosent plus rapidement.');
INSERT INTO Talent VALUES (19, 'Armurbaston', 'Annule les coups critiques du Pokémon ennemi.');
INSERT INTO Talent VALUES (20, 'Armure Miroir', 'Le Pokémon renvoie les effets réducteurs de stats qu''il reçoit.');
