INSERT INTO Talent VALUES (173, 'Normalise', 'Toutes les attaques de ce Pokémon sont considérées comme de type normal.');
INSERT INTO Talent VALUES (174, 'Oeil Composé', 'Augmente la précision. Augmente les chances de rencontrer des Pokémon sauvages tenant un objet.');
INSERT INTO Talent VALUES (175, 'Osmose', 'Le Pokémon acquiert le talent d’un allié mis K.O.');
INSERT INTO Talent VALUES (176, 'Paratonnerre', 'Attire les attaques éléctriques en 2vs2. Depuis la 5G, immunise contre les dégâts de ces capacités et booste l''Attaque Spéciale d''un cran.');
INSERT INTO Talent VALUES (177, 'Pare-Balles', 'Protège le pokémon de certaines attaques balle ou bombe.');
INSERT INTO Talent VALUES (178, 'Peau Céleste', 'Les attaques de type Normal deviennent de type Vol et infligent des dégâts multipliés par 1,2.');
INSERT INTO Talent VALUES (179, 'Peau Dure', 'L''adversaire perd 1/8ème de ses PV max à chaque fois qu''il touche ce Pokémon.');
INSERT INTO Talent VALUES (180, 'Peau Électrique', 'Les capacités de type Normal deviennent de type Électrik. Leur puissance augmente légèrement.');
INSERT INTO Talent VALUES (181, 'Peau Féérique', 'Les attaques de type Normal deviennent de type Fée et infligent des dégâts multipliés par 1,2.');
INSERT INTO Talent VALUES (182, 'Peau Gelée', 'Les attaques de type Normal deviennent de type Glace et infligent des dégâts multipliés par 1,2.');
INSERT INTO Talent VALUES (183, 'Peau Miracle', 'Les attaques ayant pour seul effet de changer le statut ne fonctionnent qu''une fois sur deux.');
INSERT INTO Talent VALUES (184, 'Peau Sèche', 'Si une attaque eau est lancée contre ce Pokémon, elle lui restaurera ses PV. En contrepartie le Pokémon acquiert une faiblesse supplémentaire au feu. Si Danse-Pluie a été lancée, le Pokémon récupère 1/8ème de ses PV à chaque tour. Si Zenith a été lancé, l');
INSERT INTO Talent VALUES (185, 'Phobique', 'Monte la Vitesse d''un cran si le Pokémon est touché par une attaque de type Spectre, Insecte ou Ténèbres.');
INSERT INTO Talent VALUES (186, 'Pickpocket', 'Vole l''objet de l''ennemi si son attaque touche.');
INSERT INTO Talent VALUES (187, 'Pied Véloce', 'Multiplie la vitesse par 1.5 si affecté par une altération de statut (paralysie, poison...). Diminue de 50% la probabilité de rencontrer des Pokémon sauvages.');
INSERT INTO Talent VALUES (188, 'Pieds Confus', 'L''esquive du Pokémon est augmentée de 50% lorsqu''il est confus.');
INSERT INTO Talent VALUES (189, 'Piège Sable', 'Le Pokémon adverse ne peut pas s''échapper, sauf si il est de type vol, ou qu''il possède Lévitation. Double les chances de rencontrer un Pokémon sauvage.');
INSERT INTO Talent VALUES (190, 'Plus', 'Augmente l''Attaque Spéciale lorsque votre pokémon est allié à un Pokémon possédant la capacité spéciale minus (2vs2)');
INSERT INTO Talent VALUES (191, 'Poing de Fer', 'Les attaques Poing du Pokémon voient leurs dégats augmentés de 20%.');
INSERT INTO Talent VALUES (192, 'Poing Invisible', 'Permet de passer outre Abri et Détection en utilisant une capacité de contact.');
INSERT INTO Talent VALUES (193, 'Point Poison', 'Si le pokémon adverse fait une attaque directe contre votre pokémon, il aura 30% de chances d''être empoisonné');
INSERT INTO Talent VALUES (194, 'Poisseux', 'Un contact avec le pokémon diminue la vitesse du pokémon attaquant.');
INSERT INTO Talent VALUES (195, 'Pose Spore', 'Probabilité de 10% d''empoisonner, d''endormir ou de paralyser l''adversaire lorsque celui-ci touche le Pokémon.');
INSERT INTO Talent VALUES (196, 'Prédiction', 'Vous permet de voir l''attaque la plus puissante du Pokémon adverse.');
INSERT INTO Talent VALUES (197, 'Pression', 'Le Pokémon défenseur consomme deux PP au lieu d''un seul pour attaquer. La probabilité rencontrer des Pokémon du plus haut niveau est augmentée de moitié si placé en tête d''équipe.');
INSERT INTO Talent VALUES (198, 'Prestance Royale', 'L’adversaire est impressionné par la majesté du Pokémon et ne peut pas le viser avec une attaque prioritaire.');
INSERT INTO Talent VALUES (199, 'Prioguérison', 'Toutes les capacités qui soignent le Pokémon directement deviennent prioritaires.');
INSERT INTO Talent VALUES (200, 'Prisme-Armure', 'Réduit les dégâts subis lorsque le Pokémon est touché par une capacité super efficace.');
INSERT INTO Talent VALUES (201, 'Prognathe', 'Augmente la puissance des attaques croc.');
INSERT INTO Talent VALUES (202, 'Propulseur', 'Permet d''ignorer l''effet des capacités ou des talents qui attirent les capacités.');
INSERT INTO Talent VALUES (203, 'Protéen', 'Change le type du pokémon pour celui de l''attaque qu''il vient de lancer.');
INSERT INTO Talent VALUES (204, 'Puanteur', 'Diminue de 50% la probabilité de rencontrer des Pokémon sauvages. Octroie 10% de chance d''apeurer.');
INSERT INTO Talent VALUES (205, 'Punk Rock', 'Augmente la puissance des capacités basées sur le son. Le Pokémon ne subit que la moitié des dégâts quand il est touché par ce genre de capacités.');
INSERT INTO Talent VALUES (206, 'Querelleur', 'Permet au pokémon de toucher les pokémon du type spectre avec des attaques du type normal ou combat.');
INSERT INTO Talent VALUES (207, 'Rage Brûlure', 'Augmente l''Att. Spé de 50% si le statut est brûlure.');
INSERT INTO Talent VALUES (208, 'Rage Poison', 'Augmente l''Attaque de 50% si le statut est poison.');
INSERT INTO Talent VALUES (209, 'Ramassage', 'Peut ramasser des objets');
INSERT INTO Talent VALUES (210, 'Ramasse Ball', 'Si le Pokémon ne tient aucun objet, il ramassera la Poké Ball lors du premier lancer raté du combat.');
INSERT INTO Talent VALUES (211, 'Rassemblement', 'Lorsque le Pokémon perd la moitié de ses PV, ses Cellules se rassemblent pour l’encourager, ce qui lui permet de prendre sa Forme Parfaite.');
INSERT INTO Talent VALUES (212, 'Receveur', 'Le Pokémon reçoit le talent d’un allié mis K.O.');
INSERT INTO Talent VALUES (213, 'Récolte', 'Les Baies peuvent être réutilisées indéfiniment durant un combat.');
INSERT INTO Talent VALUES (214, 'Regard Vif', 'Empêche perte de précision. Diminue de 50% la probabilité rencontrer des Pokémon de faible niveau si placé en tête d''équipe.');
INSERT INTO Talent VALUES (215, 'Régé-Force', 'Restaure un tiers des PV max si le Pokemon est retiré.');
INSERT INTO Talent VALUES (216, 'Repli Tactique', 'Le Pokémon évite les situations inutilement dangereuses. Quand ses PV tombent à la moitié, il se réfugie dans sa Poké Ball.');
INSERT INTO Talent VALUES (217, 'Rideau Neige', 'Diminue la précision des attaques ennemies de 20 % sous la grêle. Diminue de 50% la probabilité de rencontrer des Pokémon sauvages dans une tempête de neige (grêle).');
INSERT INTO Talent VALUES (218, 'Rivalité', 'Augmente l''Attaque du pokémon si l''adversaire est du même sexe');
INSERT INTO Talent VALUES (219, 'Sable Humide', 'Augmente beaucoup la Défense du Pokémon quand il subit une capacité de type Eau.');
INSERT INTO Talent VALUES (220, 'Sable Volant', 'Le Pokémon invoque une tempête de sable dès son arrivée en jeu.');
INSERT INTO Talent VALUES (221, 'Sans Limite', 'Frappe plus fort mais annule les effets cumulés.');
INSERT INTO Talent VALUES (222, 'Sécheresse', 'Le Pokémon invoque le soleil dès son arrivée en jeu.');
INSERT INTO Talent VALUES (223, 'Sérénité', 'Double la réussite aux effets des attaques lancées');
INSERT INTO Talent VALUES (224, 'Simple', 'Double l''effet des attaques et objets de boost.');
INSERT INTO Talent VALUES (225, 'Sniper', 'La puissance des coups critiques est triplée.');
INSERT INTO Talent VALUES (226, 'Soin Poison', 'Restaure 1/8ème des PV max à la fin de chaque tour lorsque le Pokémon est empoisonné.');
INSERT INTO Talent VALUES (227, 'Solide Roc', 'Toutes les attaques super efficaces sur ce pokémon ont leurs dégats diminués.');
INSERT INTO Talent VALUES (228, 'Souffle Delta', 'Altère les conditions météo et annule les faiblesses du type Vol.');
INSERT INTO Talent VALUES (229, 'Spectro-Bouclier', 'Le Pokémon reçoit moins de dégâts quand ses PV sont au maximum.');
INSERT INTO Talent VALUES (230, 'Statik', 'Probabilité de 30% de paralyser l''adversaire lorsque celui-ci touche le Pokémon. 50% de chance de rencontrer un Pokémon Électrik si placé en tête d''équipe.');
INSERT INTO Talent VALUES (231, 'Suintement', 'Si le pokémon adverse essaie d''absorber vos PV, c''est lui qui en perdra');
INSERT INTO Talent VALUES (232, 'Surf Caudal', 'La Vitesse du Pokémon est doublée sur un Champ Électrifié.');
INSERT INTO Talent VALUES (233, 'Symbiose', 'Le pokémon peut donner un objet à un allié.');
INSERT INTO Talent VALUES (234, 'Synchro', 'Transfère le problème de statut. 50% de chance de rencontrer des Pokémon sauvages de la même nature que le Pokémon si placé en tête d''équipe.');
INSERT INTO Talent VALUES (235, 'Synergie', 'En battant un ennemi, ce Pokémon renforce ses liens avec son Dresseur. Il prend alors sa Forme Sacha et ses Sheauriken gagnent en puissance.');
INSERT INTO Talent VALUES (236, 'Système Alpha', 'Change le type du Pokémon en fonction de la ROM équipée.');
INSERT INTO Talent VALUES (237, 'Technicien', 'La puissance des attaques avec une puissance inférieure ou égale à 60 est augmentée de 50%.');
INSERT INTO Talent VALUES (238, 'Télécharge', 'En fonction de la défense la plus faible de l''ennemi, le Pokémon augmente son attaque ou son attaque spéciale.');
INSERT INTO Talent VALUES (239, 'Télépathe', 'Anticipe et évite les attaques de ses alliés.');
INSERT INTO Talent VALUES (240, 'Téméraire', 'Tous les coups induisant des dégats de recul augmentent de 20 %.');
INSERT INTO Talent VALUES (241, 'Tempo Perso', 'Ce Pokémon ne peut être confus.');
INSERT INTO Talent VALUES (242, 'Tension', 'L''ennemi stresse et ne peut plus manger de baies.');
INSERT INTO Talent VALUES (243, 'Téra-Voltage', 'Ignore les capacités spéciales adverses lorsqu''il lance une attaque.');
INSERT INTO Talent VALUES (244, 'Terre Finale', 'Altère les conditions météo et rend inefficaces les attaques de type Eau.');
INSERT INTO Talent VALUES (245, 'Tête de Gel', 'Le glaçon sur sa tête encaisse les attaques physiques à la place du Pokémon, mais sa destruction modifie son apparence. Le glaçon se reforme quand il grêle.');
INSERT INTO Talent VALUES (246, 'Tête de Roc', 'Votre Pokémon ne subit pas de dégats du recul, en exécutant des attaques telles que Damoclès.');
INSERT INTO Talent VALUES (247, 'Tir Vif', 'Le Pokémon a 30% de chance d''attaquer en premier avec une capacité offensive. (même priorité)');
INSERT INTO Talent VALUES (248, 'Toison Épaisse', 'Les dégats des attaques physiques sont réduits de moitié.');
INSERT INTO Talent VALUES (249, 'Toison Herbue', 'Augmente la Défense lorsque le terrain est herbe.');
INSERT INTO Talent VALUES (250, 'Torche', 'Les attaques feu reçues par ce Pokémon sont absorbées et augmentent la puissance de ses propres attaques feu.');
INSERT INTO Talent VALUES (251, 'Torrent', 'Donne un bonus mulitiplicateur de X1.5 aux attaques eau lorsque les PV de ce Pokémon sont inférieurs à 1/3.');
INSERT INTO Talent VALUES (252, 'Toxitouche', 'Peut empoisonner l''ennemi par simple contact.');
INSERT INTO Talent VALUES (253, 'Turbine', 'Lorsque le Pokémon est touché par des capacités de type Eau ou Feu, sa Vitesse augmente énormément.');
INSERT INTO Talent VALUES (254, 'Turbo', 'La vitesse de votre pokémon augmente à chaque tour');
INSERT INTO Talent VALUES (255, 'Turbo Brasier', 'Ignore les capacités spéciales adverses lorsqu''il lance une attaque.');
INSERT INTO Talent VALUES (256, 'Vaccin', 'Votre Pokémon ne peut pas être empoisonné.');
INSERT INTO Talent VALUES (257, 'Ventouse', 'Votre pokémon ne peut pas etre forcé de battre en retraite par des attaques telles que Hurlement ou Cyclone. Augmente les chances de pêcher un Pokémon.');
INSERT INTO Talent VALUES (258, 'Victorieux', 'Monte la Précision des Pokémon de l''équipe de 10%.');
INSERT INTO Talent VALUES (259, 'Voile Pastel', 'Protège le Pokémon et ses alliés contre toutes les altérations de statut liées à l''empoisonnement.');
INSERT INTO Talent VALUES (260, 'Voile Sable', 'Diminue la précision des attaques ennemies de 20 % sous la tempête de sable. Diminue de 50% la probabilité de rencontrer des Pokémon sauvages dans une tempête de sable.');
INSERT INTO Attaque VALUES (1, 'À la Queue', '100', '-', '15', 'Ténèbres', 'Statut', 'Retient la cible de force, l''obligeant à agir en dernier.');
INSERT INTO Attaque VALUES (2, 'Abattage', '100', '60', '15', 'Dragon', 'Physique', 'Le lanceur balaie violemment le camp adverse avec son immense queue. Baisse l''Attaque de la cible.');
INSERT INTO Attaque VALUES (3, 'Abîme', '30', '1', '5', 'Sol', 'Physique', 'L''attaque inflige des dégâts égaux aux PVs max de la cible. Si celle-ci a lancé Tenacité ou tient la Ceinture Force, elle résistera avec 1 PV. Si Verrouillage ou Lire-Esprit ont été utilisées avant, ou si l''un des Pokémon possède la capacité Annule Garde, Abime touchera. Abime ignore les changements d''esquive et de précision, mais possède sa propre précision :');
INSERT INTO Attaque VALUES (4, 'Aboiement', '95', '55', '15', 'Ténèbres', 'Spéciale', 'Le lanceur hurle sur l''ennemi. Baisse l''Attaque Spéciale de l''ennemi.');
INSERT INTO Attaque VALUES (5, 'Abri', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur se protège de toutes les attaques. Peut échouer si utilisée plusieurs fois de suite.');
INSERT INTO Attaque VALUES (6, 'Acidarmure', '-', '-', '20', 'poison', 'Statut', 'Le lanceur modifie sa structure moléculaire pour se liquéfier et beaucoup augmenter sa Défense.');
INSERT INTO Attaque VALUES (7, 'Acide Malique', '100', '80', '10', 'Plante', 'Spéciale', 'Le lanceur attaque son adversaire avec un liquide corrosif créé à partir d''une pomme acide. Baisse la Défense Spéciale de la cible.');
INSERT INTO Attaque VALUES (8, 'Acide', '100', '40', '30', 'poison', 'Spéciale', 'L''attaque a 10% de chances de baisser la Défense Spéciale de chaque cible d''un niveau.');
INSERT INTO Attaque VALUES (9, 'Acrobatie', '100', '55', '15', 'Vol', 'Physique', 'Attaque agile. Si le lanceur ne tient pas d''objet, l''attaque inflige davantage de dégâts.');
INSERT INTO Attaque VALUES (10, 'Acupression', '-', '-', '30', 'Normal', 'Statut', 'Si une ou plusieurs statistiques du lanceur peuvent être augmentées, alors Acupression augmente une de ses statistiques de deux niveaux, choisie aléatoirement avec une probabilité égale pour chacune des statistiques pouvant être augmentées. Si aucune statistique ne peut être augmentée, Acupression échoue.');
INSERT INTO Attaque VALUES (11, 'Conversion', '-', '-', '30', 'Normal', 'Statut', 'Le lanceur transforme son type en celui d''une de ses attaques, choisie aléatoirement. Le lanceur sera toujours transformé en un type différent. Malédiction ne peut être choisi par Adaptation. Puissance Cachée et Ball''Meteo sont considérées comme de type Normal. L''attaque échoue si toutes les attaques du lanceur sont du même type que lui.');
INSERT INTO Attaque VALUES (12, 'Aegis Maxima', '100', '100', '5', 'Acier', 'Physique', 'Le lanceur se transforme en un immense bouclier et charge son adversaire. Inflige le double de dégâts aux Pokémon Dynamax.');
INSERT INTO Attaque VALUES (13, 'Aéroblast', '95', '100', '5', 'Vol', 'Spéciale', 'L''attaque possède un taux de coup critique supérieur d''un niveau à la normale.');
INSERT INTO Attaque VALUES (14, 'Aéromax', '-', '10', '10', 'Vol', 'Physique', 'Une attaque de type Vol que seuls les Pokémon Dynamax peuvent utiliser. Augmente la Vitesse des alliés.');
INSERT INTO Attaque VALUES (15, 'Aéropique', '-', '60', '20', 'Vol', 'Physique', 'Le lanceur prend l''ennemi de vitesse et le lacère. N''échoue jamais.');
INSERT INTO Attaque VALUES (16, 'Affilage', '-', '-', '30', 'Normal', 'Statut', 'Le lanceur se concentre pour être sûr de porter un coup critique au tour suivant.');
INSERT INTO Attaque VALUES (17, 'Affûtage', '-', '-', '30', 'Normal', 'Statut', 'L''attaque monte l''attaque du lanceur d''un cran.');
INSERT INTO Attaque VALUES (18, 'Aiguisage', '-', '-', '15', 'Ténèbres', 'Statut', 'Le lanceur s''aiguise les griffes. Augmente l''Attaque et la Précision.');
INSERT INTO Attaque VALUES (19, 'Ailes d''Acier', '90', '70', '25', 'Acier', 'Physique', 'Le lanceur frappe l''ennemi avec des ailes d''acier. Peut aussi augmenter la Défense du lanceur.');
INSERT INTO Attaque VALUES (20, 'Air Veinard', '-', '-', '30', 'Normal', 'Statut', 'L''adversaire ne peut pas faire de coup critique pendant 5 tours');
INSERT INTO Attaque VALUES (21, 'Aire d''Eau', '100', '80', '10', 'Eau', 'Spéciale', 'Une masse d''eau s''abat sur l''ennemi. En l''utilisant avec Aire de Feu, l''effet augmente et un arc-en-ciel apparaît.');
INSERT INTO Attaque VALUES (22, 'Aire d''Herbe', '100', '80', '10', 'Plante', 'Spéciale', 'Une masse végétale s''abat sur l''ennemi. En l''utilisant avec Aire d''Eau, l''effet augmente et un marécage apparaît.');
INSERT INTO Attaque VALUES (23, 'Aire de Feu', '100', '80', '10', 'Feu', 'Spéciale', 'Une masse de feu s''abat sur l''ennemi. En l''utilisant avec Aire d''Herbe, l''effet augmente et une mer de feu apparaît.');
INSERT INTO Attaque VALUES (24, 'Allègement', '-', '-', '15', 'Acier', 'Statut', 'Le lanceur se débarrasse des parties inutiles de son corps. Son poids diminue et sa Vitesse augmente beaucoup.');
INSERT INTO Attaque VALUES (25, 'Amass''Sable', '-', '-', '10', 'Sol', 'Statut', 'Le lanceur récupère jusqu''à la moitié de ses PV max. Durant une tempête de sable, il en récupère encore plus.');
INSERT INTO Attaque VALUES (26, 'Amnésie', '-', '-', '20', 'Psy', 'Statut', 'Le lanceur fait le vide dans son esprit pour oublier ses soucis. Augmente beaucoup sa Défense Spéciale.');
INSERT INTO Attaque VALUES (27, 'Ampleur', '100', '1', '30', 'Sol', 'Physique', 'Un tremblement de terre d''intensité variable.');
INSERT INTO Attaque VALUES (28, 'Ancrage', '100', '80', '20', 'Acier', 'Physique', 'Le lanceur jette son ancre sur la cible pour l''attaquer. Une fois accrochée, elle l''empêche de s''enfuir.');
INSERT INTO Attaque VALUES (29, 'Anneau Hydro', '-', '-', '20', 'Eau', 'Statut', 'Le lanceur regagne 1/16ème de ses PV Max à la fin de chaque tour. L''effet disparait si le lanceur switche ou est remplacé via Demi-Tour.');
INSERT INTO Attaque VALUES (30, 'Anti-Air', '100', '50', '15', 'Roche', 'Physique', 'Le lanceur jette toutes sortes de projectiles à un ennemi. Si ce dernier vole, il tombe au sol.');
INSERT INTO Attaque VALUES (31, 'Anti-Brume', '-', '-', '15', 'Vol', 'Statut', 'Un grand coup de vent qui disperse la Protection ou le Mur Lumière de l''ennemi. Diminue aussi son Esquive.');
INSERT INTO Attaque VALUES (32, 'Anti-Soin', '100', '-', '15', 'Psy', 'Statut', 'Pendant 5 tours, la cible ne peut utiliser les attaques suivantes : Appel Soins, Atterrissage, Aurore, Avale, Lait A Boire, Paresse, Rayon Lune, Repos, Soin, Synthèse ou Voeu. Anti-Soin n''annule pas les regains de Boue Noire, Grelot Coque ou Restes. Anneau-Hydro et Racines ne restaurent pas un Pokémon sous Anti-Soin.');
INSERT INTO Attaque VALUES (33, 'Apocalypse Gigalithique', '-', '1', '-', 'Roche', 'Physique', 'Le Pokémon utilise la Force Z pour créer un immense rocher et écraser l’adversaire avec. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (34, 'Apocalypse Gigalithique', '-', '1', '-', 'Roche', 'Spéciale', 'Le Pokémon utilise la Force Z pour créer un immense rocher et écraser l’adversaire avec. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (35, 'Apocalypsis Luminis', '-', '200', '-', 'Psy', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (36, 'Appel Attaque', '100', '90', '15', 'Insecte', 'Physique', 'L''attaque possède un taux de coup critique de +1.');
INSERT INTO Attaque VALUES (37, 'Appel Défense', '-', '-', '10', 'Insecte', 'Statut', 'Augmente la Défense et la Défense Spéciale du lanceur d''un niveau.');
INSERT INTO Attaque VALUES (38, 'Appel des Ombres Éternelles', '-', '1', '-', 'Spectre', 'Physique', 'Le Pokémon utilise la Force Z pour invoquer des esprits rancuniers qui s’abattent sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (39, 'Appel des Ombres Éternelles', '-', '1', '-', 'Spectre', 'Spéciale', 'Le Pokémon utilise la Force Z pour invoquer des esprits rancuniers qui s’abattent sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (40, 'Appel Soins', '-', '-', '10', 'Insecte', 'Statut', 'Restaure la moitié des PV Max.');
INSERT INTO Attaque VALUES (41, 'Après Vous', '-', '-', '15', 'Normal', 'Statut', 'S''il est le premier à agir, le lanceur permet à sa cible d''utiliser une capacité juste après lui.');
INSERT INTO Attaque VALUES (42, 'Aqua-Brèche', '100', '85', '10', 'Eau', 'Physique', 'Le lanceur utilise la force de l''eau pour attaquer. Peut aussi baisser la Défense de la cible.');
INSERT INTO Attaque VALUES (43, 'Aqua-Jet', '100', '40', '20', 'Eau', 'Physique', 'Attaque à priorité +1.');
INSERT INTO Attaque VALUES (44, 'Aria de l''Écume', '100', '90', '10', 'Eau', 'Spéciale', 'Le lanceur émet plusieurs bulles en chantant. Soigne les brûlures des Pokémon touchés par ces bulles.');
INSERT INTO Attaque VALUES (45, 'Armure', '-', '-', '30', 'Normal', 'Statut', 'Le lanceur contracte tous ses muscles pour augmenter sa Défense.');
INSERT INTO Attaque VALUES (46, 'Aromathérapie', '-', '-', '5', 'Plante', 'Statut', 'Soigne la brûlure, la confusion, le gel, la paralysie, le poison et le sommeil de tous les Pokémon dans l''équipe du lanceur.');
INSERT INTO Attaque VALUES (47, 'Arrogance', '100', '20', '10', 'Ténèbres', 'Physique', 'Ivre de puissance, le lanceur attaque de toutes ses forces. Plus ses stats ont été augmentées, plus la puissance de l''attaque est élevée.');
INSERT INTO Attaque VALUES (48, 'Assistance', '-', '-', '20', 'Normal', 'Statut', 'L''attaque utilise une attaque choisie au hasard sur un Pokémon au hasard de l''équipe, autre que le lanceur et un oeuf. Peut choisir une attaque sur un Pokémon K.O. Assistance ne peut choisir une attaque parmi les attaques suivantes :');
INSERT INTO Attaque VALUES (49, 'Assurance', '100', '60', '10', 'Ténèbres', 'Physique', 'Cette attaque est deux fois plus efficace si l''ennemi a déjà été blessé durant ce tour.');
INSERT INTO Attaque VALUES (50, 'Astuce Force', '-', '-', '10', 'Psy', 'Statut', 'Le lanceur utilise ses pouvoirs psychiques pour échanger sa Défense et son Attaque.');
INSERT INTO Attaque VALUES (51, 'Atout', '-', '1', '5', 'Normal', 'Spéciale', 'Si les PPs d''Atout après utilisation sont de');
INSERT INTO Attaque VALUES (52, 'Atterrissage', '-', '-', '10', 'Vol', 'Statut', 'Le lanceur atterrit et se repose. Restaure jusqu''à la moitié de ses PV max.');
INSERT INTO Attaque VALUES (53, 'Attraction', '100', '-', '15', 'Normal', 'Statut', 'Si l''ennemi est du sexe opposé, il tombe amoureux et rechigne alors à attaquer.');
INSERT INTO Attaque VALUES (54, 'Attrition', '100', '70', '20', 'Normal', 'Physique', 'Une attaque puissante quand l’ennemi baisse sa garde. Inflige des dégâts sans tenir compte des changements de stats.');
INSERT INTO Attaque VALUES (55, 'Aurasphère', '-', '80', '20', 'Combat', 'Spéciale', 'Le lanceur dégage une aura et projette de l''énergie. N''échoue jamais.');
INSERT INTO Attaque VALUES (56, 'Aurore', '-', '-', '5', 'Normal', 'Statut', 'L''attaque soigne 1/2 de la vie du lanceur s''il n''y a pas de climat, 2/3 sous le Soleil, 1/4 sous tout autre climat.');
INSERT INTO Attaque VALUES (57, 'Avalanche', '100', '60', '10', 'Glace', 'Physique', 'Une attaque deux fois plus puissante si le lanceur a été blessé par l''ennemi durant le tour.');
INSERT INTO Attaque VALUES (58, 'Avale', '-', '-', '10', 'Normal', 'Statut', 'Les soins sont de 25% si Stockage n''a été lancé qu''une fois, de 50% pour 2 Stockages et de 100% pour 3 Stockages. Une fois lancée, les Stockages sont perdus. Avale ne retire que les bonus défensifs octroyés par Stockage. Si le lanceur avait lancé d''autres boosts défensifs que Stockage, ceux-ci seront toujours actifs, même après Avale.');
INSERT INTO Attaque VALUES (59, 'Babil', '100', '65', '20', 'Vol', 'Spéciale', 'L''attaque a 1%, 11% ou 31% de rendre la cible confus, en fonction du son enregistré. Babil ne rend pas l''adversaire confus si le lanceur n''est pas Pijako.');
INSERT INTO Attaque VALUES (60, 'Bâillement', '-', '-', '10', 'Normal', 'Statut', 'La cible s''endort à la fin du tour suivant. Bâillement ignore les variations d''esquive et de précision. Si l''adversaire switche avant la réalisation de l''effet ou utilise Demi-Tour ou Relais, la cible ne s''endort pas.');
INSERT INTO Attaque VALUES (61, 'Bain de Smog', '-', '50', '15', 'poison', 'Spéciale', 'Le lanceur jette un tas de détritus spéciaux sur la cible. Les changements de stats de la cible sont annulés.');
INSERT INTO Attaque VALUES (62, 'Balance', '-', '-', '20', 'Normal', 'Statut', 'L''attaque ajoute les PV du lanceur et de la cible et les divise par deux. Ils sont ensuite redonnés aux deux Pokémon. Balance ignore les modifications d''esquive ou de précision, et échoue devant un Clone. L''attaque n''est pas affectée par les immunités.');
INSERT INTO Attaque VALUES (63, 'Balayage', '100', '1', '20', 'Combat', 'Physique', 'Un puissant coup de pied bas qui fauche l''ennemi. Il est plus efficace contre les ennemis lourds.');
INSERT INTO Attaque VALUES (64, 'Balayette', '100', '65', '20', 'Combat', 'Physique', 'Un coup rapide qui affecte la mobilité de l''ennemi et diminue sa Vitesse.');
INSERT INTO Attaque VALUES (65, 'Ball''Brume', '100', '70', '5', 'Psy', 'Spéciale', 'L''attaque a 50% de chance de baisser l''Attaque Spéciale de la cible d''un niveau.');
INSERT INTO Attaque VALUES (66, 'Ball''Glace', '90', '30', '20', 'Glace', 'Physique', 'La puissance double à chaque coup consécutif. L''attaque bloque l''utilisateur pour 5 tours, mais l''effet s''arrête si l''une des attaque rate ou si le lanceur ne peut pas relancer Ball''Glace. Si le lanceur choisit à nouveau Ball''Glace après la fin de l''effet, la puissance est réinitialisée. Utiliser Ball''Glace avant Roulade double la puissance de base de Ball''Glace tant que le lanceur ne switche pas.');
INSERT INTO Attaque VALUES (67, 'Ball''Météo', '100', '50', '10', 'Normal', 'Spéciale', 'Une attaque dont la puissance et le type varient en fonction du temps qu''il fait.');
INSERT INTO Attaque VALUES (68, 'Ball''Ombre', '100', '80', '15', 'Spectre', 'Spéciale', 'Projette une grande ombre sur l''ennemi. Peut aussi faire baisser sa Défense Spéciale.');
INSERT INTO Attaque VALUES (69, 'Balle Graine', '100', '25', '30', 'Plante', 'Physique', 'Le lanceur mitraille l''ennemi avec une rafale de graines. De deux à cinq rafales sont lancées à la suite.');
INSERT INTO Attaque VALUES (70, 'Ballon Brûlant', '90', '120', '5', 'Feu', 'Physique', 'Le lanceur attaque avec un ballon fait à partir d''un caillou enflammé. Peut aussi brûler la cible.');
INSERT INTO Attaque VALUES (71, 'Bang Sonique', '100', '140', '10', 'Normal', 'Spéciale', 'Attaque les Pokémon alentour grâce à une onde sonore assourdissante qui détruit tout sur son passage.');
INSERT INTO Attaque VALUES (72, 'Barrage', '-', '-', '5', 'Normal', 'Statut', 'La cible ne peut pas switcher. L''effet disparait lorsque le lanceur switche. Barrage ignore les modifications de précision et d''esquive. N''empêche pas la fuite avec Relais, Demi-Tour ou avec l''objet Carapace Mue.');
INSERT INTO Attaque VALUES (73, 'Baston', '100', '1', '10', 'Ténèbres', 'Physique', 'Le lanceur appelle tous les Pokémon de son équipe à attaquer. Plus ils sont nombreux, plus il y a d''attaques.');
INSERT INTO Attaque VALUES (74, 'Bec-Canon', '100', '100', '15', 'Vol', 'Physique', 'Le lanceur fait chauffer son bec avant d''attaquer. S''il subit une attaque directe pendant la montée en température, l''attaquant sera brûlé.');
INSERT INTO Attaque VALUES (75, 'Bec Vrille', '100', '80', '20', 'Vol', 'Physique', 'Une attaque utilisant le bec comme une perceuse.');
INSERT INTO Attaque VALUES (76, 'Bélier', '85', '90', '20', 'Normal', 'Physique', 'Le lanceur s''inflige 1/4 des dégâts infligés à la cible, arrondis à l''inférieur.');
INSERT INTO Attaque VALUES (77, 'Berceuse', '55', '-', '15', 'Normal', 'Statut', 'L''adversaire est endormi.');
INSERT INTO Attaque VALUES (78, 'Big Splash', '100', '80', '10', 'Combat', 'Physique', 'Le lanceur utilise son corps pour attaquer sa cible. Plus la Défense du lanceur est élevée, plus les dégâts infligés sont importants.');
INSERT INTO Attaque VALUES (79, 'Blabla Dodo', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur utilise une de ses capacités au hasard pendant qu''il dort.');
INSERT INTO Attaque VALUES (80, 'Blizzard', '70', '110', '5', 'Glace', 'Spéciale', 'Une violente tempête de neige s''abat sur l''ennemi. Peut aussi le geler.');
INSERT INTO Attaque VALUES (81, 'Blocage', '100', '-', '10', 'Ténèbres', 'Statut', 'Le lanceur se protège de toutes les attaques. Peut échouer si utilisée plusieurs fois de suite. Baisse beaucoup la Défense de l''assaillant s''il utilise une attaque directe.');
INSERT INTO Attaque VALUES (82, 'Blockhaus', '-', '-', '10', 'poison', 'Statut', 'Protège le lanceur contre les attaques de l''ennemi et empoisonne ce dernier s''il utilise une attaque directe sur le lanceur.');
INSERT INTO Attaque VALUES (83, 'Bluff', '100', '40', '10', 'Normal', 'Physique', 'Attaque à priorité +1 qui apeure l''ennemi. Ne peut être utilisée qu''au premier tour sur le terrain du lanceur. Switcher permet de relancer Bluff.');
INSERT INTO Attaque VALUES (84, 'Bombe Acide', '100', '40', '20', 'poison', 'Spéciale', 'Projette un liquide acide qui fait fondre l''ennemi. Sa Défense Spéciale diminue beaucoup.');
INSERT INTO Attaque VALUES (85, 'Bombe Aimant', '-', '60', '20', 'Acier', 'Physique', 'L''attaque ignore les modifications de précision et d''esquive.');
INSERT INTO Attaque VALUES (86, 'Bombe Beurk', '100', '90', '10', 'poison', 'Spéciale', 'Des détritus toxiques sont projetés sur l''ennemi. Peut aussi l''empoisonner.');
INSERT INTO Attaque VALUES (87, 'Bombe Oeuf', '75', '100', '10', 'Normal', 'Physique', 'Envoie violemment un oeuf en direction de l''ennemi.');
INSERT INTO Attaque VALUES (88, 'Boost', '-', '-', '10', 'Normal', 'Statut', 'Une autohypnose qui permet au lanceur de copier les changements de stats de la cible.');
INSERT INTO Attaque VALUES (89, 'Botte Sucrette', '100', '70', '15', 'Plante', 'Physique', 'Un coup de pied chaud comme les tropiques qui inflige des dégâts à la cible et baisse son Attaque.');
INSERT INTO Attaque VALUES (90, 'Bouclier Royal', '-', '-', '10', 'Acier', 'Statut', 'Prend une posture défensive pour bloquer les dégâts. Diminue l''Attaque de tout Pokémon qui entre en contact avec le lanceur.');
INSERT INTO Attaque VALUES (91, 'Bouclier', '-', '-', '20', 'Psy', 'Statut', 'Augmente la Défense du lanceur de deux niveaux.');
INSERT INTO Attaque VALUES (92, 'Boue-Bombe', '85', '65', '10', 'Sol', 'Spéciale', 'L''attaque a 30% de chance de baisser la précision de l''adversaire.');
INSERT INTO Attaque VALUES (93, 'Boul''Armure', '-', '-', '40', 'Normal', 'Statut', 'Augmente la Défense du lanceur d''un niveau. Double la puissance de Roulade ou Ball''Glace pour le lanceur. Le boost de puissance n''est pas transmis par Relais.');
INSERT INTO Attaque VALUES (94, 'Boule Élek', '100', '1', '10', 'Electrik', 'Spéciale', 'Le lanceur envoie une boule d''électricité. Si sa Vitesse est plus grande que celle de l''ennemi, les dégâts augmentent d''autant.');
INSERT INTO Attaque VALUES (95, 'Boule Pollen', '100', '90', '15', 'Insecte', 'Spéciale', 'Utilisée sur l''ennemi, envoie une boule explosive qui fait des dégâts ; sur un allié, donne du bon pollen nutritif qui fait récupérer des PV.');
INSERT INTO Attaque VALUES (96, 'Boule Roc', '90', '25', '10', 'Roche', 'Physique', 'Le lanceur projette un rocher sur l''ennemi de deux à cinq fois d''affilée.');
INSERT INTO Attaque VALUES (97, 'Bourdon', '100', '90', '10', 'Insecte', 'Spéciale', 'Le lanceur fait vibrer son corps pour lancer une vague sonique. Peut aussi baisser la Défense Spéciale de l''ennemi.');
INSERT INTO Attaque VALUES (98, 'Boutefeu', '100', '120', '15', 'Feu', 'Physique', 'Le lanceur s''embrase avant de charger l''ennemi. Le choc blesse aussi gravement le lanceur. Peut brûler l''ennemi.');
INSERT INTO Attaque VALUES (99, 'Branchicrok', '100', '85', '10', 'Eau', 'Physique', 'Le lanceur mord sa cible avec ses solides branchies. Si cette capacité est lancée avant que la cible n''attaque, elle infligera deux fois plus de dégâts.');
INSERT INTO Attaque VALUES (100, 'Brouhaha', '100', '90', '10', 'Normal', 'Spéciale', 'Le lanceur attaque en rugissant durant trois tours. Pendant ce temps, aucun Pokémon ne peut s''endormir.');
INSERT INTO Attaque VALUES (101, 'Brouillard', '100', '-', '20', 'Normal', 'Statut', 'L''attaque baisse la précision adverse d''un cran.');
INSERT INTO Attaque VALUES (102, 'Brume Capiteuse', '-', '-', '20', 'Fée', 'Statut', 'Grâce à un parfum mystérieux, augmente la Défense Spéciale d''un allié.');
INSERT INTO Attaque VALUES (103, 'Brume', '-', '-', '30', 'Glace', 'Statut', 'Empêche les attaques ou capacités de l''adversaire qui baissent les statistiques de l''équipe de l''utilisateur pendant cinq tours. Brume ne protège pas de Permucoeur, Permuforce ou Permugarde.');
INSERT INTO Attaque VALUES (104, 'Buée Noire', '-', '-', '30', 'Glace', 'Statut', 'Ramène toutes les statistiques des Pokémon à leurs niveaux de base. Cette attaque n''est pas bloquée par Corps Sain ou Ecran Fumée.');
INSERT INTO Attaque VALUES (105, 'Bulldoboule', '100', '65', '20', 'Insecte', 'Physique', 'Le lanceur se roule en boule et écrase son ennemi. Peut aussi apeurer l''ennemi.');
INSERT INTO Attaque VALUES (106, 'Bulles d''O', '100', '65', '20', 'Eau', 'Spéciale', 'L''attaque a 10% de chance de baisser la Vitesse de la cible d''un niveau.');
INSERT INTO Attaque VALUES (107, 'Caboche-Kaboum', '100', '150', '5', 'Feu', 'Spéciale', 'Le lanceur fait exploser sa tête pour attaquer toutes les cibles autour de lui. Il subit aussi des dégâts.');
INSERT INTO Attaque VALUES (108, 'Cadeau', '90', '1', '15', 'Normal', 'Physique', 'Effectue aléatoirement une attaque à 40, 80 ou 120 de puissance, ou soigne 80 PV de la cible.');
INSERT INTO Attaque VALUES (109, 'Cage Éclair', '90', '-', '20', 'Electrik', 'Statut', 'Un faible choc électrique frappe l''ennemi. Si l''attaque le touche, celui-ci est paralysé.');
INSERT INTO Attaque VALUES (110, 'Calcination', '100', '60', '15', 'Feu', 'Spéciale', 'Des flammes calcinent l''ennemi. S''il tient un objet, une Baie par exemple, celui-ci est brûlé et devient inutilisable.');
INSERT INTO Attaque VALUES (111, 'Câlinerie', '90', '90', '10', 'Fée', 'Physique', 'Attaque l''ennemi avec un câlin. Peut diminuer son Attaque.');
INSERT INTO Attaque VALUES (112, 'Camaraderie', '-', '-', '20', 'Normal', 'Statut', 'L''ennemi se lie d''amitié avec le lanceur et perd sa combativité, diminuant son Attaque.');
INSERT INTO Attaque VALUES (113, 'Camouflage', '-', '-', '20', 'Normal', 'Statut', 'Change le type du Pokémon en fonction du lieu.');
INSERT INTO Attaque VALUES (114, 'Canicule', '90', '95', '10', 'Feu', 'Spéciale', 'Le lanceur provoque une vague de chaleur. Peut aussi brûler l''ennemi.');
INSERT INTO Attaque VALUES (115, 'Canon Dynamax', '100', '100', '5', 'Dragon', 'Spéciale', 'Le lanceur attaque en émettant un laser depuis son noyau. Cette capacité inflige deux fois plus de dégâts si l''adversaire est un Pokémon Dynamax.');
INSERT INTO Attaque VALUES (116, 'Canon Floral', '90', '130', '5', 'Fée', 'Spéciale', 'Envoie un rayon laser dévastateur. Baisse beaucoup l''Attaque Spéciale du lanceur.');
INSERT INTO Attaque VALUES (117, 'Canon Graine', '100', '80', '15', 'Plante', 'Physique', 'Le lanceur fait pleuvoir un déluge de graines explosives sur l''ennemi.');
INSERT INTO Attaque VALUES (118, 'Carapiège', '100', '150', '5', 'Feu', 'Spéciale', 'Pose une carapace piégée. Si l''adversaire utilise une capacité physique, la carapace explose et lui inflige des dégâts.');
INSERT INTO Attaque VALUES (119, 'Cascade', '100', '80', '15', 'Eau', 'Physique', 'Le lanceur charge l''ennemi à une vitesse remarquable, ce qui peut l''apeurer.');
INSERT INTO Attaque VALUES (120, 'Casse-Brique', '100', '75', '15', 'Combat', 'Physique', 'Le lanceur attaque avec le tranchant de la main. Permet aussi de briser les barrières comme Mur Lumière et Protection.');
INSERT INTO Attaque VALUES (121, 'Cauchemar', '100', '-', '15', 'Spectre', 'Statut', 'Inflige 1/4 de dégâts à un ennemi endormi chaque tour.');
INSERT INTO Attaque VALUES (122, 'Cavalerie Lourde', '95', '95', '10', 'Sol', 'Physique', 'Le lanceur attaque violemment en utilisant tout son poids.');
INSERT INTO Attaque VALUES (123, 'Célébration', '-', '-', '40', 'Normal', 'Statut', 'Le Pokémon vous souhaite plein de bonnes choses pour cet évènement spécial.');
INSERT INTO Attaque VALUES (124, 'Cent Rancunes', '100', '75', '5', 'Ténèbres', 'Physique', 'Si les stats du lanceur ont diminué pendant ce tour, la puissance est doublée.');
INSERT INTO Attaque VALUES (125, 'Centrifugifle', '100', '60', '20', 'Ténèbres', 'Physique', 'Le lanceur pivote pour prendre de l''élan et infliger des dégâts.');
INSERT INTO Attaque VALUES (126, 'Champ Brumeux', '-', '-', '10', 'Fée', 'Statut', 'Pendant cinq tours, les Pokémon au sol ne peuvent pas subir d''altération de statut et la puissance des capacités de type Dragon est divisée par deux.');
INSERT INTO Attaque VALUES (127, 'Champ Électrifié', '-', '-', '10', 'Electrik', 'Statut', 'Pendant cinq tours, le terrain se charge d''électricité. Les Pokémon au sol ne peuvent pas s''endormir et la puissance des capacités de type Électrik augmente.');
INSERT INTO Attaque VALUES (128, 'Champ Herbu', '-', '-', '10', 'Plante', 'Statut', 'Pendant cinq tours, les Pokémon au sol récupèrent quelques PV à chaque tour et la puissance des capacités de type Plante augmente.');
INSERT INTO Attaque VALUES (129, 'Champ Psychique', '-', '-', '10', 'Psy', 'Statut', 'Pendant cinq tours, les Pokémon au sol ne peuvent plus subir d''attaques prioritaires et la puissance des capacités de type Psy augmente.');
INSERT INTO Attaque VALUES (130, 'Champlification', '100', '50', '10', 'Normal', 'Spéciale', 'Le type et la puissance varient selon le champ actif.');
INSERT INTO Attaque VALUES (131, 'Change-Côté', '100', '-', '10', 'Normal', 'Statut', 'Une force mystérieuse intervertit les effets affectant chaque côté du terrain.');
INSERT INTO Attaque VALUES (132, 'Change Éclair', '100', '70', '20', 'Electrik', 'Spéciale', 'Après son attaque, le lanceur revient à toute vitesse et change de place avec un Pokémon de l''équipe prêt au combat.');
INSERT INTO Attaque VALUES (133, 'Chant Canon', '100', '60', '15', 'Normal', 'Spéciale', 'Le lanceur attaque l''ennemi en chantant. Si plusieurs Pokémon déclenchent cette attaque à la suite, l''effet augmente.');
INSERT INTO Attaque VALUES (134, 'Chant Antique', '100', '75', '10', 'Normal', 'Spéciale', 'Le lanceur attaque l''ennemi en lui chantant une chanson d''un autre temps. Peut l''endormir.');
INSERT INTO Attaque VALUES (135, 'Chaos Draconique', '-', '1', '-', 'Dragon', 'Physique', 'Le Pokémon utilise la Force Z pour matérialiser son aura et assaillir l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (136, 'Chaos Draconique', '-', '1', '-', 'Dragon', 'Spéciale', 'Le Pokémon utilise la Force Z pour matérialiser son aura et assaillir l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (137, 'Charge Os', '90', '25', '10', 'Sol', 'Physique', 'Touche 2 à 5 fois en un tour. Il y a 37.5% de chance de toucher 2 ou 3 fois, et 12.5% de chance de toucher 4 ou 5 fois.');
INSERT INTO Attaque VALUES (138, 'Charge', '100', '40', '35', 'Normal', 'Physique', 'Le lanceur charge l''ennemi et le percute de tout son poids.');
INSERT INTO Attaque VALUES (139, 'Charge Foudre', '85', '130', '5', 'Electrik', 'Physique', 'Le lanceur s''enveloppe d''une charge électrique surpuissante et se jette sur l''ennemi. Peut aussi le paralyser.');
INSERT INTO Attaque VALUES (140, 'Chargeur', '-', '-', '20', 'Electrik', 'Statut', 'Double la puissance de n''importe quelle attaque Electrique lancée par ce Pokémon le tour suivant. Augmente la Défense Spéciale du lanceur d''un niveau.');
INSERT INTO Attaque VALUES (141, 'Charme', '100', '-', '20', 'Fée', 'Statut', 'Le lanceur fait les yeux doux pour berner l''ennemi et beaucoup réduire son Attaque.');
INSERT INTO Attaque VALUES (142, 'Châtiment', '100', '65', '10', 'Spectre', 'Spéciale', 'Attaque acharnée qui cause davantage de dégâts à l''ennemi s''il a un problème de statut.');
INSERT INTO Attaque VALUES (143, 'Chatouille', '100', '-', '20', 'Normal', 'Statut', 'Attaque qui baisse l''attaque et la défense de la cible d''un cran.');
INSERT INTO Attaque VALUES (144, 'Chgt Vitesse', '-', '-', '10', 'Acier', 'Statut', 'Le lanceur fait tourner ses engrenages. Cela augmente son Attaque et augmente beaucoup sa Vitesse.');
INSERT INTO Attaque VALUES (145, 'Choc Émotionnel', '100', '75', '15', 'Fée', 'Physique', 'Le lanceur attaque son adversaire avec une telle force que ce dernier peut s''en retrouver profondément troublé. Baisse l''Attaque Spéciale de la cible.');
INSERT INTO Attaque VALUES (146, 'Choc Mental', '100', '50', '25', 'Psy', 'Spéciale', 'L''attaque a 10% de chance de rendre confus la cible.');
INSERT INTO Attaque VALUES (147, 'Choc Météore', '100', '100', '5', 'Acier', 'Physique', 'Le lanceur fonce sur la cible à la vitesse d''une météorite. Ignore le talent de l''ennemi.');
INSERT INTO Attaque VALUES (148, 'Choc Psy', '100', '80', '10', 'Psy', 'Spéciale', 'Le lanceur matérialise des ondes mystérieuses qu''il projette sur l''ennemi. Inflige des dégâts physiques.');
INSERT INTO Attaque VALUES (149, 'Choc Venin', '100', '65', '10', 'poison', 'Spéciale', 'Le lanceur inocule un poison spécial à l''ennemi. L''effet est doublé si l''ennemi est déjà empoisonné.');
INSERT INTO Attaque VALUES (150, 'Chute Glace', '90', '85', '10', 'Glace', 'Physique', 'Envoie de gros blocs de glace sur l''ennemi pour lui infliger des dégâts. Peut aussi l''apeurer.');
INSERT INTO Attaque VALUES (151, 'Chute Libre', '100', '60', '10', 'Vol', 'Physique', 'Le lanceur emmène l''ennemi dans les airs au 1er tour et le lâche dans le vide au 2e. L''ennemi saisi ne peut pas attaquer.');
INSERT INTO Attaque VALUES (152, 'Clairvoyance', '-', '-', '40', 'Normal', 'Statut', 'Annule toutes les modifications d''esquive de l''adversaire, et l''empêche de l''améliorer. Les attaques Combat et Normal peuvent toucher les Spectres sous Clairvoyance.');
INSERT INTO Attaque VALUES (153, 'Claquoir', '85', '35', '15', 'Eau', 'Physique', 'Claquoir dure entre 2 et 5 tours en tant qu''effet passif, donc le lanceur peut choisir d''autres attaques après avoir lancé Claquoir pendant que celle-ci est toujours active. Elle inflige 1/16 des PV Max de la cible à chaque tour et l''empêche de switcher. L''effet est annulé si le lanceur switche, même si le compteur des 2 à 5 tours n''était pas encore terminé.');
INSERT INTO Attaque VALUES (154, 'Clepto-Mânes', '100', '90', '10', 'Spectre', 'Physique', 'Le lanceur plonge dans l''ombre de la cible, vole ses augmentations de stats et l''attaque.');
INSERT INTO Attaque VALUES (155, 'Clonage', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur crée un clone en sacrifiant quelques PV. Ce clone sert de leurre.');
INSERT INTO Attaque VALUES (156, 'Close Combat', '100', '120', '5', 'Combat', 'Physique', 'Le lanceur combat au corps à corps sans se protéger. Baisse aussi sa Défense et sa Défense Spéciale.');
INSERT INTO Attaque VALUES (157, 'Coaching', '-', '-', '10', 'Combat', 'Statut', 'Augmente d''un cran l''Attaque et la Défense d''un allié.');
INSERT INTO Attaque VALUES (158, 'Cocon Fatal', '-', '1', '-', 'Insecte', 'Physique', 'Le Pokémon utilise la Force Z pour cracher de longs fils de soie et enserrer l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (159, 'Cocon Fatal', '-', '1', '-', 'Insecte', 'Spéciale', 'Le Pokémon utilise la Force Z pour cracher de longs fils de soie et enserrer l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (160, 'Cogne', '100', '15', '20', 'Combat', 'Physique', 'Touche 2 à 5 fois en un tour. Il y a 37.5% de chance de toucher 2 ou 3 fois, et 12.5% de chance de toucher 4 ou 5 fois.');
INSERT INTO Attaque VALUES (161, 'Cognobidon', '-', '-', '10', 'Normal', 'Statut', 'Utilise la moitié des PV Max du lanceur pour booster son attaque au maximum (cran +6). L''attaque échoue si le lanceur ne possède plus que 50% ou moins de ses PV Max.');
INSERT INTO Attaque VALUES (162, 'Colère du Gardien d''Alola', '-', '1', '-', 'Fée', 'Spéciale', 'Le Pokémon Tutélaire utilise la Force Z et déchaîne toute la puissance d’Alola sur sa cible. Inflige des dégâts en fonction des PV restants de celle-ci.');
INSERT INTO Attaque VALUES (163, 'Colère', '100', '120', '10', 'Dragon', 'Physique', 'Le lanceur enrage et attaque pendant deux ou trois tours avant de devenir confus.');
INSERT INTO Attaque VALUES (164, 'Combo-Griffe', '80', '18', '15', 'Normal', 'Physique', 'L''ennemi est lacéré par des faux ou des griffes de deux à cinq fois d''affilée.');
INSERT INTO Attaque VALUES (165, 'Combo Hyper-Furie', '-', '1', '-', 'Combat', 'Physique', 'Le Pokémon utilise la Force Z pour créer une boule d’énergie qu’il projette sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (166, 'Combo Hyper-Furie', '-', '1', '-', 'Combat', 'Spéciale', 'Le Pokémon utilise la Force Z pour créer une boule d’énergie qu’il projette sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (167, 'Confidence', '-', '-', '20', 'Normal', 'Statut', 'Dévoile des secrets à l''ennemi, qui perd alors sa concentration et voit son Attaque Spéciale diminuer.');
INSERT INTO Attaque VALUES (168, 'Constriction', '100', '10', '35', 'Normal', 'Physique', 'L''attaque a 10% de chance de baisser la Vitesse de la cible d''un niveau.');
INSERT INTO Attaque VALUES (169, 'Contre', '100', '1', '15', 'Combat', 'Physique', 'Le lanceur ne retient plus ses coups. Plus ses PV sont bas, plus l''attaque est puissante.');
INSERT INTO Attaque VALUES (170, 'Conversion 2', '-', '-', '30', 'Normal', 'Statut', 'Transforme le type du lanceur en type qui est résistant ou immunisé à la dernière attaque qui l''a touché. Fonctionne même si l''attaque a touché un Clone. Conversion 2 ne choisit qu''un type différent de celui du lanceur. Si le lanceur est de type Acier et est touché par une attaque Dragon, Conversion 2 échoue.');
INSERT INTO Attaque VALUES (171, 'Copie-Type', '-', '-', '15', 'Normal', 'Statut', 'Le lanceur copie le type de la cible et devient du même type.');
INSERT INTO Attaque VALUES (172, 'Copie', '-', '-', '10', 'Normal', 'Statut', 'Copie la dernière attaque utilisée par la cible, mais ne fonctionne pas si l''adversaire n''a pas lancé d''attaque depuis qu''il est sur le terrain. Copie ne peut copier Babil, Copie, Gribouille, Lutte ou Metronome, ou une attaque déjà connue du lanceur. L''attaque copiée possède 5 PP.');
INSERT INTO Attaque VALUES (173, 'Coqui-Lame', '95', '75', '10', 'Eau', 'Physique', 'Un coquillage aiguisé lacère l''ennemi. Peut aussi baisser sa Défense.');
INSERT INTO Attaque VALUES (174, 'Corps Perdu', '-', '70', '10', 'Combat', 'Physique', 'L''attaque ignore les changements d''esquive et de précision.');
INSERT INTO Attaque VALUES (175, 'Cotogarde', '-', '-', '10', 'Plante', 'Statut', 'Le lanceur se protège en s''emmitouflant dans du coton. Sa Défense augmente énormément.');
INSERT INTO Attaque VALUES (176, 'Coud''Boue', '100', '20', '10', 'Sol', 'Spéciale', 'L''attaque baisse la précision de l''adversaire d''un niveau.');
INSERT INTO Attaque VALUES (177, 'Coud''Krâne', '100', '130', '10', 'Normal', 'Physique', 'Le lanceur charge son attaque le premier tour et frappe au second. La défense du lanceur monte d''un cran.');
INSERT INTO Attaque VALUES (178, 'Coup Bas', '100', '70', '5', 'Ténèbres', 'Physique', 'Le lanceur attaque en premier. L''attaque échoue si l''adversaire n''utilise pas d''attaque infligeant des dégâts. Elle échoue également si l''adversaire attaque en premier (en utilisant une attaque de plus grande priorité, ou de priorité égale avec une vitesse supérieure).');
INSERT INTO Attaque VALUES (179, 'Coup Croix', '80', '100', '5', 'Combat', 'Physique', 'L''attaque possède un taux de coup critique élevé (+1).');
INSERT INTO Attaque VALUES (180, 'Coup d''Boule', '100', '70', '15', 'Normal', 'Physique', 'Le lanceur donne un coup de tête. Peut apeurer l''ennemi.');
INSERT INTO Attaque VALUES (181, 'Coup d''Jus', '100', '80', '15', 'Electrik', 'Spéciale', 'Possède 30% de chance de paralyser chacune des cibles.');
INSERT INTO Attaque VALUES (182, 'Coup d''Main', '-', '-', '20', 'Normal', 'Statut', 'Une capacité qui augmente la puissance d''attaque d''un allié.');
INSERT INTO Attaque VALUES (183, 'Coup Double', '90', '35', '10', 'Normal', 'Physique', 'Le lanceur frappe l''ennemi deux fois d''affilée à l''aide de sa queue, de lianes ou d''un autre membre.');
INSERT INTO Attaque VALUES (184, 'Coup Varia-Type', '100', '120', '10', 'Normal', 'Physique', 'Le Pokémon s''entoure d''une puissante énergie avant de foncer sur sa cible. Le type de la capacité dépend de la ROM installée.');
INSERT INTO Attaque VALUES (185, 'Coupe Psycho', '100', '70', '20', 'Psy', 'Physique', 'Le lanceur entaille l''ennemi grâce à des lames faites d''énergie psychique. Taux de critiques élevé.');
INSERT INTO Attaque VALUES (186, 'Coupe-Vent', '100', '80', '10', 'Normal', 'Spéciale', 'Une attaque en 2 tours qui frappe au 2e tour. Haut taux de Coups Critiques (+1).');
INSERT INTO Attaque VALUES (187, 'Coupe', '95', '50', '30', 'Normal', 'Physique', 'Coupe l''ennemi avec des lames ou des griffes.');
INSERT INTO Attaque VALUES (188, 'Coup Victoire', '95', '180', '5', 'Feu', 'Physique', 'Le lanceur projette une flamme ardente de son front et se jette sur l''ennemi. Baisse la Défense, la Défense Spéciale et la Vitesse.');
INSERT INTO Attaque VALUES (189, 'Cradovague', '100', '95', '10', 'poison', 'Spéciale', 'Une vague de détritus attaque tous les Pokémon autour du lanceur. Peut aussi empoisonner.');
INSERT INTO Attaque VALUES (190, 'Crève-Coeur', '100', '60', '25', 'Psy', 'Physique', 'Déconcentre l''ennemi avec des mouvements mignons avant de le frapper violemment. Peut apeurer l''ennemi.');
INSERT INTO Attaque VALUES (191, 'Croc de Mort', '90', '80', '15', 'Normal', 'Physique', 'L''attaque possède 10% de chance d''apeurer l''adversaire.');
INSERT INTO Attaque VALUES (192, 'Croc Fatal', '90', '1', '10', 'Normal', 'Physique', 'L''attaque inflige 50% des PVs restants de l''adversaire, arrondis à l''inférieur. Si les dégâts sont censés être inférieurs à 1, ceux-ci passent à 1 automatiquement.');
INSERT INTO Attaque VALUES (193, 'Crochet Venin', '100', '50', '15', 'poison', 'Physique', 'L''attaque a 30% de chance d''empoisonner gravement l''adversaire.');
INSERT INTO Attaque VALUES (194, 'Croco Larme', '100', '-', '20', 'Ténèbres', 'Statut', 'Le lanceur fait semblant de pleurer pour troubler l''ennemi et beaucoup baisser sa Défense Spéciale.');
INSERT INTO Attaque VALUES (195, 'Crocs Éclair', '95', '65', '15', 'Electrik', 'Physique', 'Le lanceur utilise une morsure électrifiée. Peut aussi paralyser ou apeurer l''ennemi.');
INSERT INTO Attaque VALUES (196, 'Crocs Feu', '95', '65', '15', 'Feu', 'Physique', 'Le lanceur utilise une morsure enflammée. Peut aussi brûler ou apeurer l''ennemi.');
INSERT INTO Attaque VALUES (197, 'Crocs Givre', '95', '65', '15', 'Glace', 'Physique', 'Le lanceur utilise une morsure glaciale. Peut aussi geler ou apeurer l''ennemi.');
INSERT INTO Attaque VALUES (198, 'Croissance', '-', '-', '20', 'Normal', 'Statut', 'Le corps du lanceur se développe. Augmente l''Attaque et l''Attaque Spéciale.');
INSERT INTO Attaque VALUES (199, 'Croque Fort', '100', '80', '10', 'Ténèbres', 'Physique', 'Le lanceur et sa cible ne peuvent plus quitter le terrain jusqu''à ce que l''un d''entre eux tombe K.O. L''effet est annulé si l''un des deux Pokémon disparaît.');
INSERT INTO Attaque VALUES (200, 'Cru-Ailes', '100', '60', '35', 'Vol', 'Physique', 'L''ennemi est frappé par de larges ailes déployées pour infliger des dégâts.');
INSERT INTO Attaque VALUES (201, 'Cryomax', '-', '10', '10', 'Glace', 'Physique', 'Une attaque de type Glace que seuls les Pokémon Dynamax peuvent utiliser. Invoque une tempête de grêle qui dure cinq tours.');
INSERT INTO Attaque VALUES (202, 'Cyclone', '-', '-', '20', 'Normal', 'Statut', 'L''attaque force l''adversaire à switcher pour un Pokémon sélectionné au hasard dans le reste de l''équipe. L''attaque échoue si l''adversaire possède Ventouse ou est sous l''effet de Racines.');
INSERT INTO Attaque VALUES (203, 'Damoclès', '100', '120', '15', 'Normal', 'Physique', 'Le lanceur subit 1/3 des dégâts infligés à l''adversaire. Ce chiffre est arrondi à l''inférieur.');
INSERT INTO Attaque VALUES (204, 'Danse Draco', '-', '-', '20', 'Dragon', 'Statut', 'Une danse mystique dont le rythme effréné augmente l''Attaque et la Vitesse du lanceur.');
INSERT INTO Attaque VALUES (205, 'Danse du Feu', '100', '80', '10', 'Feu', 'Spéciale', 'Le lanceur enveloppe l''ennemi de flammes. Peut aussi augmenter l''Attaque Spéciale du lanceur.');
INSERT INTO Attaque VALUES (206, 'Danse Éveil', '100', '90', '15', 'Normal', 'Spéciale', 'Le lanceur attaque en dansant avec enthousiasme. Le type de la capacité est le même que celui du lanceur.');
INSERT INTO Attaque VALUES (207, 'Danse Flammes', '85', '35', '15', 'Feu', 'Spéciale', 'Un tourbillon de flammes emprisonne l''ennemi pendant quatre à cinq tours.');
INSERT INTO Attaque VALUES (208, 'Danse Fleurs', '100', '120', '10', 'Plante', 'Spéciale', 'Attaque durant 2 à 3 tours, et rend confus le lanceur à la fin de l''attaque.');
INSERT INTO Attaque VALUES (209, 'Danse Folle', '100', '-', '20', 'Normal', 'Statut', 'L''attaque rend confus tous les autres Pokémon sur le terrain.');
INSERT INTO Attaque VALUES (210, 'Danse Lames', '-', '-', '20', 'Normal', 'Statut', 'Une danse frénétique qui exalte l''esprit combatif. Augmente beaucoup l''Attaque du lanceur.');
INSERT INTO Attaque VALUES (211, 'Danse Lune', '-', '-', '10', 'Psy', 'Statut', 'Le lanceur tombe K.O. pour soigner le statut et les PV du Pokémon qui prendra sa place au combat.');
INSERT INTO Attaque VALUES (212, 'Danse Pluie', '-', '-', '5', 'Eau', 'Statut', 'Invoque de fortes pluies qui durent cinq tours, augmentant la puissance des capacités de type Eau et baissant celle des capacités de type Feu.');
INSERT INTO Attaque VALUES (213, 'Danse Plumes', '100', '-', '15', 'Vol', 'Statut', 'Une montagne de plumes ensevelit l''ennemi et réduit beaucoup son Attaque.');
INSERT INTO Attaque VALUES (214, 'Dard Mortel', '100', '50', '25', 'Insecte', 'Physique', 'Augmente énormément l''Attaque du lanceur si un ennemi est mis K.O. avec cette capacité.');
INSERT INTO Attaque VALUES (215, 'Dard-Nuée', '95', '25', '20', 'Insecte', 'Physique', 'Envoie une rafale de dards. Peut toucher de deux à cinq fois.');
INSERT INTO Attaque VALUES (216, 'Dard-Venin', '100', '15', '35', 'poison', 'Physique', 'L''attaque a 30% de chance d''empoisonner l''adversaire.');
INSERT INTO Attaque VALUES (217, 'Dark Body Press', '-', '180', '-', 'Ténèbres', 'Physique', 'Félinferno utilise la Force Z pour gonfler ses muscles et écraser la cible de toutes ses forces.');
INSERT INTO Attaque VALUES (218, 'Dark Lariat', '100', '85', '10', 'Ténèbres', 'Physique', 'Le Pokémon étend les bras et frappe l''adversaire en tournant violemment. Inflige des dégâts et ignore les changements de stats de la cible.');
INSERT INTO Attaque VALUES (219, 'Déflagration', '85', '110', '5', 'Feu', 'Spéciale', 'Un déluge de flammes ardentes submerge l''ennemi. Peut aussi le brûler.');
INSERT INTO Attaque VALUES (220, 'Dégommage', '100', '1', '10', 'Ténèbres', 'Physique', 'Le lanceur envoie l''objet qu''il tient sur l''ennemi. La puissance et les effets dépendent de l''objet.');
INSERT INTO Attaque VALUES (221, 'Déluge Causti-Toxique', '-', '1', '-', 'poison', 'Physique', 'Le Pokémon utilise la Force Z pour répandre un marécage empoisonné où l’adversaire sombre. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (222, 'Déluge Causti-Toxique', '-', '1', '-', 'poison', 'Spéciale', 'Le Pokémon utilise la Force Z pour répandre un marécage empoisonné où l’adversaire sombre. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (223, 'Déluge Plasmique', '-', '-', '25', 'Electrik', 'Statut', 'Change les attaques de type Normal en type Electrik.');
INSERT INTO Attaque VALUES (224, 'Demi-Tour', '100', '70', '20', 'Insecte', 'Physique', 'Après son attaque, le lanceur revient à toute vitesse et change de place avec un Pokémon de l''équipe prêt au combat.');
INSERT INTO Attaque VALUES (225, 'Dépit', '100', '-', '10', 'Spectre', 'Statut', 'Réduit les PPs de la dernière attaque utilisée par l''adversaire de 4. L''attaque échoue si l''adversaire n''a pas utilisé d''attaque depuis son entrée sur le terrain, si l''adversaire a échoué dans l''exécution d''une attaque (dûe au sommeil, à la paralysie, rechargement, etc...) avant de subir Dépit, ou si la dernière attaque lancée par l''adversaire ne possède plus de PPs.');
INSERT INTO Attaque VALUES (226, 'Dernier Mot', '100', '-', '20', 'Ténèbres', 'Statut', 'Menace l''ennemi dans une ultime tirade avant de changer de place avec un autre Pokémon. Réduit l''Attaque et l''Attaque Spéciale de l''ennemi.');
INSERT INTO Attaque VALUES (227, 'Dernier Recours', '100', '140', '5', 'Normal', 'Physique', 'L''attaque échoue si elle est la seule attaque du lanceur ou si les autres attaques n''ont pas été utilisées depuis que le lanceur est sur le terrain.');
INSERT INTO Attaque VALUES (228, 'Destruction', '100', '200', '5', 'Normal', 'Physique', 'Le lanceur explose en blessant tous les Pokémon autour de lui. Le lanceur tombe K.O.');
INSERT INTO Attaque VALUES (229, 'Détection', '-', '-', '5', 'Combat', 'Statut', 'Progège le lanceur de la plupart des attaques pendant un tour. Si le lanceur a réussi Detection, Tenacite ou Abri le tour précédent, Detection n''a que 50% de chance de réussir.');
INSERT INTO Attaque VALUES (230, 'Détrempage', '100', '-', '20', 'Eau', 'Statut', 'Le lanceur projette beaucoup d''eau sur sa cible, qui devient de type Eau.');
INSERT INTO Attaque VALUES (231, 'Détricanon', '80', '120', '5', 'poison', 'Physique', 'Le lanceur envoie des détritus sur l''ennemi. Peut aussi l''empoisonner.');
INSERT INTO Attaque VALUES (232, 'Détritus', '100', '65', '20', 'poison', 'Spéciale', 'L''attaque a 30% de chances d''empoisonner l''adversaire.');
INSERT INTO Attaque VALUES (233, 'Dévorêve', '100', '100', '15', 'Psy', 'Spéciale', 'Le lanceur mange le rêve de l''ennemi endormi et récupère en PV la moitié des dégâts infligés.');
INSERT INTO Attaque VALUES (234, 'Direct Toxik', '100', '80', '20', 'poison', 'Physique', 'Attaque l''ennemi avec un tentacule, un bras, ou un autre membre plein de poison. Peut aussi l''empoisonner.');
INSERT INTO Attaque VALUES (235, 'Distorsion', '-', '-', '5', 'Psy', 'Statut', 'Le lanceur crée une zone mystérieuse où les Pokémon les plus lents frappent en priorité pendant cinq tours.');
INSERT INTO Attaque VALUES (236, 'Don Naturel', '100', '1', '15', 'Normal', 'Physique', 'La puissance et le type dépend de la baie tenue');
INSERT INTO Attaque VALUES (237, 'Double Baffe', '90', '40', '15', 'Dragon', 'Physique', 'Le lanceur frappe l''ennemi deux fois d''affilée avec les parties les plus robustes de son corps.');
INSERT INTO Attaque VALUES (238, 'Double Dard', '100', '25', '20', 'Insecte', 'Physique', 'L''attaque frappe deux fois en un tour. Chaque coup a 20% de chances d''empoisonner.');
INSERT INTO Attaque VALUES (239, 'Double Pied', '100', '30', '30', 'Combat', 'Physique', 'Deux coups de pied qui frappent l''ennemi deux fois d''affilée.');
INSERT INTO Attaque VALUES (240, 'Double Volée', '90', '40', '10', 'Vol', 'Physique', 'Attaque 2 fois.');
INSERT INTO Attaque VALUES (241, 'Doux Baiser', '75', '-', '10', 'Fée', 'Statut', 'Le lanceur envoie un bisou si mignon et désarmant qu''il plonge l''ennemi dans la confusion.');
INSERT INTO Attaque VALUES (242, 'Doux Parfum', '100', '-', '20', 'Normal', 'Statut', 'L''attaque baisse l''esquive de l''adversaire d''un cran.');
INSERT INTO Attaque VALUES (243, 'Dracacophonie Flamboyante', '-', '185', '-', 'Dragon', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (244, 'Dracacophonie', '100', '-', '5', 'Dragon', 'Statut', 'Le lanceur sacrifie une partie de ses PV pour augmenter toutes ses stats.');
INSERT INTO Attaque VALUES (245, 'Draco-Ascension', '100', '120', '5', 'Vol', 'Physique', 'Le Pokémon s''abat à toute vitesse sur la cible depuis les hautes couches de l''atmosphère. Baisse la Défense et la Défense Spéciale du lanceur.');
INSERT INTO Attaque VALUES (246, 'Draco-Charge', '75', '100', '10', 'Dragon', 'Physique', 'L''attaque a 20% de chance d''apeurer l''ennemi.');
INSERT INTO Attaque VALUES (247, 'Draco-Choc', '100', '85', '10', 'Dragon', 'Spéciale', 'Le lanceur ouvre la bouche pour envoyer une onde de choc qui frappe l''ennemi.');
INSERT INTO Attaque VALUES (248, 'Draco-Flèches', '100', '50', '10', 'Dragon', 'Physique', 'Le lanceur attaque en propulsant deux Fantyrm. S''il y a deux adversaires, chacun d''entre eux est frappé par un Fantyrm.');
INSERT INTO Attaque VALUES (249, 'Draco-Griffe', '100', '80', '15', 'Dragon', 'Physique', 'Le lanceur lacère l''ennemi de ses grandes griffes aiguisées.');
INSERT INTO Attaque VALUES (250, 'Draco-Marteau', '100', '90', '15', 'Dragon', 'Physique', 'Le lanceur utilise son corps comme un véritable marteau pour écraser la cible.');
INSERT INTO Attaque VALUES (251, 'Draco-Météore', '90', '130', '5', 'Dragon', 'Spéciale', 'Baisse l''Attaque Spéciale du lanceur de deux niveaux.');
INSERT INTO Attaque VALUES (252, 'Draco-Queue', '90', '60', '10', 'Dragon', 'Physique', 'Un coup puissant qui blesse la cible et l''envoie au loin. Lors d''un combat contre un Pokémon sauvage seul, met fin au combat.');
INSERT INTO Attaque VALUES (253, 'Draco-Rage', '100', '1', '10', 'Dragon', 'Spéciale', 'Envoie des ondes de choc infligeant 40 PV de dégâts.');
INSERT INTO Attaque VALUES (254, 'Draco-Souffle', '100', '60', '20', 'Dragon', 'Spéciale', 'L''attaque a 30% de chance de paralyser l''adversaire.');
INSERT INTO Attaque VALUES (255, 'Dracomax', '-', '10', '10', 'Dragon', 'Physique', 'Une attaque de type Dragon que seuls les Pokémon Dynamax peuvent utiliser. Baisse l''Attaque de la cible.');
INSERT INTO Attaque VALUES (256, 'Dynamo-Poing', '50', '100', '5', 'Combat', 'Physique', 'Rend confus l''adversaire si l''attaque le touche.');
INSERT INTO Attaque VALUES (257, 'E-Coque', '-', '-', '10', 'Normal', 'Statut', 'Soigne 50% de la vie du lanceur.');
INSERT INTO Attaque VALUES (258, 'Eau Revoir', '100', '60', '20', 'Eau', 'Physique', 'Après l''attaque, le lanceur change de place avec un Pokémon de l''équipe.');
INSERT INTO Attaque VALUES (259, 'Éboulement', '90', '75', '10', 'Roche', 'Physique', 'Envoie de gros rochers sur l''ennemi pour infliger des dégâts. Peut aussi l''apeurer.');
INSERT INTO Attaque VALUES (260, 'Ébullilave', '100', '80', '15', 'Feu', 'Spéciale', 'Des boules de feu s''abattent sur tous les Pokémon autour du lanceur. Peut aussi les brûler.');
INSERT INTO Attaque VALUES (261, 'Ébullition', '100', '80', '15', 'Eau', 'Spéciale', 'L''ennemi est attaqué par un jet d''eau bouillante. Peut aussi le brûler.');
INSERT INTO Attaque VALUES (262, 'Échange Psy', '100', '-', '10', 'Psy', 'Statut', 'Si la cible n''a pas de statut alors que le lanceur oui, cette attaque transfère le statut du lanceur sur l''adversaire.');
INSERT INTO Attaque VALUES (263, 'Échange', '-', '-', '10', 'Psy', 'Statut', 'Le lanceur utilise ses pouvoirs psychiques pour échanger son talent avec la cible.');
INSERT INTO Attaque VALUES (264, 'Écho', '100', '40', '15', 'Normal', 'Spéciale', 'Un cri retentissant blesse l''ennemi. Si le lanceur ou d''autres Pokémon l''utilisent à chaque tour, l''effet augmente.');
INSERT INTO Attaque VALUES (265, 'Éclair Croix', '100', '100', '5', 'Electrik', 'Physique', 'Projette un orbe électrique gigantesque. L''effet augmente sous l''influence de Flamme Croix.');
INSERT INTO Attaque VALUES (266, 'Éclair Fou', '100', '90', '15', 'Electrik', 'Physique', 'Une charge électrique violente qui blesse aussi légèrement le lanceur.');
INSERT INTO Attaque VALUES (267, 'Éclair Gelé', '90', '140', '5', 'Glace', 'Physique', 'Projette un bloc de glace électrifié sur l''ennemi au second tour. Peut aussi le paralyser.');
INSERT INTO Attaque VALUES (268, 'Éclair', '100', '40', '30', 'Electrik', 'Spéciale', 'L''attaque a 10% de chances de paralyser.');
INSERT INTO Attaque VALUES (269, 'Éclat Magique', '100', '80', '10', 'Fée', 'Spéciale', 'Libère une puissante décharge lumineuse qui inflige des dégâts à l''ennemi.');
INSERT INTO Attaque VALUES (270, 'Éclate-Roc', '100', '40', '15', 'Combat', 'Physique', 'Porte un coup de poing à l''ennemi qui peut baisser sa Défense.');
INSERT INTO Attaque VALUES (271, 'Éclate Griffe', '95', '75', '10', 'Normal', 'Physique', 'Possède 50% de chance de baisser la Défense de la cible.');
INSERT INTO Attaque VALUES (272, 'Éclats Glace', '100', '40', '30', 'Glace', 'Physique', 'Le lanceur crée des éclats de glace qu''il envoie sur l''ennemi. Frappe en priorité.');
INSERT INTO Attaque VALUES (273, 'Éco-Sphère', '100', '90', '10', 'Plante', 'Spéciale', 'Utilise les pouvoirs de la nature pour attaquer l''ennemi. Peut aussi baisser sa Défense Spéciale.');
INSERT INTO Attaque VALUES (274, 'Écras''Face', '100', '40', '35', 'Normal', 'Physique', 'Écrase l''ennemi avec les pattes avant ou la queue, par exemple.');
INSERT INTO Attaque VALUES (275, 'Écrasement', '100', '65', '20', 'Normal', 'Physique', 'L''attaque a 30% de chances d''apeurer le Pokémon adverse.');
INSERT INTO Attaque VALUES (276, 'Écrous d''Poing', '100', '60', '5', 'Acier', 'Physique', 'Le lanceur fait pivoter l''écrou de sa poitrine deux fois d''affilée pour frapper l''adversaire avec ses bras. Peut apeurer l''ennemi.');
INSERT INTO Attaque VALUES (277, 'Écume', '100', '40', '30', 'Eau', 'Spéciale', 'L''attaque a 10% de chance de baisser la Vitesse de la cible d''un niveau.');
INSERT INTO Attaque VALUES (278, 'Effort', '100', '1', '5', 'Normal', 'Physique', 'Si la cible possède plus de PV que le lanceur, alors les PV de la cible sont réduits au nombre de PV du lanceur. Sinon, rien ne se passe.');
INSERT INTO Attaque VALUES (279, 'Élecanon', '50', '120', '5', 'Electrik', 'Spéciale', 'Si elle touche, la cible est paralysée.');
INSERT INTO Attaque VALUES (280, 'Électacle', '100', '120', '15', 'Electrik', 'Physique', 'Le lanceur s''inflige 1/3 des dégâts infligés à la cible, arrondis à l''inférieur. L''attaque a 10% de chances de paralyser la cible.');
INSERT INTO Attaque VALUES (281, 'Électrikipik', '100', '80', '10', 'Electrik', 'Physique', 'Le lanceur fonce sur la cible et lui envoie un puissant choc électrique. Peut aussi l''effrayer.');
INSERT INTO Attaque VALUES (282, 'Électrisation', '-', '-', '20', 'Electrik', 'Statut', 'Si le lanceur attaque avant la cible, les capacités de celle-ci seront de type Électrik jusqu''à la fin du tour.');
INSERT INTO Attaque VALUES (283, 'Électro-Surf Survolté', '-', '175', '-', 'Electrik', 'Spéciale', 'Le Raichu de la région d’Alola utilise la Force Z pour frapper la cible et la paralyser.');
INSERT INTO Attaque VALUES (284, 'Embargo', '100', '-', '15', 'Ténèbres', 'Statut', 'L''adversaire ne peut plus utiliser l''objet porté');
INSERT INTO Attaque VALUES (285, 'Empal''Korne', '30', '1', '5', 'Normal', 'Physique', 'L''attaque inflige des dégâts égaux aux PVs max de la cible. Si celle-ci a lancé Tenacité ou tient la Ceinture Force, elle résistera avec 1 PV. Si Verrouillage ou Lire-Esprit ont été utilisées avant, ou si l''un des Pokémon possède la capacité Annule Garde, Empal''Korne touchera. Empal''Korne ignore les changements d''esquive et de précision, mais possède sa propre précision :');
INSERT INTO Attaque VALUES (286, 'Enchantomax', '-', '10', '10', 'Fée', 'Physique', 'Une attaque de type Fée que seuls les Pokémon Dynamax peuvent utiliser. Crée un Champ Brumeux qui dure cinq tours.');
INSERT INTO Attaque VALUES (287, 'Encore', '100', '-', '5', 'Normal', 'Statut', 'Oblige l''ennemi à répéter la dernière capacité utilisée durant trois tours.');
INSERT INTO Attaque VALUES (288, 'Encornebois', '100', '75', '10', 'Plante', 'Physique', 'Un coup de corne qui draine l''énergie de l''ennemi. Convertit la moitié des dégâts infligés en PV pour le lanceur.');
INSERT INTO Attaque VALUES (289, 'Engrenage', '-', '-', '20', 'Acier', 'Statut', 'Change de réglage pour augmenter l''Attaque et l''Attaque Spéciale des alliés ayant les talents Plus ou Moins.');
INSERT INTO Attaque VALUES (290, 'Enroulement', '-', '-', '20', 'poison', 'Statut', 'Le lanceur s''enroule sur lui-même et se concentre. Son Attaque, sa Défense et sa Précision augmentent.');
INSERT INTO Attaque VALUES (291, 'Entrave', '100', '-', '20', 'Normal', 'Statut', 'Empêche la cible d''utiliser la dernière attaque lancée pendant 4 à 7 tours. Une seule attaque peut être neutralisée par Pokémon. Entrave échoue si la cible n''a pas utilisé une seule attaque depuis qu''elle est sur le terrain ou si la dernière attaque utilisée n''a plus de PP. La cible ne fait rien si l''attaque qu''elle allait utilisée est entravée durant le tour.');
INSERT INTO Attaque VALUES (292, 'Ère Glaciaire', '95', '65', '10', 'Glace', 'Spéciale', 'Un souffle de vent qui congèle tout sur son passage s''abat sur l''ennemi. Réduit aussi sa Vitesse.');
INSERT INTO Attaque VALUES (293, 'Éructation', '90', '120', '10', 'poison', 'Spéciale', 'Le lanceur se tourne vers l''ennemi et lui éructe dessus, infligeant des dégâts. Ne fonctionne que si le lanceur consomme la Baie qu''il tient.');
INSERT INTO Attaque VALUES (294, 'Éruption Géo-Sismique', '-', '1', '-', 'Sol', 'Physique', 'Le Pokémon utilise la Force Z pour entraîner l’adversaire dans les profondeurs de la terre. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (295, 'Éruption Géo-Sismique', '-', '1', '-', 'Sol', 'Spéciale', 'Le Pokémon utilise la Force Z pour entraîner l’adversaire dans les profondeurs de la terre. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (296, 'Éruption', '100', '150', '5', 'Feu', 'Spéciale', 'La puissance de l''attaque est calculée selon la formule suivante : 150 * PV / PV Max.');
INSERT INTO Attaque VALUES (297, 'Escalade', '85', '90', '20', 'Normal', 'Physique', 'Peut rendre confus');
INSERT INTO Attaque VALUES (298, 'Escarmouche', '100', '90', '10', 'Insecte', 'Physique', 'Une capacité très puissante, mais qui ne fonctionne qu''au premier tour.');
INSERT INTO Attaque VALUES (299, 'Esprit Frappeur', '90', '110', '5', 'Spectre', 'Physique', 'Le lanceur manipule l''objet tenu par l''ennemi pour l''attaquer. Échoue s''il ne tient rien.');
INSERT INTO Attaque VALUES (300, 'Essorage', '100', '1', '5', 'Normal', 'Spéciale', 'La puissance de base d''Essorage est de 1 + floor(120 * PVs restants de l''adversaire / PVs max de l''adversaire).');
INSERT INTO Attaque VALUES (301, 'Estocorne', '-', '70', '10', 'Acier', 'Physique', 'Le lanceur transperce la cible avec sa corne effilée. Touche toujours l''ennemi.');
INSERT INTO Attaque VALUES (302, 'Étincelle', '100', '65', '20', 'Electrik', 'Physique', 'L''attaque a 30% de chances de paralyser.');
INSERT INTO Attaque VALUES (303, 'Étonnement', '100', '30', '15', 'Spectre', 'Physique', 'L''attaque a 30% de chance d''apeurer l''ennemi.');
INSERT INTO Attaque VALUES (304, 'Étreinte', '85', '15', '20', 'Normal', 'Physique', 'Etreinte dure entre 2 et 5 tours en tant qu''effet passif, donc le lanceur peut choisir d''autres attaques après avoir lancé Etreinte pendant que celle-ci est toujours active. Elle inflige 1/16 des PV Max de la cible à chaque tour et l''empêche de switcher. L''effet est annulé si le lanceur switche, même si le compteur des 2 à 5 tours n''était pas encore terminé.');
INSERT INTO Attaque VALUES (305, 'Étrennes', '-', '-', '30', 'Normal', 'Statut', 'Utilisée pendant un combat, multiplie par deux l''argent gagné à la fin.');
INSERT INTO Attaque VALUES (306, 'Évo-Chardasso', '-', '1', '20', 'Normal', 'Physique', 'Aucune description...');
INSERT INTO Attaque VALUES (307, 'Évo-Congélo', '90', '100', '10', 'Glace', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (308, 'Évo-Dynamo', '100', '60', '20', 'Electrik', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (309, 'Évo-Écolo', '90', '100', '10', 'Plante', 'Physique', 'Aucune description...');
INSERT INTO Attaque VALUES (310, 'Évo-Fabulo', '85', '120', '5', 'Fée', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (311, 'Évo-Flambo', '100', '60', '20', 'Feu', 'Physique', 'Aucune description...');
INSERT INTO Attaque VALUES (312, 'Évo-Psycho', '95', '80', '15', 'Psy', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (313, 'Évo-Ténébro', '95', '80', '15', 'Ténèbres', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (314, 'Évo-Thalasso', '100', '60', '20', 'Eau', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (315, 'Exécu-Son', '100', '80', '15', 'Ténèbres', 'Physique', 'Inflige une douleur tellement violente à la cible qu''elle ne peut plus émettre de sons pendant deux tours.');
INSERT INTO Attaque VALUES (316, 'Explo-Brume', '100', '100', '5', 'Fée', 'Spéciale', 'Le lanceur frappe tous les Pokémon autour de lui et tombe K.O.. La puissance augmente si le Champ Brumeux est actif.');
INSERT INTO Attaque VALUES (317, 'Exploforce', '70', '120', '5', 'Combat', 'Spéciale', 'Le lanceur rassemble ses forces et laisse éclater son pouvoir. Peut aussi baisser la Défense Spéciale de l''ennemi.');
INSERT INTO Attaque VALUES (318, 'Explonuit', '95', '85', '10', 'Ténèbres', 'Spéciale', 'Le lanceur attaque l''ennemi avec une onde de choc ténébreuse. Peut aussi baisser sa Précision.');
INSERT INTO Attaque VALUES (319, 'Explosion', '100', '250', '5', 'Normal', 'Physique', 'Le lanceur explose et inflige des dégâts à tous les Pokémon autour de lui. Met K.O. le lanceur.');
INSERT INTO Attaque VALUES (320, 'Extrasenseur', '100', '80', '20', 'Psy', 'Spéciale', 'L''attaque a 10% de chance d''apeurer sa cible.');
INSERT INTO Attaque VALUES (321, 'Exuviation', '-', '-', '15', 'Normal', 'Statut', 'Le lanceur brise sa coquille. Il baisse sa Défense et sa Défense Spéciale, mais augmente beaucoup son Attaque, son Attaque Spéciale et sa Vitesse.');
INSERT INTO Attaque VALUES (322, 'Façade', '100', '70', '20', 'Normal', 'Physique', 'Une attaque dont la puissance double lorsque le lanceur est empoisonné, paralysé ou brûlé.');
INSERT INTO Attaque VALUES (323, 'Fatal-Foudre', '70', '110', '10', 'Electrik', 'Spéciale', 'La foudre tombe sur l''ennemi pour lui infliger des dégâts. Peut aussi le paralyser.');
INSERT INTO Attaque VALUES (324, 'Fauche-Âme des Sept Étoiles', '-', '195', '-', 'Spectre', 'Physique', 'Marshadow concentre toute la Force Z dans ses poings et ses pieds pour infliger un déluge de coups à la cible.');
INSERT INTO Attaque VALUES (325, 'Faux-Chage', '100', '40', '40', 'Normal', 'Physique', 'Le lanceur retient ses coups pour que l''ennemi garde au moins 1 PV et ne tombe pas K.O.');
INSERT INTO Attaque VALUES (326, 'Feinte', '-', '60', '20', 'Ténèbres', 'Physique', 'L''attaque ignore les modifications de précision ou d''esquive.');
INSERT INTO Attaque VALUES (327, 'Fertilisation', '-', '-', '10', 'Sol', 'Statut', 'Fertilise le terrain, ce qui augmente l''Attaque et l''Attaque Spéciale des pokémon de type Plante.');
INSERT INTO Attaque VALUES (328, 'Feu d''Enfer', '50', '100', '5', 'Feu', 'Spéciale', 'L''ennemi est entouré d''un torrent de flammes ardentes qui le brûlent.');
INSERT INTO Attaque VALUES (329, 'Feu Ensorcelé', '100', '75', '10', 'Feu', 'Spéciale', 'Attaque avec des flammes brûlantes soufflées de la bouche du lanceur. Diminue l''Attaque Spéciale de l''ennemi.');
INSERT INTO Attaque VALUES (330, 'Feu Envieux', '100', '70', '5', 'Feu', 'Spéciale', 'Touche tous les Pokémon adverses et brûlent ceux ayant reçus des augmentations de stats ce tour-ci.');
INSERT INTO Attaque VALUES (331, 'Feu Follet', '85', '-', '15', 'Feu', 'Statut', 'Lance un bouquet de flammes maléfiques à l''ennemi pour lui infliger une brûlure.');
INSERT INTO Attaque VALUES (332, 'Feu Glacé', '90', '140', '5', 'Glace', 'Spéciale', 'Au second tour, le lanceur projette un souffle de vent glacial dévastateur sur l''ennemi. Peut aussi le brûler.');
INSERT INTO Attaque VALUES (333, 'Feu Sacré', '95', '100', '5', 'Feu', 'Physique', 'L''attaque a 50% de chances de brûler l''adversaire. Permet également au lanceur de se dégeler.');
INSERT INTO Attaque VALUES (334, 'Feuillage', '100', '40', '40', 'Plante', 'Physique', 'Le lanceur attaque l''ennemi avec des feuilles.');
INSERT INTO Attaque VALUES (335, 'Feuille Magik', '-', '60', '20', 'Plante', 'Spéciale', 'Le lanceur disperse d''étranges feuilles qui poursuivent l''ennemi. N''échoue jamais.');
INSERT INTO Attaque VALUES (336, 'Fil Toxique', '100', '-', '20', 'poison', 'Statut', 'Tisse un fil imprégné de venin. Empoisonne la cible et baisse sa Vitesse.');
INSERT INTO Attaque VALUES (337, 'Flair', '-', '-', '40', 'Normal', 'Statut', 'Annule toutes les modifications d''esquive de l''adversaire, et l''empêche de l''améliorer. Les attaques Combat et Normal peuvent toucher les Spectres sous Flair.');
INSERT INTO Attaque VALUES (338, 'Flamme Bleue', '85', '130', '5', 'Feu', 'Spéciale', 'De magnifiques et redoutables flammes bleues fondent sur l''ennemi. Peut aussi le brûler.');
INSERT INTO Attaque VALUES (339, 'Flamme Croix', '100', '100', '5', 'Feu', 'Spéciale', 'Projette une boule de feu gigantesque. L''effet augmente sous l''influence d''Éclair Croix.');
INSERT INTO Attaque VALUES (340, 'Flamme Ultime', '100', '130', '5', 'Feu', 'Spéciale', 'Le Pokémon se consume et les flammes de son corps infligent des dégâts élevés à la cible. Le lanceur perd le type Feu.');
INSERT INTO Attaque VALUES (341, 'Flammèche', '100', '40', '25', 'Feu', 'Spéciale', 'L''attaque a 10% de chance de brûler la cible.');
INSERT INTO Attaque VALUES (342, 'Flash', '100', '-', '20', 'Normal', 'Statut', 'Explosion lumineuse qui fait baisser la précision.');
INSERT INTO Attaque VALUES (343, 'Flatterie', '100', '-', '15', 'Ténèbres', 'Statut', 'Rend la cible confuse, mais augmente son Attaque Spéciale.');
INSERT INTO Attaque VALUES (344, 'Flying Press', '95', '100', '10', 'Combat', 'Physique', 'Une attaque en piqué depuis le ciel, à la fois de type Combat et de type Vol.');
INSERT INTO Attaque VALUES (345, 'Fontaine de Vie', '-', '-', '10', 'Eau', 'Statut', 'Le lanceur projette une eau mystérieuse autour du terrain pour restaurer ses PV et ceux de ses alliés au combat.');
INSERT INTO Attaque VALUES (346, 'Force Cachée', '100', '70', '20', 'Normal', 'Physique', 'Les effets de l''attaque dépendent du lieu.');
INSERT INTO Attaque VALUES (347, 'Force Chtonienne', '100', '90', '10', 'Sol', 'Physique', 'Utilise la puissance du sol et la concentre sur l''ennemi pour infliger des dégâts.');
INSERT INTO Attaque VALUES (348, 'Force Cosmique', '-', '-', '20', 'Psy', 'Statut', 'Le lanceur absorbe un pouvoir mystique spatial qui augmente sa Défense et sa Défense Spéciale.');
INSERT INTO Attaque VALUES (349, 'Force G', '100', '80', '10', 'Plante', 'Physique', 'Le lanceur attaque son adversaire avec une pomme qu''il fait tomber de très haut. Baisse la Défense de la cible.');
INSERT INTO Attaque VALUES (350, 'Force Nature', '-', '-', '20', 'Normal', 'Statut', 'Une attaque qui tire sa force de la nature. Son type varie selon le terrain.');
INSERT INTO Attaque VALUES (351, 'Force Poigne', '100', '55', '30', 'Normal', 'Physique', 'L''ennemi est attrapé et compressé par les côtés.');
INSERT INTO Attaque VALUES (352, 'Force', '100', '80', '15', 'Normal', 'Physique', 'Le lanceur cogne l''ennemi de toutes ses forces.');
INSERT INTO Attaque VALUES (353, 'Force Ajoutée', '100', '20', '10', 'Psy', 'Spéciale', 'Le lanceur attaque l''ennemi avec une force accumulée. Plus les stats du lanceur sont augmentées, plus le coup est efficace.');
INSERT INTO Attaque VALUES (354, 'Forte-Paume', '100', '60', '10', 'Combat', 'Physique', 'Une onde de choc frappe l''ennemi. Peut aussi paralyser la cible.');
INSERT INTO Attaque VALUES (355, 'Fouet de Feu', '100', '80', '15', 'Feu', 'Physique', 'Frappe la cible avec un fouet incandescent et baisse sa Défense.');
INSERT INTO Attaque VALUES (356, 'Fouet Lianes', '100', '45', '25', 'Plante', 'Physique', 'Fouette l''ennemi avec de fines lianes pour infliger des dégâts.');
INSERT INTO Attaque VALUES (357, 'Fourbette', '-', '80', '10', 'Ténèbres', 'Physique', 'Le lanceur fait semblant de se prosterner et utilise ses cheveux pour poignarder sa cible. Touche à tous les coups.');
INSERT INTO Attaque VALUES (358, 'Fracass''Tête', '80', '150', '5', 'Roche', 'Physique', 'Le lanceur subit 1/2 des dégâts infligés à l''adversaire. Ce chiffre est arrondi à l''inférieur.');
INSERT INTO Attaque VALUES (359, 'Frappe Atlas', '100', '1', '20', 'Combat', 'Physique', 'L''ennemi est projeté grâce au pouvoir de la gravité. Inflige des dégâts équivalents au niveau du lanceur.');
INSERT INTO Attaque VALUES (360, 'Frappe Psy', '100', '100', '10', 'Psy', 'Spéciale', 'Le lanceur matérialise des ondes mystérieuses qu''il projette sur l''ennemi. Inflige des dégâts physiques.');
INSERT INTO Attaque VALUES (361, 'Frénésie', '100', '20', '20', 'Normal', 'Physique', 'Augmente l''Attaque du lanceur d''un niveau à chaque coup reçu.');
INSERT INTO Attaque VALUES (362, 'Frotte-Frimousse', '100', '20', '20', 'Electrik', 'Physique', 'Le lanceur attaque en frottant ses bajoues chargées d''électricité. Paralyse l''ennemi.');
INSERT INTO Attaque VALUES (363, 'Frustration', '100', '1', '20', 'Normal', 'Physique', 'Attaque plus puissante si le DRESSEUR est mal aimé.');
INSERT INTO Attaque VALUES (364, 'Fulguro-Lance Gigavolt', '-', '1', '-', 'Electrik', 'Physique', 'Le Pokémon utilise la Force Z pour générer un courant électrique puissant qu’il projette sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (365, 'Fulguro-Lance Gigavolt', '-', '1', '-', 'Electrik', 'Spéciale', 'Le Pokémon utilise la Force Z pour générer un courant électrique puissant qu’il projette sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (366, 'Fulguromax', '-', '10', '10', 'Electrik', 'Physique', 'Une attaque de type Électrik que seuls les Pokémon Dynamax peuvent utiliser. Crée un Champ Électrifié qui dure cinq tours.');
INSERT INTO Attaque VALUES (367, 'Fulmifer', '100', '1', '10', 'Acier', 'Physique', 'Si la dernière attaque à toucher le lanceur venait d''un Pokémon adverse, cette attaque inflige 150% des dégâts reçus à ce Pokémon. Ne fonctionne que si le lanceur agit après l''attaquant.');
INSERT INTO Attaque VALUES (368, 'Fulmigraine', '85', '120', '5', 'Plante', 'Spéciale', 'L''attaque a 40% de chances de baisser la défense spéciale de l''adversaire de deux crans.');
INSERT INTO Attaque VALUES (369, 'Fureur des Plumes Spectrales', '-', '180', '-', 'Spectre', 'Physique', 'Archéduc utilise la Force Z pour créer un nuage de flèches qui transpercent la cible.');
INSERT INTO Attaque VALUES (370, 'Furie-Bond', '100', '80', '15', 'Insecte', 'Physique', 'Le lanceur se jette sur la cible de toutes ses forces pour lui infliger des dégâts et baisser son Attaque.');
INSERT INTO Attaque VALUES (371, 'Furie Dimension', '-', '100', '5', 'Ténèbres', 'Physique', 'Le Pokémon utilise sa multitude de bras pour infliger une nuée de coups qui ignorent les capacités telles qu''Abri ou Détection. Baisse la Défense du lanceur.');
INSERT INTO Attaque VALUES (372, 'Furie', '85', '15', '20', 'Normal', 'Physique', 'Touche 2 à 5 fois en un tour. Il y a 37.5% de chance de toucher 2 ou 3 fois, et 12.5% de chance de toucher 4 ou 5 fois.');
INSERT INTO Attaque VALUES (373, 'Garde-à-Joues', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur mange la Baie qu''il tient, ce qui augmente beaucoup sa Défense.');
INSERT INTO Attaque VALUES (374, 'Garde Florale', '-', '-', '10', 'Fée', 'Statut', 'Grâce à une force mystérieuse, la Défense de tous les Pokémon Plante au combat augmente.');
INSERT INTO Attaque VALUES (375, 'Garde Large', '-', '-', '10', 'Roche', 'Statut', 'Annule les attaques visant toute l''équipe pendant un tour.');
INSERT INTO Attaque VALUES (376, 'Gardomax', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur se protège de toutes les attaques. Peut échouer si utilisée plusieurs fois de suite.');
INSERT INTO Attaque VALUES (377, 'Gare au Ronflex', '-', '210', '-', 'Normal', 'Physique', 'Ronflex utilise la Force Z pour montrer ce qu’il a dans le ventre et écraser la cible de tout son poids.');
INSERT INTO Attaque VALUES (378, 'Gaz Corrosif', '100', '-', '40', 'poison', 'Statut', 'Dissout les objets tenus par les Pokémon sur le terrain');
INSERT INTO Attaque VALUES (379, 'Gaz Toxik', '90', '-', '40', 'poison', 'Statut', 'Un nuage de gaz toxique est projeté au visage de l''ennemi pour l''empoisonner.');
INSERT INTO Attaque VALUES (380, 'Géo-Contrôle', '-', '-', '10', 'Fée', 'Statut', 'Le lanceur absorbe de l''énergie au premier tour et augmente beaucoup son Attaque Spéciale, sa Défense Spéciale et sa Vitesse au second.');
INSERT INTO Attaque VALUES (381, 'Giclédo', '100', '150', '5', 'Eau', 'Spéciale', 'La puissance de base de l''attaque est de 150 * PVs actuels / PVs max. Si le Pokémon a toute sa vie, l''attaque a une puissance de 150.');
INSERT INTO Attaque VALUES (382, 'Giga Impact', '90', '150', '5', 'Normal', 'Physique', 'Le lanceur charge l''ennemi de toute sa puissance et doit ensuite se reposer au tour suivant.');
INSERT INTO Attaque VALUES (383, 'Giga-Sangsue', '100', '75', '10', 'Plante', 'Spéciale', 'Une attaque qui convertit la moitié des dégâts infligés en PV pour le lanceur.');
INSERT INTO Attaque VALUES (384, 'Giga-Tonnerre', '-', '195', '-', 'Electrik', 'Spéciale', 'Le Pikachu à casquette utilise la Force Z pour augmenter sa puissance électrique avant de la déchaîner sur la cible. Taux de critique élevé.');
INSERT INTO Attaque VALUES (385, 'Gigotage', '100', '1', '15', 'Normal', 'Physique', 'La puissance de l''attaque dépend des PV du lanceur. Voici la puissance en fonction des PV restants :');
INSERT INTO Attaque VALUES (386, 'Glaciation', '30', '1', '5', 'Glace', 'Spéciale', 'L''attaque inflige des dégâts égaux aux PVs max de la cible. Si celle-ci a lancé Tenacité ou tient la Ceinture Force, elle résistera avec 1 PV. Si Verrouillage ou Lire-Esprit ont été utilisées avant, ou si l''un des Pokémon possède la capacité Annule Garde, Glaciation touchera. Glaciation ignore les changements d''esquive et de précision, mais possède sa propre précision :');
INSERT INTO Attaque VALUES (387, 'Gladius Maximus', '100', '100', '5', 'Acier', 'Physique', 'Le lanceur se transforme en une immense épée et pourfend son adversaire. Inflige le double de dégâts aux Pokémon Dynamax.');
INSERT INTO Attaque VALUES (388, 'Glas de Soin', '-', '-', '5', 'Normal', 'Statut', 'Soigne brûlure, confusion, gel, paralysie, poison ou sommeil de tous les Pokémon de l''équipe du lanceur. Aucun Pokémon n''est soigné si un Pokémon de l''équipe possède la capacité spéciale Anti-Bruit.');
INSERT INTO Attaque VALUES (389, 'Gliss''Herbe', '100', '70', '20', 'Plante', 'Physique', 'Capacité prioritaire si le terrain est couvert d''un Champ Herbu.');
INSERT INTO Attaque VALUES (390, 'Gonflette', '-', '-', '20', 'Combat', 'Statut', 'Le lanceur tend ses muscles pour se gonfler, ce qui booste son Attaque et sa Défense.');
INSERT INTO Attaque VALUES (391, 'Goudronnage', '100', '-', '15', 'Roche', 'Statut', 'Le lanceur recouvre sa cible de goudron liquide pour baisser sa Vitesse et la rendre vulnérable au feu.');
INSERT INTO Attaque VALUES (392, 'Gravité', '-', '-', '5', 'Psy', 'Statut', 'Pendant cinq tours, les Pokémon Vol ou qui ont Lévitation deviennent sensibles aux capacités Sol, et les capacités volantes deviennent inutilisables.');
INSERT INTO Attaque VALUES (393, 'Grêle', '-', '-', '10', 'Glace', 'Statut', 'Invoque une tempête de grêle qui dure cinq tours. Blesse tous les Pokémon, sauf ceux de type Glace.');
INSERT INTO Attaque VALUES (394, 'Gribouille', '-', '-', '-', 'Normal', 'Statut', 'Copie de manière permanente la dernière attaque adverse. Lutte et Babil ne peuvent être copiées par Gribouille.');
INSERT INTO Attaque VALUES (395, 'Griffe Acier', '95', '50', '35', 'Acier', 'Physique', 'L''attaque a 10% de chance d''augmenter l''Attaque du lanceur d''un niveau.');
INSERT INTO Attaque VALUES (396, 'Griffe Ombre', '100', '70', '15', 'Spectre', 'Physique', 'Attaque avec une griffe puissante faite d''ombres. Taux de critiques élevé.');
INSERT INTO Attaque VALUES (397, 'Griffe', '100', '40', '35', 'Normal', 'Physique', 'Lacère l''ennemi avec des griffes acérées pour lui infliger des dégâts.');
INSERT INTO Attaque VALUES (398, 'Grimace', '100', '-', '10', 'Normal', 'Statut', 'Le lanceur fait une grimace qui effraie l''ennemi et réduit beaucoup sa Vitesse.');
INSERT INTO Attaque VALUES (399, 'Grincement', '85', '-', '40', 'Normal', 'Statut', 'Le lanceur émet un son strident qui baisse beaucoup la Défense de l''ennemi.');
INSERT INTO Attaque VALUES (400, 'Grobisou', '75', '-', '10', 'Normal', 'Statut', 'Le lanceur fait un bisou à l''ennemi en prenant une mine effrayante. Endort l''ennemi.');
INSERT INTO Attaque VALUES (401, 'Grondement', '-', '-', '40', 'Normal', 'Statut', 'Le lanceur grogne pour se rassurer, ce qui augmente son Attaque et celle de ses alliés.');
INSERT INTO Attaque VALUES (402, 'Groz''Yeux', '100', '-', '30', 'Normal', 'Statut', 'Le lanceur fait les gros yeux à l''ennemi pour l''intimider et baisser sa Défense.');
INSERT INTO Attaque VALUES (403, 'Guillotine', '30', '1', '5', 'Normal', 'Physique', 'L''attaque inflige des dégâts égaux aux PVs max de la cible. Si celle-ci a lancé Tenacité ou tient la Ceinture Force, elle résistera avec 1 PV. Si Verrouillage ou Lire-Esprit ont été utilisées avant, ou si l''un des Pokémon possède la capacité Annule Garde, Guillotine touchera. Guillotine ignore les changements d''esquive et de précision, mais possède sa propre précision :');
INSERT INTO Attaque VALUES (404, 'Gyroballe', '100', '1', '5', 'Acier', 'Physique', 'Le lanceur effectue une rotation et frappe l''ennemi. Plus la Vitesse du lanceur est basse, plus il inflige de dégâts.');
INSERT INTO Attaque VALUES (405, 'Halloween', '100', '-', '20', 'Spectre', 'Statut', 'Insuffle à la cible l''esprit d''Halloween, et ajoute le type Spectre à ses types actuels.');
INSERT INTO Attaque VALUES (406, 'Hantise', '100', '90', '10', 'Spectre', 'Physique', 'Le lanceur disparaît au premier tour et frappe au second. Cette attaque passe outre les protections.');
INSERT INTO Attaque VALUES (407, 'Harcèlement', '100', '20', '20', 'Insecte', 'Spéciale', 'Cette attaque perdure pendant quatre à cinq tours. L''ennemi ne peut pas fuir au cours de cette période.');
INSERT INTO Attaque VALUES (408, 'Hâte', '-', '-', '30', 'Psy', 'Statut', 'Le lanceur se relaxe et allège son corps pour beaucoup augmenter sa Vitesse.');
INSERT INTO Attaque VALUES (409, 'Hélio-Choc Dévastateur', '-', '200', '-', 'Acier', 'Physique', 'Aucune description...');
INSERT INTO Attaque VALUES (410, 'Hurle-Temps', '90', '150', '5', 'Dragon', 'Spéciale', 'Le lanceur frappe si fort qu''il affecte le cours du temps. Il se repose au tour suivant.');
INSERT INTO Attaque VALUES (411, 'Hurlement des Roches-Lames', '-', '190', '-', 'Roche', 'Physique', 'Aucune description...');
INSERT INTO Attaque VALUES (412, 'Hurlement', '-', '-', '20', 'Normal', 'Statut', 'Effraie le Pokémon ennemi et le remplace par un autre. Lors d''un combat contre un Pokémon sauvage seul, met fin au combat.');
INSERT INTO Attaque VALUES (413, 'Hydro-Queue', '90', '90', '10', 'Eau', 'Physique', 'Le lanceur attaque en balançant sa queue comme une lame de fond en pleine tempête.');
INSERT INTO Attaque VALUES (414, 'Hydroblast', '90', '150', '5', 'Eau', 'Spéciale', 'Une trombe d''eau heurte l''ennemi. Le lanceur doit se reposer au tour suivant.');
INSERT INTO Attaque VALUES (415, 'Hydrocanon', '80', '110', '5', 'Eau', 'Spéciale', 'Un puissant jet d''eau est dirigé sur l''ennemi.');
INSERT INTO Attaque VALUES (416, 'Hydromax', '-', '10', '10', 'Eau', 'Physique', 'Une attaque de type Eau que seuls les Pokémon Dynamax peuvent utiliser. Invoque de fortes pluies qui durent cinq tours.');
INSERT INTO Attaque VALUES (417, 'Hypnose', '60', '-', '20', 'Psy', 'Statut', 'Le lanceur hypnotise l''ennemi pour le plonger dans un profond sommeil.');
INSERT INTO Attaque VALUES (418, 'Imitation', '-', '-', '10', 'Psy', 'Statut', 'Remplace la capacité du lanceur par celle de la cible. L''attaque échoue si la capacité de la cible est Garde Mystik. Imitation ignore les changements d''esquive et de précision.');
INSERT INTO Attaque VALUES (419, 'Impact Choupinova', '-', '1', '-', 'Fée', 'Physique', 'Le Pokémon utilise la Force Z pour créer une dimension irréelle où l’adversaire est à sa merci. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (420, 'Impact Choupinova', '-', '1', '-', 'Fée', 'Spéciale', 'Le Pokémon utilise la Force Z pour créer une dimension irréelle où l’adversaire est à sa merci. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (421, 'Implore', '100', '60', '25', 'Normal', 'Physique', 'Donne au lanceur l''objet de la cible. Si le lanceur possède déjà un objet ou que la cible n''en possède pas, Implore inflige des dégâts mais n''a pas d''effet secondaire. Si la cible possède la capacité spéciale Glue ou Multitype, l''effet secondaire ne se déclenche pas.');
INSERT INTO Attaque VALUES (422, 'Incendie', '100', '100', '5', 'Feu', 'Spéciale', 'Des boules de feu s''abattent sur tous les Pokémon autour du lanceur. Peut aussi les brûler.');
INSERT INTO Attaque VALUES (423, 'Insectomax', '-', '10', '10', 'Insecte', 'Physique', 'Une attaque de type Insecte que seuls les Pokémon Dynamax peuvent utiliser. Baisse l''Attaque Spéciale de la cible.');
INSERT INTO Attaque VALUES (424, 'Interversion', '-', '-', '15', 'Psy', 'Statut', 'Le lanceur se téléporte à l''aide d''un pouvoir mystérieux. Il échange sa place avec celle d''un allié sur le terrain.');
INSERT INTO Attaque VALUES (425, 'Regard Médusant', '100', '-', '30', 'Normal', 'Statut', 'Le lanceur intimide l''ennemi grâce à son regard terrifiant pour le paralyser.');
INSERT INTO Attaque VALUES (426, 'Ire de la Nature', '90', '1', '10', 'Fée', 'Spéciale', 'Le lanceur déchaîne toute la colère de la nature pour baisser les PV de la cible de moitié.');
INSERT INTO Attaque VALUES (427, 'Jackpot', '100', '40', '20', 'Normal', 'Physique', 'Des pièces sont lancées sur l''ennemi. Permet d''obtenir de l''argent à la fin du combat.');
INSERT INTO Attaque VALUES (428, 'Jet de Sable', '100', '-', '15', 'Sol', 'Statut', 'Baisse la précision de l''adversaire d''un cran.');
INSERT INTO Attaque VALUES (429, 'Jet de Vapeur', '95', '110', '5', 'Eau', 'Spéciale', 'Plonge l''ennemi dans une chaleur étouffante. Peut le brûler.');
INSERT INTO Attaque VALUES (430, 'Jet-Pierres', '90', '50', '15', 'Roche', 'Physique', 'Le lanceur lâche une pierre sur l''ennemi.');
INSERT INTO Attaque VALUES (431, 'Joute Astrale', '100', '150', '5', 'Combat', 'Physique', 'Le lanceur attaque son adversaire avec son poireau. Toutefois, cette action le fait vaciller et il doit se reposer au tour suivant.');
INSERT INTO Attaque VALUES (432, 'Jugement', '100', '100', '10', 'Normal', 'Spéciale', 'Le lanceur libère une myriade de rayons de lumière. Le type varie selon la Plaque que tient le lanceur.');
INSERT INTO Attaque VALUES (433, 'Kokiyarme', '100', '90', '10', 'poison', 'Spéciale', 'Devient physique ou spéciale selon les défenses de la cible. Peut empoisonner.');
INSERT INTO Attaque VALUES (434, 'Koud''Korne', '100', '65', '25', 'Normal', 'Physique', 'Frappe l''ennemi d''un coup de corne pointue pour infliger des dégâts.');
INSERT INTO Attaque VALUES (435, 'Lait à Boire', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur récupère jusqu''à la moitié de ses PV max.');
INSERT INTO Attaque VALUES (436, 'Lame d''Air', '95', '75', '15', 'Vol', 'Spéciale', 'Le lanceur attaque avec une lame d''air qui fend tout. Peut aussi apeurer l''ennemi.');
INSERT INTO Attaque VALUES (437, 'Lame de Roc', '80', '100', '5', 'Roche', 'Physique', 'Fait surgir des pierres aiguisées sous l''ennemi. Taux de critiques élevé.');
INSERT INTO Attaque VALUES (438, 'Lame Feuille', '100', '90', '15', 'Plante', 'Physique', 'Une feuille coupante comme une lame entaille l''ennemi. Taux de critiques élevé.');
INSERT INTO Attaque VALUES (439, 'Lame Ointe', '100', '85', '10', 'Combat', 'Spéciale', 'L''ennemi est lacéré par une longue corne. Son pouvoir mystérieux inflige des dégâts physiques.');
INSERT INTO Attaque VALUES (440, 'Lame Pangéenne', '85', '120', '10', 'Sol', 'Physique', 'Le Pokémon transforme la puissance de la terre et attaque la cible avec une lame acérée.');
INSERT INTO Attaque VALUES (441, 'Lame Sainte', '100', '90', '15', 'Combat', 'Physique', 'Un coup de corne violent qui lacère l''ennemi et lui inflige des dégâts quels que soient ses changements de stats.');
INSERT INTO Attaque VALUES (442, 'Lame Solaire', '100', '125', '10', 'Plante', 'Physique', 'Le lanceur absorbe la lumière au premier tour et attaque au second tour en libérant cette énergie sous forme de lames.');
INSERT INTO Attaque VALUES (443, 'Lance-Boue', '-', '-', '15', 'Sol', 'Statut', 'Les attaques Electriques infligent 50% de dégâts en moins.');
INSERT INTO Attaque VALUES (444, 'Lance-Flammes', '100', '90', '15', 'Feu', 'Spéciale', 'L''ennemi reçoit un torrent de flammes. Peut aussi le brûler.');
INSERT INTO Attaque VALUES (445, 'Lance-Soleil', '100', '120', '10', 'Plante', 'Spéciale', 'Absorbe la lumière au premier tour et envoie un rayon puissant au tour suivant.');
INSERT INTO Attaque VALUES (446, 'Lancécrou', '85', '50', '15', 'Acier', 'Physique', 'Le lanceur jette deux écrous d''acier qui frappent l''ennemi deux fois d''affilée.');
INSERT INTO Attaque VALUES (447, 'Larcin', '100', '60', '25', 'Ténèbres', 'Physique', 'Le lanceur attaque la cible et vole son objet. Le lanceur ne peut rien voler s''il tient déjà un objet.');
INSERT INTO Attaque VALUES (448, 'Larme à l''Oeil', '-', '-', '20', 'Normal', 'Statut', 'Le lanceur jette un regard plein de larmes à la cible. Celle-ci perd toute velléité de combat et voit son Attaque et son Attaque Spéciale baisser.');
INSERT INTO Attaque VALUES (449, 'Laser Cryogénique', '-', '1', '-', 'Glace', 'Physique', 'Le Pokémon utilise la Force Z pour baisser la température brutalement et congeler l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (450, 'Laser Cryogénique', '-', '1', '-', 'Glace', 'Spéciale', 'Le Pokémon utilise la Force Z pour baisser la température brutalement et congeler l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (451, 'Laser Glace', '100', '90', '10', 'Glace', 'Spéciale', 'Un rayon de glace frappe l''ennemi. Peut aussi le geler.');
INSERT INTO Attaque VALUES (452, 'Laser Infinimax', '90', '160', '5', 'Dragon', 'Spéciale', 'L''attaque la plus puissante d''Éthernatos sous sa forme originelle. S''il l''utilise, il devra se reposer au tour suivant.');
INSERT INTO Attaque VALUES (453, 'Laser Météore', '90', '120', '10', 'Roche', 'Spéciale', 'Charge au premier tour en augmentant d''un cran l''Attaque Spéciale du lanceur, puis attaque au second.');
INSERT INTO Attaque VALUES (454, 'Laser Prisme', '100', '160', '10', 'Psy', 'Spéciale', 'Le lanceur utilise la puissance d''un prisme pour envoyer un laser destructeur, mais il doit se reposer au tour suivant.');
INSERT INTO Attaque VALUES (455, 'Léchouille', '100', '30', '30', 'Spectre', 'Physique', 'L''attaque a 30% de chance de paralyser la cible.');
INSERT INTO Attaque VALUES (456, 'Lévikinésie', '-', '-', '15', 'Psy', 'Statut', 'Un pouvoir qui fait flotter l''ennemi dans les airs. Pendant 3 tours, il devient plus facile à atteindre.');
INSERT INTO Attaque VALUES (457, 'Lien du Destin', '-', '-', '5', 'Spectre', 'Statut', 'Si le lanceur est mis KO avant sa prochaine attaque, le Pokémon l''ayant mis KO l''est à son tour . Ceci ne prend pas en compte les dégâts de fin de tour comme la Grêle, la Tempêtesable, Vampigraine ou de status.');
INSERT INTO Attaque VALUES (458, 'Ligotage', '90', '15', '20', 'Normal', 'Physique', 'Ligotage dure entre 2 et 5 tours en tant qu''effet passif, donc le lanceur peut choisir d''autres attaques après avoir lancé Ligotage pendant que celle-ci est toujours active. Elle inflige 1/16 de la vie totale de la cible à chaque tour et l''empêche de switcher. L''effet est annulé si le lanceur switche, même si le compteur des 2 à 5 tours n''était pas encore terminé.');
INSERT INTO Attaque VALUES (459, 'Lilliput', '-', '-', '10', 'Normal', 'Statut', 'L''attaque augmente l''esquive du lanceur d''un niveau.');
INSERT INTO Attaque VALUES (460, 'Lire-Esprit', '-', '-', '5', 'Normal', 'Statut', 'L''attaque utilisée lors du prochain tour réussira à coup sûr. Lire-Esprit ignore les modifications de précision ou d''esquive. Si la cible utilise Abri ou Détection après être touché par Lire-Esprit, alors l''attaque utilisée par le lanceur ce tour a 100 - Précision de base chance de passer à travers Abri et Détection. Cette possibilité ne s''applique pas aux attaques OHKO.');
INSERT INTO Attaque VALUES (461, 'Lithomax', '-', '10', '10', 'Roche', 'Physique', 'Une attaque de type Roche que seuls les Pokémon Dynamax peuvent utiliser. Invoque une tempête de sable qui dure cinq tours.');
INSERT INTO Attaque VALUES (462, 'Lumi-Éclat', '100', '70', '5', 'Psy', 'Spéciale', 'L''attaque a 50% de chance de baisser la Défense Spéciale de l''adversaire d''un niveau.');
INSERT INTO Attaque VALUES (463, 'Lumière du Néant', '90', '140', '5', 'Fée', 'Spéciale', 'Utilise la puissance de la fleur Éternelle pour lancer un formidable rayon d''énergie. Blesse aussi gravement le lanceur.');
INSERT INTO Attaque VALUES (464, 'Luminocanon', '100', '80', '10', 'Acier', 'Spéciale', 'Le lanceur concentre son énergie lumineuse et la fait exploser. Peut aussi baisser la Défense Spéciale de l''ennemi.');
INSERT INTO Attaque VALUES (465, 'Lumi-Queue', '-', '-', '20', 'Insecte', 'Statut', 'L''attaque monte l''attaque spéciale du lanceur de deux crans.');
INSERT INTO Attaque VALUES (466, 'Lutte', '-', '50', '-', 'Aucun', 'Physique', 'Le lanceur perd 25% de sa vie à chaque attaque. Cette attaque est lancée automatiquement si le Pokémon ne peut plus utiliser aucune de ses attaques. Lutte ignore les changements d''esquive et de précision, ainsi que les immunités et les résistances.');
INSERT INTO Attaque VALUES (467, 'Lyophilisation', '100', '70', '20', 'Glace', 'Spéciale', 'Refroidit violemment l''ennemi et peut le geler. Super efficace sur les Pokémon de type Eau.');
INSERT INTO Attaque VALUES (468, 'Mach Punch', '100', '40', '30', 'Combat', 'Physique', 'Coup de poing fulgurant. Frappe en priorité.');
INSERT INTO Attaque VALUES (469, 'Machination', '-', '-', '20', 'Ténèbres', 'Statut', 'Stimule l''esprit par de mauvaises pensées. Augmente beaucoup l''Attaque Spéciale du lanceur.');
INSERT INTO Attaque VALUES (470, 'Mâchouille', '100', '80', '15', 'Ténèbres', 'Physique', 'Le lanceur mord l''ennemi de ses crocs pointus. Peut aussi baisser sa Défense.');
INSERT INTO Attaque VALUES (471, 'Magné-Contrôle', '-', '-', '20', 'Electrik', 'Statut', 'Manipule les champs magnétiques pour augmenter la Défense et la Défense Spéciale des Pokémon alliés dotés du talent Plus ou du talent Moins.');
INSERT INTO Attaque VALUES (472, 'Mains Jointes', '-', '-', '40', 'Normal', 'Statut', 'Le lanceur et un allié se prennent la main, ce qui les rend heureux.');
INSERT INTO Attaque VALUES (473, 'Malédiction', '-', '-', '10', 'Spectre', 'Statut', 'Sacrifie la moitié des PV Max du lanceur et fait perdre à la cible 1/4 de ses PV Max par tour si le lanceur est de type Spectre. L''effet est annulé si la cible switche. L''effet est transmis par Relais.');
INSERT INTO Attaque VALUES (474, 'Maléfice Sylvain', '100', '-', '20', 'Plante', 'Statut', 'La cible est charmée par l''esprit de la forêt. Le type Plante est ajouté à ses types actuels.');
INSERT INTO Attaque VALUES (475, 'Mania', '100', '120', '10', 'Normal', 'Physique', 'Le lanceur est bloqué sur cette attaque pendant 2 à 3 tours. Une fois terminée, le lanceur est confus.');
INSERT INTO Attaque VALUES (476, 'Marteau de Glace', '90', '100', '10', 'Glace', 'Physique', 'Le lanceur donne un puissant coup de poing à l''ennemi. Réduit la Vitesse du lanceur.');
INSERT INTO Attaque VALUES (477, 'Marto-Poing', '90', '100', '10', 'Combat', 'Physique', 'Le lanceur donne un puissant coup de poing à l''ennemi. Réduit la Vitesse du lanceur.');
INSERT INTO Attaque VALUES (478, 'Martobois', '100', '120', '15', 'Plante', 'Physique', 'Le lanceur s''inflige 1/3 des dégâts infligés à la cible, arrondis à l''inférieur.');
INSERT INTO Attaque VALUES (479, 'Massd''Os', '85', '65', '20', 'Sol', 'Physique', 'L''attaque a 10% de chance d''apeurer la cible.');
INSERT INTO Attaque VALUES (480, 'Mawashi Geri', '85', '60', '15', 'Combat', 'Physique', 'L''attaque a 30% de chances d''apeurer l''adversaire.');
INSERT INTO Attaque VALUES (481, 'Méga-Sangsue', '100', '40', '15', 'Plante', 'Spéciale', 'Une attaque qui convertit la moitié des dégâts infligés en PV pour le lanceur.');
INSERT INTO Attaque VALUES (482, 'Mégacorne', '85', '120', '10', 'Insecte', 'Physique', 'Le lanceur utilise ses gigantesques cornes pour charger l''ennemi.');
INSERT INTO Attaque VALUES (483, 'Mégafouet', '85', '120', '10', 'Plante', 'Physique', 'Le lanceur fait virevolter violemment ses lianes ou ses tentacules pour fouetter l''ennemi.');
INSERT INTO Attaque VALUES (484, 'Mégaphone', '100', '90', '10', 'Normal', 'Spéciale', 'Le lanceur pousse un cri dont l''écho terrifiant a le pouvoir d''infliger des dégâts à l''ennemi.');
INSERT INTO Attaque VALUES (485, 'Métalaser', '95', '140', '5', 'Acier', 'Spéciale', 'Le lanceur concentre du métal issu de tout son corps en un rayon qu''il projette violemment sur sa cible. Il subit aussi des dégâts.');
INSERT INTO Attaque VALUES (486, 'Métalliroue', '100', '130', '5', 'Acier', 'Physique', 'Fait disparaître le champ actif mais échoue s''il n''y en a pas.');
INSERT INTO Attaque VALUES (487, 'Métallomax', '-', '10', '10', 'Acier', 'Physique', 'Une attaque de type Acier que seuls les Pokémon Dynamax peuvent utiliser. Augmente la Défense des alliés.');
INSERT INTO Attaque VALUES (488, 'Météores', '-', '60', '20', 'Normal', 'Spéciale', 'Le lanceur envoie des rayons d''étoiles. Touche toujours l''ennemi.');
INSERT INTO Attaque VALUES (489, 'Métronome', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur agite un doigt et stimule son cerveau pour utiliser presque n''importe quelle capacité au hasard.');
INSERT INTO Attaque VALUES (490, 'Mimi-Queue', '100', '-', '30', 'Normal', 'Statut', 'L''attaque baisse la défense de la cible d''un cran.');
INSERT INTO Attaque VALUES (491, 'Mimique', '-', '-', '20', 'Vol', 'Statut', 'Utilise la dernière attaque qui a ciblé le lanceur par un Pokémon qui est toujours sur le terrain. Les attaques ayant plusieurs cibles comptent tant que le lanceur de Mimique était une des cibles, sinon l''attaque échoue. Mimique peut copier des attaques même si elles ratent, échouent, n''affectent pas la cible, sont bloquées par Abri ou Détection, ou un Clone.');
INSERT INTO Attaque VALUES (492, 'Miroi-Tir', '85', '65', '10', 'Acier', 'Spéciale', 'L''attaque a 30% de chance de diminuer la précision de l''adversaire d''un niveau.');
INSERT INTO Attaque VALUES (493, 'Mitra-Poing', '100', '150', '20', 'Combat', 'Physique', 'Le lanceur se concentre avant d''attaquer. Échoue s''il est touché avant d''avoir frappé.');
INSERT INTO Attaque VALUES (494, 'Moi d''Abord', '-', '1', '20', 'Normal', 'Statut', 'Si l''adversaire utilise une attaque offensive et que le lanceur de Moi d''Abord est plus rapide, alors il lancera l''attaque ennemie avec une puissance boostée de 50 %. Le STAB est appliqué si le lanceur est du même type que l''attaque adverse.');
INSERT INTO Attaque VALUES (495, 'Monte-Tension', '100', '70', '20', 'Electrik', 'Spéciale', 'La puissance est doublée si la cible est sur un Champ Électrifié.');
INSERT INTO Attaque VALUES (496, 'Morphing', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur se transforme en son adversaire, copiant ses stats (exceptés les PVs), les attaques (5 PPs chacune), le type, le poids et la capacité spéciale. Les stats sont copiées de manière exacte, sans se soucier des EVs, IVs, niveau et Base Stats. Les IVs sont tout de même récupérés pour la puissance et le type de Puissance Cachée. Morphing copie l''espèce, ce qui signifie que l''Ecaille Océan, la Dent Océan, la Masso Os, le Poing Chance, la Balle Lumière, la Poudre Métal, la Poudre Vitesse, la Rosée Ame et le Bâton ne s''activeront que si le Pokémon copié est de l''espèce appropriée. Le niveau n''est pas copié, donc un bas niveau infligera moins de dégâts qu''un haut niveau car le niveau de l''attaquant entre en compte dans le calcul des dégâts.');
INSERT INTO Attaque VALUES (497, 'Morsure', '100', '60', '25', 'Ténèbres', 'Physique', 'L''attaque a 30% de chance d''apeurer l''ennemi.');
INSERT INTO Attaque VALUES (498, 'Mort''Ailes', '100', '80', '10', 'Vol', 'Spéciale', 'Vole l''énergie de la cible. Rend au lanceur un nombre de PV supérieur ou égal à la moitié des dégâts infligés.');
INSERT INTO Attaque VALUES (499, 'Mur de Fer', '-', '-', '15', 'Acier', 'Statut', 'L''épiderme du lanceur devient dur comme du fer, ce qui augmente beaucoup sa Défense.');
INSERT INTO Attaque VALUES (500, 'Mur Lumière', '-', '-', '30', 'Psy', 'Statut', 'Crée un fabuleux mur de lumière qui réduit les dégâts causés par les capacités spéciales pendant cinq tours.');
INSERT INTO Attaque VALUES (501, 'Myria-Flèches', '100', '90', '10', 'Sol', 'Physique', 'Touche même les Pokémon dans les airs. Dans ce cas, la cible retombe au sol.');
INSERT INTO Attaque VALUES (502, 'Myria-Vagues', '100', '90', '10', 'Sol', 'Physique', 'Attaque avec des vagues glissant au sol. L''ennemi pris dedans ne peut pas s''échapper.');
INSERT INTO Attaque VALUES (503, 'Nappage', '-', '-', '15', 'Fée', 'Statut', 'Le lanceur augmente beaucoup l''Attaque et l''Attaque Spéciale de la cible en la nappant de glaçage.');
INSERT INTO Attaque VALUES (504, 'Neuf pour Un', '-', '-', '-', 'Normal', 'Statut', 'Évoli utilise la Force Z pour emprunter la puissance de tous ses amis évolués et beaucoup augmenter toutes ses stats.');
INSERT INTO Attaque VALUES (505, 'Nitrocharge', '100', '50', '20', 'Feu', 'Physique', 'Le lanceur s''entoure de flammes pour attaquer l''ennemi. Il se concentre et sa Vitesse augmente.');
INSERT INTO Attaque VALUES (506, 'Noeud Herbe', '100', '1', '20', 'Plante', 'Spéciale', 'L''ennemi est piégé dans de l''herbe qui le fait trébucher. Plus il est lourd, plus il subit de dégâts.');
INSERT INTO Attaque VALUES (507, 'Normalomax', '-', '10', '10', 'Normal', 'Physique', 'Une attaque de type Normal que seuls les Pokémon Dynamax peuvent utiliser. Baisse la Vitesse de la cible.');
INSERT INTO Attaque VALUES (508, 'Nuée de Poudre', '100', '-', '20', 'Insecte', 'Statut', 'L''ennemi est pris dans un nuage de poudre. S''il utilise une capacité de type Feu lors du même tour, le nuage explose et lui inflige des dégâts.');
INSERT INTO Attaque VALUES (509, 'Ocroupi', '85', '90', '10', 'Eau', 'Spéciale', 'Le lanceur attaque en projetant de l''eau boueuse. Peut aussi réduire la Précision de l''ennemi.');
INSERT INTO Attaque VALUES (510, 'Octazooka', '85', '65', '10', 'Eau', 'Spéciale', 'L''attaque a 50% de chance de baisser la précision de l''adversaire d''un niveau.');
INSERT INTO Attaque VALUES (511, 'Octoprise', '100', '-', '15', 'Combat', 'Statut', 'Le lanceur empêche l''adversaire de fuir. À chaque tour qui passe, cette capacité baisse la Défense et la Défense Spéciale de la cible.');
INSERT INTO Attaque VALUES (512, 'Oeil Miracle', '-', '-', '40', 'Psy', 'Statut', 'Annule toutes les modifications d''esquive de l''adversaire, et l''empêche de l''améliorer. Les attaques Psy peuvent toucher les Pokémon Tenebres sous Oeil Miracle.');
INSERT INTO Attaque VALUES (513, 'Ombre Nocturne', '100', '1', '15', 'Spectre', 'Spéciale', 'L''attaque inflige des dégâts égaux au niveau du lanceur. Les Pokémon Normal sont immunisés.');
INSERT INTO Attaque VALUES (514, 'Ombre Portée', '100', '40', '30', 'Spectre', 'Physique', 'Attaque à priorité +1.');
INSERT INTO Attaque VALUES (515, 'Onde Boréale', '100', '65', '20', 'Glace', 'Spéciale', 'L''attaque a 10% de chance de baisser l''Attaque de la cible d''un niveau.');
INSERT INTO Attaque VALUES (516, 'Onde de Choc', '-', '60', '20', 'Electrik', 'Spéciale', 'Le lanceur envoie un choc électrique rapide à l''ennemi. Impossible à esquiver.');
INSERT INTO Attaque VALUES (517, 'Onde Folie', '100', '-', '10', 'Spectre', 'Statut', 'L''attaque rend confus la cible.');
INSERT INTO Attaque VALUES (518, 'Onde Originelle', '85', '110', '10', 'Eau', 'Spéciale', 'D''innombrables rayons lumineux d''un bleu étincelant s''abattent sur la cible.');
INSERT INTO Attaque VALUES (519, 'Onde Vide', '100', '40', '30', 'Combat', 'Spéciale', 'Le lanceur attaque en premier.');
INSERT INTO Attaque VALUES (520, 'Ondes Étranges', '100', '-', '15', 'Electrik', 'Statut', 'Le corps du lanceur produit des ondes anormales qui enveloppent l''ennemi et diminuent beaucoup son Attaque Spéciale.');
INSERT INTO Attaque VALUES (521, 'Orage Adamantin', '95', '100', '5', 'Roche', 'Physique', 'Provoque une tempête de diamants qui inflige des dégâts. Peut beaucoup augmenter la Défense du lanceur.');
INSERT INTO Attaque VALUES (522, 'Os Ombre', '100', '85', '10', 'Spectre', 'Physique', 'Le lanceur frappe avec un os possédé par l''âme d''un défunt. Peut aussi baisser la Défense de la cible.');
INSERT INTO Attaque VALUES (523, 'Osmerang', '90', '50', '10', 'Sol', 'Physique', 'L''attaque frappe deux fois en un tour.');
INSERT INTO Attaque VALUES (524, 'Ouragan', '100', '40', '20', 'Dragon', 'Spéciale', 'L''attaque a 20% de chances d''apeurer le Pokémon adverse.');
INSERT INTO Attaque VALUES (525, 'Overdrive', '100', '80', '10', 'Electrik', 'Spéciale', 'Le lanceur gratte ses cordes de guitare ou de basse pour créer de violentes vibrations sonores qui blessent la cible.');
INSERT INTO Attaque VALUES (526, 'Papillodanse', '-', '-', '20', 'Insecte', 'Statut', 'Une danse mystique dont le rythme parfait augmente l''Attaque Spéciale, la Défense Spéciale et la Vitesse du lanceur.');
INSERT INTO Attaque VALUES (527, 'Par Ici', '-', '-', '20', 'Normal', 'Statut', 'Toutes les attaques lancées par l''adversaire ciblent le lanceur. Si deux Pokémon utilisent Par Ici pendant le même tour, les attaques cibleront celui qui l''a lancée en second.');
INSERT INTO Attaque VALUES (528, 'Para-Spore', '75', '-', '30', 'Plante', 'Statut', 'L''adversaire est paralysé.');
INSERT INTO Attaque VALUES (529, 'Parabocharge', '100', '65', '20', 'Electrik', 'Spéciale', 'Inflige des dégâts à tous les Pokémon autour du lanceur. Il récupère en PV la moitié des dégâts infligés.');
INSERT INTO Attaque VALUES (530, 'Paresse', '-', '-', '10', 'Normal', 'Statut', 'Soigne 50% de la vie du lanceur.');
INSERT INTO Attaque VALUES (531, 'Partage Force', '-', '-', '10', 'Psy', 'Statut', 'Additionne l''Attaque Spéciale et l''Attaque du lanceur et de sa cible et les redistribue équitablement entre les deux.');
INSERT INTO Attaque VALUES (532, 'Partage Garde', '-', '-', '10', 'Psy', 'Statut', 'Additionne la Défense et la Défense Spéciale du lanceur et de sa cible et les redistribue équitablement entre les deux.');
INSERT INTO Attaque VALUES (533, 'Passe-Cadeau', '-', '-', '15', 'Normal', 'Statut', 'Si la cible ne tient pas d''objet, le lanceur lui donne l''objet qu''il porte.');
INSERT INTO Attaque VALUES (534, 'Passe-Passe', '100', '-', '10', 'Ténèbres', 'Statut', 'Les objets portés sont échangés entre le lanceur et la cible. L''attaque échoue contre les Pokémon dotés de Glue ou de Multi-type. L''attaque fonctionne même si la cible ne porte pas d''objet.');
INSERT INTO Attaque VALUES (535, 'Patati-Patattrape', '-', '190', '-', 'Fée', 'Physique', 'Aucune description...');
INSERT INTO Attaque VALUES (536, 'Patience', '-', '1', '10', 'Normal', 'Physique', 'Patience est une attaque à trois tours. Durant les deux premiers tours, le lanceur emmagasine de l''énergie. Lors du dernier tour, le lanceur touche le dernier Pokémon à lui avoir infligé des dégâts avec une attaque qui lui inflige le double des dégâts qu''il a reçu durant les trois tours. L''attaque touche les Spectres.');
INSERT INTO Attaque VALUES (537, 'Peignée', '100', '120', '15', 'Normal', 'Physique', 'Le lanceur donne un coup avec sa tête couronnée d''une fière crinière. Blesse aussi légèrement le lanceur.');
INSERT INTO Attaque VALUES (538, 'Permucoeur', '-', '-', '10', 'Psy', 'Statut', 'Le lanceur utilise un pouvoir psychique pour échanger ses changements de stats avec la cible.');
INSERT INTO Attaque VALUES (539, 'Permuforce', '-', '-', '10', 'Psy', 'Statut', 'Pouvoir qui échange les modifications de l''Attaque Spéciale et de l''Attaque du lanceur avec la cible.');
INSERT INTO Attaque VALUES (540, 'Permugarde', '-', '-', '10', 'Psy', 'Statut', 'Pouvoir qui échange les modifications de la Défense Spéciale et de la Défense avec la cible.');
INSERT INTO Attaque VALUES (541, 'Permuvitesse', '-', '-', '10', 'Psy', 'Statut', 'Intervertit la Vitesse du lanceur et celle de la cible.');
INSERT INTO Attaque VALUES (542, 'Pétalexplosion Éblouissante', '-', '1', '-', 'Plante', 'Physique', 'Le Pokémon utilise la Force Z pour libérer l’énergie des plantes et attaquer l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (543, 'Pétalexplosion Éblouissante', '-', '1', '-', 'Plante', 'Spéciale', 'Le Pokémon utilise la Force Z pour libérer l’énergie des plantes et attaquer l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (544, 'Photo-Geyser', '100', '100', '5', 'Psy', 'Spéciale', 'Le lanceur fait jaillir un pilier de lumière. Compare l''Attaque et l''Attaque Spéciale, et utilise celle qui infligera le plus de dégâts.');
INSERT INTO Attaque VALUES (545, 'Photocopie', '-', '-', '20', 'Normal', 'Statut', 'Utilise la dernière attaque qui a été réussie. Un Pokémon peut Photocopier ses propres attaques s''il a été le dernier Pokémon à réussir une attaque. Si la dernière attaque réussie était Photocopie, l''attaque échoue.');
INSERT INTO Attaque VALUES (546, 'Phytomax', '-', '10', '10', 'Plante', 'Physique', 'Une attaque de type Plante que seuls les Pokémon Dynamax peuvent utiliser. Crée un Champ Herbu qui dure cinq tours.');
INSERT INTO Attaque VALUES (547, 'Phytomixeur', '90', '65', '10', 'Plante', 'Spéciale', 'L''ennemi est pris dans un tourbillon de feuilles acérées. Peut aussi baisser sa Précision.');
INSERT INTO Attaque VALUES (548, 'Picanon', '100', '20', '15', 'Normal', 'Physique', 'L''attaque touche 2 à 5 fois pendant le tour.');
INSERT INTO Attaque VALUES (549, 'Pico-Défense', '-', '-', '10', 'Plante', 'Statut', 'Protège des attaques, et diminue les PV de tout attaquant qui entre en contact avec le lanceur.');
INSERT INTO Attaque VALUES (550, 'Picore', '100', '60', '20', 'Vol', 'Physique', 'Le lanceur picore la cible. Si cette dernière tient une Baie, le lanceur la mange et profite de ses effets.');
INSERT INTO Attaque VALUES (551, 'Picots', '-', '-', '20', 'Sol', 'Statut', 'Le lanceur disperse des piquants sur le sol pour blesser tout ennemi qui entre au combat.');
INSERT INTO Attaque VALUES (552, 'Picpic', '100', '35', '35', 'Vol', 'Physique', 'Frappe l''ennemi d''un bec pointu ou d''une corne pour infliger des dégâts.');
INSERT INTO Attaque VALUES (553, 'Pics Toxik', '-', '-', '20', 'poison', 'Statut', 'Lance des pics autour de l''ennemi. Ils empoisonnent les ennemis qui entrent au combat.');
INSERT INTO Attaque VALUES (554, 'Pied Brûleur', '90', '85', '10', 'Feu', 'Physique', 'Le lanceur envoie un coup de pied au taux de critiques élevé. Peut aussi brûler la cible.');
INSERT INTO Attaque VALUES (555, 'Pied Sauté', '95', '100', '10', 'Combat', 'Physique', 'Si Pied Saute rate ou si l''adversaire utilise Abri, le lanceur perd la moitié des dégâts qu''il aurait infligé s''il avait touché. Si l''attaque cible un Spectre, le lanceur perd la moitié des dégâts qu''il lui aurait infligé s''il n''était pas de type Spectre.');
INSERT INTO Attaque VALUES (556, 'Pied Voltige', '90', '130', '10', 'Combat', 'Physique', 'Si Pied Voltige rate ou si l''adversaire utilise Abri, le lanceur perd la moitié des dégâts qu''il aurait infligé s''il avait touché. Si l''attaque cible un Spectre, le lanceur perd la moitié des dégâts qu''il lui aurait infligé s''il n''était pas de type Spectre.');
INSERT INTO Attaque VALUES (557, 'Piège de Roc', '-', '-', '20', 'Roche', 'Statut', 'Lance des pierres flottantes autour de l''ennemi, qui blessent tout adversaire entrant au combat.');
INSERT INTO Attaque VALUES (558, 'Piège de Venin', '100', '-', '20', 'poison', 'Statut', 'Sécrète un liquide empoisonné. Diminue l''Attaque, l''Attaque Spéciale et la Vitesse de l''ennemi empoisonné.');
INSERT INTO Attaque VALUES (559, 'Piétisol', '100', '60', '20', 'Sol', 'Physique', 'Le lanceur piétine le sol et inflige des dégâts à tous les Pokémon autour de lui. Baisse aussi leur Vitesse.');
INSERT INTO Attaque VALUES (560, 'Pika-Fracas', '-', '1', '20', 'Electrik', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (561, 'Pika-Piqué', '95', '90', '15', 'Vol', 'Physique', 'Aucune description...');
INSERT INTO Attaque VALUES (562, 'Pika-Splash', '100', '90', '15', 'Eau', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (563, 'Pika-Sprint', '100', '80', '10', 'Electrik', 'Physique', 'Aucune description...');
INSERT INTO Attaque VALUES (564, 'Pikachute Foudroyante', '-', '210', '-', 'Electrik', 'Physique', 'Pikachu utilise la Force Z pour concentrer toute son électricité avant de se jeter à toute vitesse sur l’adversaire.');
INSERT INTO Attaque VALUES (565, 'Pilonnage', '85', '15', '20', 'Normal', 'Physique', 'Touche 2 à 5 fois en un tour. L''attque possède 37.5% de chance de toucher 2 ou 3 fois, et 12.5% de chance de toucher 4 ou 5 fois.');
INSERT INTO Attaque VALUES (566, 'Pince-Masse', '90', '100', '10', 'Eau', 'Physique', 'L''attaque possède un taux de coup critique élevé (+1).');
INSERT INTO Attaque VALUES (567, 'Piqué Supersonique', '-', '1', '-', 'Vol', 'Physique', 'Le Pokémon utilise la Force Z pour s’envoler très haut dans le ciel avant de plonger sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (568, 'Piqué Supersonique', '-', '1', '-', 'Vol', 'Spéciale', 'Le Pokémon utilise la Force Z pour s’envoler très haut dans le ciel avant de plonger sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (569, 'Piqué', '90', '140', '5', 'Vol', 'Physique', 'Le lanceur charge son attaque le premier tour et frappe au second. L''attaque a 30% de chances d''apeurer l''adversaire.');
INSERT INTO Attaque VALUES (570, 'Piqûre', '100', '60', '20', 'Insecte', 'Physique', 'Si la cible tient une Baie, le lanceur la prend et l''utilise immédiatemant. Si la cible tient une Baie Jacoba ou Pommo, la Baie n''a aucun effet mais est perdue. Si la cible tient une Baie Panga, elle s''active et réduit la puissance de Piqure avant qu''elle ne puisse être volée. Si un Pokémon sous Embargo utilise Piqure sur un Pokémon tenant une Baie, la Baie est détruite sans être activée.');
INSERT INTO Attaque VALUES (571, 'Pisto-Poing', '100', '40', '30', 'Acier', 'Physique', 'Attaque à priorité +1.');
INSERT INTO Attaque VALUES (572, 'Pistolet à O', '100', '40', '25', 'Eau', 'Spéciale', 'De l''eau est projetée avec force sur l''ennemi.');
INSERT INTO Attaque VALUES (573, 'Plaie Croix', '100', '80', '15', 'Insecte', 'Physique', 'Le lanceur taillade l''ennemi en utilisant ses faux ou ses griffes comme une paire de ciseaux.');
INSERT INTO Attaque VALUES (574, 'Plaquage', '100', '85', '15', 'Normal', 'Physique', 'Le lanceur se laisse tomber sur l''ennemi de tout son poids. Peut aussi le paralyser.');
INSERT INTO Attaque VALUES (575, 'Plasma Punch', '100', '100', '15', 'Electrik', 'Physique', 'Le lanceur attaque en projetant de l''électricité avec ses poings. Convertit les capacités de type Normal en type Électrik.');
INSERT INTO Attaque VALUES (576, 'Plénitude', '-', '-', '20', 'Psy', 'Statut', 'Le lanceur se concentre et fait le vide dans son esprit pour augmenter son Attaque Spéciale et sa Défense Spéciale.');
INSERT INTO Attaque VALUES (577, 'Plongée', '100', '80', '10', 'Eau', 'Physique', 'Le lanceur plonge sous l''eau au premier tour et frappe au second.');
INSERT INTO Attaque VALUES (578, 'Plumo-Queue', '85', '25', '10', 'Normal', 'Physique', 'Le lanceur frappe l''ennemi de deux à cinq fois d''affilée avec sa queue robuste.');
INSERT INTO Attaque VALUES (579, 'Poing Boost', '100', '40', '20', 'Combat', 'Physique', 'À force de frapper, les poings deviennent plus durs. Augmente l''Attaque du lanceur si l''ennemi est touché.');
INSERT INTO Attaque VALUES (580, 'Poing Comète', '85', '18', '15', 'Normal', 'Physique', 'Touche 2 à 5 fois en un tour. Il y a 37.5% de chance de toucher 2 ou 3 fois, et 12.5% de chance de toucher 4 ou 5 fois.');
INSERT INTO Attaque VALUES (581, 'Poing Dard', '100', '60', '15', 'Plante', 'Physique', 'L''attaque a 30% de chance d''apeurer l''ennemi.');
INSERT INTO Attaque VALUES (582, 'Poing Éclair', '100', '75', '15', 'Electrik', 'Physique', 'Un coup de poing électrique vient frapper l''ennemi. Peut le paralyser.');
INSERT INTO Attaque VALUES (583, 'Poing Feu', '100', '75', '15', 'Feu', 'Physique', 'Un coup de poing enflammé vient frapper l''ennemi. Peut le brûler.');
INSERT INTO Attaque VALUES (584, 'Poing Karaté', '100', '50', '25', 'Combat', 'Physique', 'Une attaque tranchante à taux de critiques élevé (+1).');
INSERT INTO Attaque VALUES (585, 'Poing Météore', '90', '90', '10', 'Acier', 'Physique', 'L''attaque a 20% de chance d''augmenter l''Attaque du lanceur d''un niveau.');
INSERT INTO Attaque VALUES (586, 'Poing Obscur', '100', '80', '5', 'Ténèbres', 'Physique', 'Ignore les effets de la cible et inflige un coup critique à coup sûr.');
INSERT INTO Attaque VALUES (587, 'Poing Ombre', '-', '60', '20', 'Spectre', 'Physique', 'L''attaque ignore les changements d''esquive et de précision.');
INSERT INTO Attaque VALUES (588, 'Poing Glace', '100', '75', '15', 'Glace', 'Physique', 'Un coup de poing glacé vient frapper l''ennemi. Peut le geler.');
INSERT INTO Attaque VALUES (589, 'Poison Croix', '100', '70', '20', 'poison', 'Physique', 'Un coup tranchant qui peut empoisonner l''ennemi. Taux de critiques élevé.');
INSERT INTO Attaque VALUES (590, 'Poliroche', '-', '-', '20', 'Roche', 'Statut', 'Le lanceur polit son corps pour diminuer sa résistance au vent. Augmente beaucoup la Vitesse.');
INSERT INTO Attaque VALUES (591, 'Possessif', '-', '-', '10', 'Psy', 'Statut', 'Si l''ennemi et le lanceur ont des capacités en commun, l''ennemi ne pourra pas les utiliser.');
INSERT INTO Attaque VALUES (592, 'Poudre Dodo', '75', '-', '15', 'Plante', 'Statut', 'L''adversaire est endormi.');
INSERT INTO Attaque VALUES (593, 'Poudre Magique', '100', '-', '20', 'Psy', 'Statut', 'Le lanceur recouvre sa cible d''une poudre magique qui change son type en Psy.');
INSERT INTO Attaque VALUES (594, 'Poudre Toxik', '75', '-', '35', 'poison', 'Statut', 'Une poudre toxique empoisonne l''ennemi.');
INSERT INTO Attaque VALUES (595, 'Poudre Fureur', '-', '-', '20', 'Insecte', 'Statut', 'Le lanceur s''asperge d''une poudre irritante pour attirer l''attention. Il attire toutes les attaques ennemies.');
INSERT INTO Attaque VALUES (596, 'Poudreuse', '100', '40', '25', 'Glace', 'Spéciale', 'L''attaque a 10% de chance de geler l''adversaire.');
INSERT INTO Attaque VALUES (597, 'Poursuite', '100', '40', '20', 'Ténèbres', 'Physique', 'Lorsqu''un Pokémon est retiré du terrain, en switchant ou en utilisant Demi-Tour, si un ennemi utilise Poursuite et peut attaquer, alors il attaque le Pokémon se retirant avec une puissance doublée. Sinon, le lanceur utilise Poursuite normalement, lors de son tour.');
INSERT INTO Attaque VALUES (598, 'Pouvoir Antique', '100', '60', '5', 'Roche', 'Spéciale', 'L''attaque a 10% de chance d''augmenter l''Attaque, la Défense, l''Attaque Spéciale, la Défense Spéciale et la Vitesse du lanceur.');
INSERT INTO Attaque VALUES (599, 'Pouvoir Lunaire', '100', '95', '15', 'Fée', 'Spéciale', 'Attaque l''ennemi grâce au pouvoir de la lune. Peut diminuer son Attaque Spéciale.');
INSERT INTO Attaque VALUES (600, 'Prescience', '100', '120', '10', 'Psy', 'Spéciale', 'De l''énergie psychique vient frapper l''ennemi deux tours après l''utilisation de cette capacité.');
INSERT INTO Attaque VALUES (601, 'Presse', '100', '1', '5', 'Normal', 'Physique', 'La formule pour la puissance de Presse est la suivante : 1 + floor(120 * PV cible/ PV Max cible).');
INSERT INTO Attaque VALUES (602, 'Prévention', '-', '-', '15', 'Combat', 'Statut', 'Le lanceur et son équipe sont protégés contre les attaques prioritaires.');
INSERT INTO Attaque VALUES (603, 'Prise de Bec', '100', '85', '10', 'Electrik', 'Physique', 'Le lanceur transperce sa cible avec son bec chargé d''électricité. Si cette attaque est lancée avant que la cible n''attaque, elle infligera deux fois plus de dégâts.');
INSERT INTO Attaque VALUES (604, 'Projecteur', '-', '-', '15', 'Normal', 'Statut', 'Met un Pokémon sous le feu des projecteurs et force tout le monde à le viser.');
INSERT INTO Attaque VALUES (605, 'Projection', '90', '60', '10', 'Combat', 'Physique', 'Projette le Pokémon ennemi et le remplace par un autre. Lors d''un combat contre un Pokémon sauvage seul, met fin au combat.');
INSERT INTO Attaque VALUES (606, 'Protection', '-', '-', '20', 'Psy', 'Statut', 'Crée un fabuleux mur de lumière qui réduit les dégâts causés par les capacités physiques pendant cinq tours.');
INSERT INTO Attaque VALUES (607, 'Provoc', '100', '-', '20', 'Ténèbres', 'Statut', 'Provoque l''ennemi. L''oblige à n''utiliser que des attaques pendant trois tours.');
INSERT INTO Attaque VALUES (608, 'Psycho-Boost', '90', '140', '5', 'Psy', 'Spéciale', 'L''attaque diminue l''Attaque Spéciale du lanceur de deux niveaux.');
INSERT INTO Attaque VALUES (609, 'Psycho-Croc', '100', '85', '10', 'Psy', 'Physique', 'Le lanceur mord la cible avec ses pouvoirs psychiques. Brise aussi les barrières comme Mur Lumière et Protection.');
INSERT INTO Attaque VALUES (610, 'Psycho-Pulvérisation EX', '-', '1', '-', 'Psy', 'Physique', 'Le Pokémon utilise la Force Z pour manipuler l’adversaire et lui infliger de sérieux dégâts. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (611, 'Psycho-Pulvérisation EX', '-', '1', '-', 'Psy', 'Spéciale', 'Le Pokémon utilise la Force Z pour manipuler l’adversaire et lui infliger de sérieux dégâts. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (612, 'Psychomax', '-', '10', '10', 'Psy', 'Physique', 'Une attaque de type Psy que seuls les Pokémon Dynamax peuvent utiliser. Crée un Champ Psychique qui dure cinq tours.');
INSERT INTO Attaque VALUES (613, 'Psyko', '100', '90', '10', 'Psy', 'Spéciale', 'Une puissante force télékinétique frappe l''ennemi. Peut aussi faire baisser sa Défense Spéciale.');
INSERT INTO Attaque VALUES (614, 'Psykoud''Boul', '90', '80', '15', 'Psy', 'Physique', 'Le lanceur concentre sa volonté et donne un coup de tête. Peut aussi apeurer l''ennemi.');
INSERT INTO Attaque VALUES (615, 'Pugilomax', '-', '10', '10', 'Combat', 'Physique', 'Une attaque de type Combat que seuls les Pokémon Dynamax peuvent utiliser. Augmente l''Attaque des alliés.');
INSERT INTO Attaque VALUES (616, 'Puissance Cachée', '100', '60', '15', 'Normal', 'Spéciale', 'L''efficacité de l''attaque dépend du lanceur.');
INSERT INTO Attaque VALUES (617, 'Puissance', '-', '-', '30', 'Normal', 'Statut', 'Le lanceur prend une profonde inspiration et se concentre pour augmenter son taux de critiques.');
INSERT INTO Attaque VALUES (618, 'Punition', '100', '1', '5', 'Ténèbres', 'Physique', 'La puissance de l''attaque est déterminée par la formule suivante : Puissance = 60 + (20 *nombre de boost de la cible), avec un maximum de 200. Les baisses de statistiques n''influencent pas la puissance de l''attaque.');
INSERT INTO Attaque VALUES (619, 'Purédpois', '70', '30', '20', 'poison', 'Spéciale', 'L''attaque a 40% de chances d''empoisonner l''adversaire.');
INSERT INTO Attaque VALUES (620, 'Purification', '-', '-', '20', 'poison', 'Statut', 'Le lanceur soigne les altérations de statut de la cible, ce qui lui permet de regagner des PV.');
INSERT INTO Attaque VALUES (621, 'Pyro-Explosion Cataclysmique', '-', '1', '-', 'Feu', 'Physique', 'Le Pokémon utilise la Force Z pour cracher une boule de feu qui réduit l’adversaire en cendres. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (622, 'Pyro-Explosion Cataclysmique', '-', '1', '-', 'Feu', 'Spéciale', 'Le Pokémon utilise la Force Z pour cracher une boule de feu qui réduit l’adversaire en cendres. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (623, 'Pyromax', '-', '100', '10', 'Feu', 'Physique', 'Une attaque de type Feu que seuls les Pokémon Dynamax peuvent utiliser. Fait briller le soleil pendant cinq tours.');
INSERT INTO Attaque VALUES (624, 'Queue de Fer', '75', '100', '15', 'Acier', 'Physique', 'Attaque l''ennemi avec une queue de fer. Peut aussi baisser sa Défense.');
INSERT INTO Attaque VALUES (625, 'Queue-Poison', '100', '50', '25', 'poison', 'Physique', 'L''attaque a 10% de chance d''empoisonner la cible. Taux de critiques élevé (+1).');
INSERT INTO Attaque VALUES (626, 'Racines', '-', '-', '20', 'Plante', 'Statut', 'Le lanceur regagne 1/16 de ses PV Max à la fin de chaque tour et ne peut plus switcher sauf via Demi-Tour ou Relais. Cyclone et Hurlement n''ont plus d''effet sur le lanceur. Racines annule l''immunité Sol de Pokémon Vol ou possédant Lévitation.');
INSERT INTO Attaque VALUES (627, 'Rafale Écailles', '90', '25', '20', 'Dragon', 'Physique', 'Attaque entre 2 et 5 fois, augmente d''un cran la Vitesse du lanceur et diminue d''un cran la Défense du lanceur.');
INSERT INTO Attaque VALUES (628, 'Rafale Feu', '90', '150', '5', 'Feu', 'Spéciale', 'Le lanceur ne peut attaquer ou switcher au tour suivant.');
INSERT INTO Attaque VALUES (629, 'Rafale Psy', '100', '65', '20', 'Psy', 'Spéciale', 'L''attaque a 10% de chance de rendre confus l''adversaire.');
INSERT INTO Attaque VALUES (630, 'Râle Mâle', '100', '-', '30', 'Normal', 'Statut', 'Le lanceur pousse un rugissement qui intimide l''ennemi et diminue son Attaque et son Attaque Spéciale.');
INSERT INTO Attaque VALUES (631, 'Rancune', '-', '-', '5', 'Spectre', 'Statut', 'Si le lanceur est mis KO avant sa prochaine attaque, l''adversaire perd tous les PP de l''attaque avec laquelle il a mis KO le lanceur. Ne prend pas en compte les dégâts de fin de tour comme les dégâts de climat ou Vampigraine.');
INSERT INTO Attaque VALUES (632, 'Rapace', '100', '120', '15', 'Vol', 'Physique', 'Le lanceur replie ses ailes et charge en rase-mottes. Blesse gravement le lanceur.');
INSERT INTO Attaque VALUES (633, 'Ravage Rampant', '90', '70', '10', 'Insecte', 'Physique', 'Baisse d''un cran l''Attaque Spéciale de la cible.');
INSERT INTO Attaque VALUES (634, 'Rayon Chargé', '90', '50', '10', 'Electrik', 'Spéciale', 'Le lanceur tire un rayon chargé d''électricité. Peut aussi augmenter son Attaque Spéciale.');
INSERT INTO Attaque VALUES (635, 'Rayon Gemme', '100', '80', '20', 'Roche', 'Spéciale', 'Le lanceur attaque avec un rayon de lumière qui scintille comme s''il était composé de gemmes.');
INSERT INTO Attaque VALUES (636, 'Rayon Lune', '-', '-', '5', 'Fée', 'Statut', 'L''attaque soigne 1/2 de la vie du lanceur s''il n''y a pas de climat, 2/3 sous le Soleil, 1/4 sous tout autre climat.');
INSERT INTO Attaque VALUES (637, 'Rayon Signal', '100', '75', '15', 'Insecte', 'Spéciale', 'L''attaque a 10% de chances de rendre l''adversaire confus.');
INSERT INTO Attaque VALUES (638, 'Rayon Simple', '100', '-', '15', 'Normal', 'Statut', 'Le lanceur envoie des ondes mystérieuses à l''ennemi. Son talent devient Simple.');
INSERT INTO Attaque VALUES (639, 'Rayon Spectral', '100', '100', '5', 'Spectre', 'Spéciale', 'Le lanceur attaque avec un rayon de lumière mystérieux. Ignore le talent de la cible.');
INSERT INTO Attaque VALUES (640, 'Rayons Séléno-Explosifs', '-', '200', '-', 'Spectre', 'Spéciale', 'Aucune description...');
INSERT INTO Attaque VALUES (641, 'Rebond', '85', '85', '5', 'Vol', 'Physique', 'Le lanceur bondit très haut et plonge sur l''ennemi au second tour. Peut aussi le paralyser.');
INSERT INTO Attaque VALUES (642, 'Rebondifeu', '100', '70', '15', 'Feu', 'Spéciale', 'Quand l''attaque atteint sa cible, elle projette des flammes qui touchent tout ennemi situé à côté.');
INSERT INTO Attaque VALUES (643, 'Recyclage', '-', '-', '10', 'Normal', 'Statut', 'Recycle un objet tenu à usage unique déjà utilisé lors du combat pour pouvoir l''utiliser à nouveau.');
INSERT INTO Attaque VALUES (644, 'Reflet Magik', '-', '-', '15', 'Psy', 'Statut', 'Toutes les attaques suivantes n''affectent pas le lanceur de Reflet Magik mais sont renvoyées à leur lanceur.');
INSERT INTO Attaque VALUES (645, 'Reflet', '-', '-', '15', 'Normal', 'Statut', 'Le lanceur se déplace si vite qu''il crée des copies illusoires de lui-même, augmentant son Esquive.');
INSERT INTO Attaque VALUES (646, 'Regard Noir', '-', '-', '5', 'Normal', 'Statut', 'La cible ne peut plus switcher. L''effet est perdu lorsque le lanceur switche. Regard Noir ignore les changements d''esquive et de précision. Elle n''empêche pas le switch par utilisation de Relais, Demi-Tour ou de l''objet Carapace Mue.');
INSERT INTO Attaque VALUES (647, 'Regard Touchant', '100', '-', '30', 'Fée', 'Statut', 'Fixe l''ennemi d''un air très attendrissant qui le touche et diminue son Attaque. Agit en priorité.');
INSERT INTO Attaque VALUES (648, 'Régénération', '-', '-', '20', 'Normal', 'Statut', 'Soigne un empoisonnement, une paralysie, une brûlure.');
INSERT INTO Attaque VALUES (649, 'Relâche', '100', '1', '10', 'Normal', 'Spéciale', 'L''attaque a une puissance de 100 après un Stockage, de 200 après deux et de 300 après trois Stockages. Après l''utilisation de Relâche, les Stockages sont perdus. Relâche inflige');
INSERT INTO Attaque VALUES (650, 'Relais', '-', '-', '40', 'Normal', 'Statut', 'Le lanceur échange sa place et tout changement de stat avec un Pokémon de l''équipe.');
INSERT INTO Attaque VALUES (651, 'Rengorgement', '-', '-', '30', 'Normal', 'Statut', 'Le lanceur se rengorge. Augmente l''Attaque et l''Attaque Spéciale.');
INSERT INTO Attaque VALUES (652, 'Renversement', '-', '-', '20', 'Ténèbres', 'Statut', 'Inverse tous les changements de stats de la cible.');
INSERT INTO Attaque VALUES (653, 'Repli', '-', '-', '40', 'Eau', 'Statut', 'La défense du lanceur est montée d''un cran.');
INSERT INTO Attaque VALUES (654, 'Repos', '-', '-', '10', 'Psy', 'Statut', 'Le lanceur regagne tous ses PV et soigne ses altérations de statut, puis il dort pendant deux tours.');
INSERT INTO Attaque VALUES (655, 'Représailles', '100', '50', '10', 'Ténèbres', 'Physique', 'Le lanceur charge son énergie, puis attaque. La puissance est doublée si le lanceur agit après l''ennemi.');
INSERT INTO Attaque VALUES (656, 'Requiem', '-', '-', '5', 'Normal', 'Statut', 'Tous les Pokémon sur le terrain sont mis KO après 3 tours. Même les Pokémon invulnérables car en chargement de Plongée, Rebond, Revenant, Tunnel ou Vol sont affectés. Un Pokémon avec la capacité spéciale Anti-Bruit n''est pas affecté par Requiem.');
INSERT INTO Attaque VALUES (657, 'Retenue', '100', '40', '40', 'Normal', 'Physique', 'Le lanceur attaque avec retenue, et laisse au moins 1 PV à l''ennemi.');
INSERT INTO Attaque VALUES (658, 'Retour', '100', '1', '20', 'Normal', 'Physique', 'Attaque dont la puissance dépend de l''amitié.');
INSERT INTO Attaque VALUES (659, 'Réveil Forcé', '100', '70', '10', 'Combat', 'Physique', 'La puissance double si la cible est endormie. Le Pokémon est alors réveillé.');
INSERT INTO Attaque VALUES (660, 'Revenant', '100', '120', '5', 'Spectre', 'Physique', 'Le lanceur devient intouchable au premier tour, puis frappe au second. L''attaque passe à travers Abri et Détection.');
INSERT INTO Attaque VALUES (661, 'Riposte', '100', '1', '20', 'Combat', 'Physique', 'Si le lanceur a été touché par une attaque physique durant le tour, Riposte inflige le double de dégâts au lanceur de cette attaque. N''affecte pas les Spectre.');
INSERT INTO Attaque VALUES (662, 'Roc-Boulet', '90', '150', '5', 'Roche', 'Physique', 'Le lanceur attaque en projetant un gros rocher sur l''ennemi. Il doit se reposer au tour suivant.');
INSERT INTO Attaque VALUES (663, 'Ronflement', '100', '50', '15', 'Normal', 'Spéciale', 'Une attaque qui ne fonctionne que si le lanceur est endormi. Le boucan peut aussi apeurer l''ennemi.');
INSERT INTO Attaque VALUES (664, 'Roue de Feu', '100', '60', '25', 'Feu', 'Physique', 'L''attaque possède 10% de chance de brûler la cible. Si le lanceur est gelé, il est automatiquement dégelé quand il lance l''attaque.');
INSERT INTO Attaque VALUES (665, 'Roue Libre', '100', '110', '10', 'Electrik', 'Physique', 'Morpeko libère l''énergie stockée dans ses joues pour attaquer et augmenter sa Vitesse. Le type de cette capacité change en fonction de la forme de Morpeko.');
INSERT INTO Attaque VALUES (666, 'Roulade', '90', '30', '20', 'Roche', 'Physique', 'La puissance double à chaque coup consécutif. L''attaque bloque l''utilisateur pour 5 tours, mais l''effet s''arrête si l''une des attaque rate ou si le lanceur ne peut pas relancer Roulade. Si le lanceur choisit à nouveau Roulade après la fin de l''effet, la puissance est réinitialisée. Utiliser Boul''Armure avant Roulade double la puissance de base de Roulade tant que le lanceur ne switche pas.');
INSERT INTO Attaque VALUES (667, 'Rugissement', '100', '-', '40', 'Normal', 'Statut', 'Le lanceur pousse un cri tout mimi pour tromper la vigilance de l''ennemi et baisser son Attaque.');
INSERT INTO Attaque VALUES (668, 'Rune Protect', '-', '-', '25', 'Normal', 'Statut', 'Crée un champ protecteur qui empêche toutes les altérations de statut pendant cinq tours.');
INSERT INTO Attaque VALUES (669, 'Ruse', '100', '30', '10', 'Normal', 'Physique', 'Cette attaque ne fonctionne que si la cible utilise Abri ou Détection. Si c''est le cas, cette attaque inflige des dégâts normaux et désactive l''effet d''Abri ou Détection jusqu''à la fin du tour. Cependant, la cible verra la probabilité de réussir Abri ou Détection au prochain tour divisée par deux comme si l''attaque avait réussi.');
INSERT INTO Attaque VALUES (670, 'Sable Ardent', '100', '70', '10', 'Sol', 'Spéciale', 'Inflige des dégâts et peut brûler la cible.');
INSERT INTO Attaque VALUES (671, 'Sabotage', '100', '65', '20', 'Ténèbres', 'Physique', 'Fait perdre à la cible son objet tenu. N''a aucun effet sur les Pokémon ayant la capacité spéciale Glue ou Multitype.');
INSERT INTO Attaque VALUES (672, 'Sacrifice', '80', '80', '20', 'Combat', 'Physique', 'Le lanceur s''inflige 1/4 des dégâts infligés à la cible, arrondis à l''inférieur.');
INSERT INTO Attaque VALUES (673, 'Saisie', '-', '-', '10', 'Ténèbres', 'Statut', 'Vole les effets de la prochaine attaque ennemie.');
INSERT INTO Attaque VALUES (674, 'Sanction Suprême', '100', '100', '10', 'Dragon', 'Spéciale', 'La cible subit des dégâts et, si elle a déjà agi à ce tour, elle perd aussi son talent.');
INSERT INTO Attaque VALUES (675, 'Saumure', '100', '65', '10', 'Eau', 'Spéciale', 'Cette attaque est deux fois plus puissante lorsque l''ennemi a moins de la moitié de ses PV.');
INSERT INTO Attaque VALUES (676, 'Sécrétion', '95', '-', '40', 'Insecte', 'Statut', 'L''attaque baisse la vitesse de l''adversaire d''un cran.');
INSERT INTO Attaque VALUES (677, 'Séduction', '100', '-', '20', 'Normal', 'Statut', 'Diminue l''attaque spéciale du Pokémon ennemi de 2 niveaux s''il est de sexe opposé.');
INSERT INTO Attaque VALUES (678, 'Séisme', '100', '100', '10', 'Sol', 'Physique', 'Le lanceur provoque un tremblement de terre touchant tous les Pokémon autour de lui.');
INSERT INTO Attaque VALUES (679, 'Selve Salvatrice', '-', '-', '10', 'Plante', 'Statut', 'Restaure 25% des PV max du Pokémon et son allié, et retire les problèmes de statut.');
INSERT INTO Attaque VALUES (680, 'Sheauriken', '100', '15', '20', 'Eau', 'Spéciale', 'Attaque l''ennemi avec des shuriken de mucus. Frappe en priorité deux à cinq fois d''affilée en un tour.');
INSERT INTO Attaque VALUES (681, 'Siffl''Herbe', '55', '-', '15', 'Plante', 'Statut', 'Endort l''ennemi en sifflant une douce mélodie.');
INSERT INTO Attaque VALUES (682, 'Sinistromax', '-', '10', '10', 'Ténèbres', 'Physique', 'Une attaque de type Ténèbres que seuls les Pokémon Dynamax peuvent utiliser. Baisse la Défense Spéciale de la cible.');
INSERT INTO Attaque VALUES (683, 'Siphon', '85', '35', '15', 'Eau', 'Spéciale', 'Piège l''ennemi dans une trombe d''eau pendant quatre à cinq tours.');
INSERT INTO Attaque VALUES (684, 'Sismomax', '-', '10', '10', 'Sol', 'Physique', 'Une attaque de type Sol que seuls les Pokémon Dynamax peuvent utiliser. Augmente la Défense Spéciale des alliés.');
INSERT INTO Attaque VALUES (685, 'Soin Floral', '-', '-', '10', 'Fée', 'Statut', 'Rend la moitié de ses PV max à la cible. Plus efficace sur un Champ Herbu.');
INSERT INTO Attaque VALUES (686, 'Soin', '-', '-', '10', 'Normal', 'Statut', 'Un soin qui permet au lanceur de récupérer jusqu''à la moitié de ses PV max.');
INSERT INTO Attaque VALUES (687, 'Sommation', '-', '-', '15', 'Psy', 'Statut', 'Force la cible à lancer immédiatement la dernière capacité qu''elle a utilisée.');
INSERT INTO Attaque VALUES (688, 'Sonic Boom', '90', '1', '20', 'Normal', 'Spéciale', 'L''attaque inflige toujours 20 de dommages. Les Spectres y sont immunisés.');
INSERT INTO Attaque VALUES (689, 'Soucigraine', '100', '-', '10', 'Plante', 'Statut', 'Soucigraine change la capacité spéciale de la cible pour Insomnia. L''attaque échoue si la capacité de la cible est Multi-type ou Absentéisme.');
INSERT INTO Attaque VALUES (690, 'Souffle Glacé', '90', '60', '10', 'Glace', 'Spéciale', 'Un souffle froid blesse l''ennemi. L''effet est toujours critique.');
INSERT INTO Attaque VALUES (691, 'Souplesse', '75', '80', '20', 'Normal', 'Physique', 'Fouette l''ennemi avec la queue ou une liane, par exemple, pour infliger des dégâts.');
INSERT INTO Attaque VALUES (692, 'Souvenir', '100', '-', '10', 'Ténèbres', 'Statut', 'Met le lanceur KO et diminue de deux niveaux l''Attaque et l''Attaque Spéciale de la cible.');
INSERT INTO Attaque VALUES (693, 'Spatio-Rift', '95', '100', '5', 'Dragon', 'Spéciale', 'Le taux de coups critiques est un cran au-dessus de la normale.');
INSERT INTO Attaque VALUES (694, 'Spectromax', '-', '10', '10', 'Spectre', 'Physique', 'Une attaque de type Spectre que seuls les Pokémon Dynamax peuvent utiliser. Baisse la Défense de la cible.');
INSERT INTO Attaque VALUES (695, 'Spore Coton', '100', '-', '40', 'Plante', 'Statut', 'Baisse la Vitesse de la cible de deux niveaux.');
INSERT INTO Attaque VALUES (696, 'Spore', '100', '-', '15', 'Plante', 'Statut', 'L''adversaire est endormi.');
INSERT INTO Attaque VALUES (697, 'Stalactite', '100', '25', '30', 'Glace', 'Physique', 'Le lanceur jette des pics de glace sur l''ennemi, de deux à cinq fois de suite.');
INSERT INTO Attaque VALUES (698, 'Stimulant', '100', '70', '10', 'Normal', 'Physique', 'L''attaque double sa puissance si la cible est paralysée, mais soigne également la cible de sa paralysie.');
INSERT INTO Attaque VALUES (699, 'Stockage', '-', '-', '20', 'Normal', 'Statut', 'La défense et la défense spéciale du lanceur sont montées d''un cran, et l''attaque permet au lanceur d''utiliser Avale et Relâche. Stockage ne peut être lancée que 3 fois. Les boosts de stats sont perdus après l''utilisation d''Avale ou Relâche. Les Stockages sont perdus si le lanceur switches. Si le Pokémon utilise Relais, les Stockages ne sont pas relayés, mais les boosts de stats le sont. Si d''autres boosts de stats ont été utilisés en plus de Stockage, ceux-ci ne sont pas perdus lors de l''utilisation d''Avale ou Relâche.');
INSERT INTO Attaque VALUES (700, 'Stratopercut', '90', '85', '15', 'Combat', 'Physique', 'Le Pokémon frappe les ennemis dans la phase de chargement de Vol et Rebond avec une puissance et une précision normale.');
INSERT INTO Attaque VALUES (701, 'Strido-Son', '85', '-', '40', 'Acier', 'Statut', 'Un cri horrible tel un crissement métallique qui réduit beaucoup la Défense Spéciale de l''ennemi.');
INSERT INTO Attaque VALUES (702, 'Suc Digestif', '100', '-', '10', 'poison', 'Statut', 'Le lanceur répand ses sucs digestifs sur l''ennemi. Le fluide neutralise le talent de l''ennemi.');
INSERT INTO Attaque VALUES (703, 'Super Tourbillon Abyssal', '-', '1', '-', 'Eau', 'Physique', 'Le Pokémon utilise la Force Z pour créer un tourbillon gigantesque qui avale l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (704, 'Super Tourbillon Abyssal', '-', '1', '-', 'Eau', 'Spéciale', 'Le Pokémon utilise la Force Z pour créer un tourbillon gigantesque qui avale l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (705, 'Supernova Originelle', '-', '185', '-', 'Psy', 'Spéciale', 'Mew utilise la Force Z pour attaquer la cible. Le terrain devient un Champ Psychique.');
INSERT INTO Attaque VALUES (706, 'Surchauffe', '90', '130', '5', 'Feu', 'Spéciale', 'Attaque l''ennemi à pleine puissance. Le contrecoup baisse beaucoup l''Attaque Spéciale du lanceur.');
INSERT INTO Attaque VALUES (707, 'Surf', '100', '90', '15', 'Eau', 'Spéciale', 'Une énorme vague s''abat sur le champ de bataille et inflige des dégâts à tous les Pokémon autour du lanceur.');
INSERT INTO Attaque VALUES (708, 'Surpuissance', '100', '120', '5', 'Combat', 'Physique', 'Une attaque puissante, mais qui baisse l''Attaque et la Défense du lanceur.');
INSERT INTO Attaque VALUES (709, 'Survinsecte', '100', '50', '20', 'Insecte', 'Spéciale', 'Le lanceur se débat de toutes ses forces, et baisse l''Attaque Spéciale de l''ennemi.');
INSERT INTO Attaque VALUES (710, 'Symphonie des Ondines', '-', '195', '-', 'Eau', 'Spéciale', 'Oratoria utilise la Force Z pour rassembler une grande quantité d’eau et la projeter sur la cible à pleine puissance.');
INSERT INTO Attaque VALUES (711, 'Synchropeine', '100', '120', '10', 'Psy', 'Spéciale', 'Des ondes mystérieuses blessent tous les Pokémon alentour qui sont du même type que le lanceur.');
INSERT INTO Attaque VALUES (712, 'Synthèse', '-', '-', '5', 'Plante', 'Statut', 'L''attaque soigne 1/2 de la vie du lanceur s''il n''y a pas de climat, 2/3 sous le Soleil, 1/4 sous tout autre climat.');
INSERT INTO Attaque VALUES (713, 'Tacle Feu', '100', '1', '10', 'Feu', 'Physique', 'Le lanceur projette son corps enflammé contre l''ennemi. S''il est plus lourd que l''ennemi, l''effet augmente en conséquence.');
INSERT INTO Attaque VALUES (714, 'Tacle Lourd', '100', '1', '10', 'Acier', 'Physique', 'Le lanceur se jette sur l''ennemi de tout son poids. S''il est plus lourd que l''ennemi, l''effet augmente en conséquence.');
INSERT INTO Attaque VALUES (715, 'Taillade', '95', '40', '20', 'Insecte', 'Physique', 'La puissance de l''attaque est doublée à chaque fois qu''elle touche. Si Taillade rate, la puissance revient à 10. Les attaques n''ont pas besoin d''être consécutives pour voir leur puissance augmentée. Le maximum de puissance est de 160.');
INSERT INTO Attaque VALUES (716, 'Tambour Battant', '100', '80', '10', 'Plante', 'Physique', 'Le lanceur bat son tambour pour en diriger les racines sur la cible, l''attaquer, et baisser sa Vitesse.');
INSERT INTO Attaque VALUES (717, 'Tapotige', '100', '40', '40', 'Plante', 'Physique', 'Le lanceur attaque sa cible en la piquant avec une branche pointue.');
INSERT INTO Attaque VALUES (718, 'Tatamigaeshi', '-', '-', '10', 'Combat', 'Statut', 'Retourne un tatami pour bloquer, comme avec un bouclier, les capacités visant le lanceur ou ses alliés. N''a pas d''effet sur les attaques de statut.');
INSERT INTO Attaque VALUES (719, 'Techno-Buster', '100', '120', '5', 'Normal', 'Spéciale', 'Le lanceur projette un rayon lumineux sur l''ennemi. Le type varie selon le Module que tient le lanceur.');
INSERT INTO Attaque VALUES (720, 'Télékinésie', '80', '-', '15', 'Psy', 'Statut', 'Diminue la précision de l''adversaire d''un niveau.');
INSERT INTO Attaque VALUES (721, 'Téléport', '-', '-', '20', 'Psy', 'Statut', 'Permet de changer de place avec un autre Pokémon de l''équipe s''il y en a. Quand cette capacité est utilisée par un Pokémon sauvage, celui-ci fuit le combat.');
INSERT INTO Attaque VALUES (722, 'Telluriforce', '100', '90', '10', 'Sol', 'Spéciale', 'De terribles séismes secouent l''ennemi. Peut aussi baisser sa Défense Spéciale.');
INSERT INTO Attaque VALUES (723, 'Tempête Florale', '100', '90', '15', 'Plante', 'Physique', 'Déclenche une violente tempête de fleurs qui inflige des dégâts à tous les Pokémon alentour.');
INSERT INTO Attaque VALUES (724, 'Tempête de Sable', '-', '-', '10', 'Roche', 'Statut', 'Une tempête de sable blesse tous les Pokémon pendant cinq tours, sauf ceux de type Roche, Sol ou Acier. Augmente la Défense Spéciale des Pokémon Roche.');
INSERT INTO Attaque VALUES (725, 'Tempête Verte', '90', '130', '5', 'Plante', 'Spéciale', 'Invoque une tempête de feuilles acérées. Le contrecoup réduit beaucoup l''Attaque Spéciale du lanceur.');
INSERT INTO Attaque VALUES (726, 'Ten-Danse', '100', '-', '15', 'Normal', 'Statut', 'Le lanceur danse sur un rythme étrange. Il force sa cible à l''imiter, ce qui lui fait adopter son talent.');
INSERT INTO Attaque VALUES (727, 'Ténacité', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur résiste aux attaques avec 1 PV. Peut échouer si utilisée plusieurs fois de suite.');
INSERT INTO Attaque VALUES (728, 'Tête de Fer', '100', '80', '15', 'Acier', 'Physique', 'Le lanceur heurte l''ennemi avec sa tête dure comme de l''acier. Peut aussi l''apeurer.');
INSERT INTO Attaque VALUES (729, 'Thérémonie', '-', '-', '10', 'Normal', 'Statut', 'Le lanceur invite tous les Pokémon sur le terrain à prendre le goûter autour d''une tasse de thé. Ceux qui tiennent une Baie la mangent.');
INSERT INTO Attaque VALUES (730, 'Tir de Boue', '95', '55', '15', 'Sol', 'Spéciale', 'Le lanceur attaque en projetant de la boue sur l''ennemi. Réduit aussi la Vitesse de la cible.');
INSERT INTO Attaque VALUES (731, 'Tir de Précision', '100', '80', '15', 'Eau', 'Spéciale', 'Le lanceur parvient toujours à viser la cible voulue, en ignorant l''effet des talents et des capacités capables de détourner les attaques.');
INSERT INTO Attaque VALUES (732, 'Tisse Ombre', '100', '80', '10', 'Spectre', 'Physique', 'Une attaque qui coud l''ennemi à son ombre, ce qui l''empêche de s''enfuir.');
INSERT INTO Attaque VALUES (733, 'Toile Élek', '95', '55', '15', 'Electrik', 'Spéciale', 'Attrape l''ennemi dans un filet électrique. Baisse aussi la Vitesse de l''ennemi.');
INSERT INTO Attaque VALUES (734, 'Toile Gluante', '-', '-', '20', 'Insecte', 'Statut', 'Déploie une toile visqueuse autour de l''ennemi qui ralentit la Vitesse de tout adversaire entrant au combat.');
INSERT INTO Attaque VALUES (735, 'Toile', '-', '-', '10', 'Insecte', 'Statut', 'La cible ne peut plus switcher. L''effet est perdu lorsque le lanceur switche. Toile ignore les changements d''esquive et de précision. Elle n''empêche pas le switch par utilisation de Relais, Demi-Tour ou de l''objet Carapace Mue.');
INSERT INTO Attaque VALUES (736, 'Tomberoche', '95', '60', '15', 'Roche', 'Physique', 'Des rochers frappent l''ennemi. Réduit aussi sa Vitesse.');
INSERT INTO Attaque VALUES (737, 'Tonnerre', '100', '90', '15', 'Electrik', 'Spéciale', 'Une grosse décharge électrique tombe sur l''ennemi. Peut aussi le paralyser.');
INSERT INTO Attaque VALUES (738, 'Torgnoles', '85', '15', '10', 'Normal', 'Physique', 'Touche 2 à 5 fois en un tour. Il y a 37.5% de chance de toucher 2 ou 3 fois, et 12.5% de chance de toucher 4 ou 5 fois.');
INSERT INTO Attaque VALUES (739, 'Tornade', '100', '40', '35', 'Vol', 'Spéciale', 'La puissance est doublée face à un Pokémon utilisant Rebond ou Vol.');
INSERT INTO Attaque VALUES (740, 'Torrent de Coups', '100', '25', '5', 'Eau', 'Physique', 'Ignore les effets de la cible et inflige un coup critique à coup sûr. Touche trois fois.');
INSERT INTO Attaque VALUES (741, 'Tour de Magie', '100', '-', '10', 'Psy', 'Statut', 'Le lanceur prend la cible au dépourvu et l''oblige à échanger son objet contre le sien.');
INSERT INTO Attaque VALUES (742, 'Tour Rapide', '100', '50', '40', 'Normal', 'Physique', 'Libère le lanceur de Vampigraine, et débarasse le terrain des Picôts, Pics Toxiks ou Piège de Roc. Libère le lanceur de Claquoir, Danseflamme, Etreinte, Ligotage, Tourbi-Sable, Siphon et Vortex Magma. N''a aucun effet sur un Spectre.');
INSERT INTO Attaque VALUES (743, 'Tourbi-Sable', '85', '35', '15', 'Sol', 'Physique', 'Le lanceur emprisonne l''ennemi dans une tempête de sable terrifiante qui dure de quatre à cinq tours.');
INSERT INTO Attaque VALUES (744, 'Tourmente', '100', '-', '15', 'Ténèbres', 'Statut', 'Le lanceur irrite l''ennemi pour l''empêcher d''utiliser la même capacité deux fois de suite.');
INSERT INTO Attaque VALUES (745, 'Tourniquet', '-', '-', '15', 'Eau', 'Statut', 'Les attaques de types feu voient leurs dégâts réduits de 50% jusqu''à ce que le lanceur switche.');
INSERT INTO Attaque VALUES (746, 'Tout ou Rien', '100', '1', '5', 'Combat', 'Spéciale', 'Une attaque très risquée. Le lanceur perd tous ses PV restants et inflige autant de dégâts à l''ennemi.');
INSERT INTO Attaque VALUES (747, 'Toxik', '90', '-', '10', 'poison', 'Statut', 'Empoisonne gravement l''ennemi. Les dégâts dus au poison augmentent à chaque tour.');
INSERT INTO Attaque VALUES (748, 'Toxinomax', '-', '10', '10', 'poison', 'Physique', 'Une attaque de type Poison que seuls les Pokémon Dynamax peuvent utiliser. Augmente l''Attaque Spéciale des alliés.');
INSERT INTO Attaque VALUES (749, 'Tranch''Air', '95', '60', '25', 'Vol', 'Spéciale', 'L''attaque possède un taux de coup critique supérieur d''un niveau à la normale.');
INSERT INTO Attaque VALUES (750, 'Tranch''Herbe', '95', '55', '25', 'Plante', 'Physique', 'Des feuilles aiguisées comme des rasoirs entaillent l''ennemi. Taux de critiques élevé.');
INSERT INTO Attaque VALUES (751, 'Tranche-Nuit', '100', '70', '15', 'Ténèbres', 'Physique', 'Le lanceur lacère l''ennemi à la première occasion. Taux de critiques élevé.');
INSERT INTO Attaque VALUES (752, 'Tranche', '100', '70', '20', 'Normal', 'Physique', 'Le taux de coups critiques est un cran au-dessus de la normale.');
INSERT INTO Attaque VALUES (753, 'Trempette', '-', '-', '40', 'Normal', 'Statut', 'Le lanceur barbote et éclabousse les environs. Cette capacité n''a aucun effet.');
INSERT INTO Attaque VALUES (754, 'Trépignement', '100', '75', '10', 'Sol', 'Physique', 'Le lanceur attaque en utilisant sa frustration. S''il a utilisé une capacité qui a échoué au tour précédent, la puissance de Trépignement est doublée.');
INSERT INTO Attaque VALUES (755, 'Tricherie', '100', '95', '15', 'Ténèbres', 'Physique', 'Le lanceur utilise la force de l''ennemi. Plus l''Attaque de l''ennemi est élevée, plus le lanceur inflige de dégâts.');
INSERT INTO Attaque VALUES (756, 'Triplattaque', '100', '80', '10', 'Normal', 'Spéciale', 'Le lanceur envoie trois boules d''énergie simultanément. Peut aussi paralyser, brûler ou geler l''ennemi.');
INSERT INTO Attaque VALUES (757, 'Triple Axel', '90', '20', '10', 'Glace', 'Physique', 'Attaque à 3 coups dont la puissance augmente à chaque coup porté.');
INSERT INTO Attaque VALUES (758, 'Triple Pied', '90', '10', '10', 'Combat', 'Physique', 'L''attaque s''exécute 3 fois dans le tour. La puissance est de 10 pour le premier coup, 20 pour le second et 30 pour le dernier. Chaque coup possède sa propre précision, et l''attaque s''arrête dès qu''un coup rate ou que les 3 coups sont dépensés.');
INSERT INTO Attaque VALUES (759, 'Troquenard', '100', '35', '15', 'Plante', 'Physique', 'Le lanceur piège sa cible dans son Troquenard d''acier et lui inflige des dégâts pendant quatre ou cinq tours.');
INSERT INTO Attaque VALUES (760, 'Trou Noir des Ombres', '-', '1', '-', 'Ténèbres', 'Physique', 'Le Pokémon utilise la Force Z pour rassembler l’énergie négative et y aspirer l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (761, 'Trou Noir des Ombres', '-', '1', '-', 'Ténèbres', 'Spéciale', 'Le Pokémon utilise la Force Z pour rassembler l’énergie négative et y aspirer l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (762, 'Trou Noir', '50', '-', '10', 'Ténèbres', 'Statut', 'Endort toutes les cibles.');
INSERT INTO Attaque VALUES (763, 'TrouDimensionnel', '-', '80', '5', 'Psy', 'Spéciale', 'Crée une faille dimensionnelle pour attaquer soudainement l''ennemi de côté. Ignore même les capacités comme Abri ou Détection.');
INSERT INTO Attaque VALUES (764, 'Tunnel', '100', '80', '10', 'Sol', 'Physique', 'Le lanceur creuse au premier tour et frappe au second.');
INSERT INTO Attaque VALUES (765, 'Tunnelier', '95', '80', '10', 'Sol', 'Physique', 'Le lanceur tourne sur lui-même comme une perceuse et se jette sur l''ennemi. Taux de critiques élevé.');
INSERT INTO Attaque VALUES (766, 'Turbo-Charge Bulldozer', '-', '1', '-', 'Normal', 'Physique', 'Le Pokémon utilise la Force Z pour s’élancer à toute vitesse sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (767, 'Turbo-Charge Bulldozer', '-', '1', '-', 'Normal', 'Spéciale', 'Le Pokémon utilise la Force Z pour s’élancer à toute vitesse sur l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (768, 'Ultimapoing', '85', '80', '20', 'Normal', 'Physique', 'L''ennemi reçoit un coup de poing d''une puissance incroyable.');
INSERT INTO Attaque VALUES (769, 'Ultimawashi', '75', '120', '5', 'Normal', 'Physique', 'Un coup de pied surpuissant qui frappe l''ennemi.');
INSERT INTO Attaque VALUES (770, 'Ultime Bastion', '-', '-', '5', 'Combat', 'Statut', 'Le lanceur voit toutes ses stats augmenter, mais en contrepartie, il ne peut plus fuir ou se retirer du combat.');
INSERT INTO Attaque VALUES (771, 'Ultralaser', '90', '150', '5', 'Normal', 'Spéciale', 'Projette un puissant rayon sur l''ennemi. Le lanceur doit se reposer au tour suivant.');
INSERT INTO Attaque VALUES (772, 'Ultrason', '55', '-', '20', 'Normal', 'Statut', 'L''adversaire est confus.');
INSERT INTO Attaque VALUES (773, 'Uppercut', '100', '70', '10', 'Normal', 'Physique', 'L''attaque a 20% de chance de rendre confus l''adversaire.');
INSERT INTO Attaque VALUES (774, 'Vague Psy', '100', '1', '15', 'Psy', 'Spéciale', 'Les dégâts sont égaux à 0.5x, 0.6x, 0.7x, 0.8x, 0.9x, 1.0x, 1.1x, 1.2x, 1.3x, 1.4x, or 1.5x le niveau du lanceur.');
INSERT INTO Attaque VALUES (775, 'Vampi-Poing', '100', '75', '10', 'Combat', 'Physique', 'Un coup de poing qui draine l''énergie. Convertit la moitié des dégâts infligés en PV pour le lanceur.');
INSERT INTO Attaque VALUES (776, 'Vampibaiser', '100', '50', '10', 'Fée', 'Spéciale', 'Aspire la force vitale de l''ennemi par un baiser. Rend au lanceur un nombre de PV supérieur ou égal à la moitié des dégâts infligés.');
INSERT INTO Attaque VALUES (777, 'Vampigraine', '90', '-', '10', 'Plante', 'Statut', 'A la fin de chaque tour, 1/8 des PV Max de la cible sont transférés au lanceur. Si la cible possède la capacité spéciale Suintement, le lanceur perdra des PV au lieu d''en gagner. Vampigraine n''a aucun effet sur les Pokémon Plante et continue tant que la cible n''a pas quitté le terrain.');
INSERT INTO Attaque VALUES (778, 'Vampirisme', '100', '80', '10', 'Insecte', 'Physique', 'Une attaque qui aspire le sang de l''ennemi. La moitié des dégâts sont convertis en PV pour le lanceur.');
INSERT INTO Attaque VALUES (779, 'Vantardise', '85', '-', '15', 'Normal', 'Statut', 'Fait enrager la cible et la plonge dans la confusion, mais augmente beaucoup son Attaque.');
INSERT INTO Attaque VALUES (780, 'Vapeur Féérique', '95', '90', '10', 'Fée', 'Spéciale', 'Le lanceur émet de la vapeur pour attaquer sa cible. Peut aussi la rendre confuse.');
INSERT INTO Attaque VALUES (781, 'Vaste Pouvoir', '100', '80', '10', 'Psy', 'Spéciale', 'Si le Champ Psychique est actif, la puissance augmente et la capacité touche tous les ennemis.');
INSERT INTO Attaque VALUES (782, 'Végé-Attaque', '90', '150', '5', 'Plante', 'Spéciale', 'Un violent coup de racines s''abat sur l''ennemi. Immobilise le lanceur au tour suivant.');
INSERT INTO Attaque VALUES (783, 'Vendetta', '100', '60', '10', 'Combat', 'Physique', 'Une attaque deux fois plus puissante si le lanceur a été blessé par l''ennemi durant ce tour.');
INSERT INTO Attaque VALUES (784, 'Vengeance', '100', '70', '5', 'Normal', 'Physique', 'Venge un Pokémon de l''équipe mis K.O. Si un Pokémon de l''équipe a été mis K.O. au tour d''avant, l''effet augmente.');
INSERT INTO Attaque VALUES (785, 'Vent Argenté', '100', '60', '5', 'Insecte', 'Spéciale', 'Attaque poudreuse qui peut augmenter les capacités.');
INSERT INTO Attaque VALUES (786, 'Vent Arrière', '-', '-', '15', 'Vol', 'Statut', 'Pour 3 tours, les Pokémon de l''équipe du lanceur voient leur vitesse doublée. Cela ne compte pas comme un effet de boost. L''attaque perdure même si le lanceur switche.');
INSERT INTO Attaque VALUES (787, 'Vent Féérique', '100', '40', '30', 'Fée', 'Spéciale', 'Déchaîne un vent magique qui cingle l''ennemi.');
INSERT INTO Attaque VALUES (788, 'Vent Glace', '95', '55', '15', 'Glace', 'Spéciale', 'Une bourrasque de vent froid blesse l''ennemi. Réduit aussi sa Vitesse.');
INSERT INTO Attaque VALUES (789, 'Vent Mauvais', '100', '60', '5', 'Spectre', 'Spéciale', 'L''attaque a 10% de chance d''augmenter l''Attaque, la Défense, l''Attaque Spéciale, la Défense Spéciale et la Vitesse du lanceur d''un niveau.');
INSERT INTO Attaque VALUES (790, 'Vent Violent', '70', '110', '10', 'Vol', 'Spéciale', 'Le lanceur déclenche une tempête de vents violents qui s''abat sur l''ennemi. Peut aussi le rendre confus.');
INSERT INTO Attaque VALUES (791, 'Verrou Enchanté', '-', '-', '10', 'Fée', 'Statut', 'Des chaînes entourent la zone de combat, empêchant tous les Pokémon de fuir au prochain tour.');
INSERT INTO Attaque VALUES (792, 'Verrouillage', '-', '-', '5', 'Normal', 'Statut', 'L''attaque utilisée lors du prochain tour réussira à coup sûr. Verrouillage ignore les modifications de précision ou d''esquive. Si la cible utilise Abri ou Détection après être touché par Verrouillage, alors l''attaque utilisée par le lanceur ce tour a 100 - Précision de base chance de passer à travers Abri et Détection. Cette possibilité ne s''applique pas aux attaques OHKO.');
INSERT INTO Attaque VALUES (793, 'Vibra Soin', '-', '-', '10', 'Psy', 'Statut', 'Une aura de bien-être fait récupérer la moitié de ses PV max à la cible.');
INSERT INTO Attaque VALUES (794, 'Vibraqua', '100', '60', '20', 'Eau', 'Spéciale', 'Le lanceur envoie un puissant jet d''eau sur l''ennemi. Peut le rendre confus.');
INSERT INTO Attaque VALUES (795, 'Vibrécaille', '100', '110', '5', 'Dragon', 'Spéciale', 'Le lanceur déclenche un vacarme en frottant ses écailles les unes contre les autres pour attaquer. Baisse la Défense du lanceur.');
INSERT INTO Attaque VALUES (796, 'Vibrobscur', '100', '80', '15', 'Ténèbres', 'Spéciale', 'Le lanceur dégage une horrible aura chargée de pensées maléfiques. Peut aussi apeurer l''ennemi.');
INSERT INTO Attaque VALUES (797, 'Vif Roc', '100', '40', '20', 'Roche', 'Physique', 'Le lanceur charge l''ennemi à toute vitesse. Frappe en priorité.');
INSERT INTO Attaque VALUES (798, 'Vigilance', '-', '-', '10', 'Fée', 'Statut', 'Utilise une force mystérieuse pour protéger l''équipe des capacités de statut. Ne protège pas des autres capacités.');
INSERT INTO Attaque VALUES (799, 'Vitesse Extrême', '100', '80', '5', 'Normal', 'Physique', 'Attaque à priorité +1.');
INSERT INTO Attaque VALUES (800, 'Vive-Attaque', '100', '40', '30', 'Normal', 'Physique', 'Attaque à priorité +1.');
INSERT INTO Attaque VALUES (801, 'Voeu Destructeur', '100', '140', '5', 'Acier', 'Spéciale', 'Frappe l''adversaire à la fin du troisième tour de chargement. Les dégâts sont calculés en fonction de l''Attaque Spéciale du lanceur et de la Défense Spéciale de la cible au moment du lancement de l''impact et non quand elle touche. L''Orbe Vie ne booste pas Carnareket et son lanceur ne subit pas ses dégâts de recul. Carnareket ne peut faire un coup critique.');
INSERT INTO Attaque VALUES (802, 'Voeu Soin', '-', '-', '10', 'Psy', 'Statut', 'Le lanceur est mis KO, et le Pokémon suivant voit ses PV restaurés et son statut soigné. Si le Pokémon suivant est mis KO par les Picôts ou Piège de Roc, le Pokémon le remplaçant sera soigné.');
INSERT INTO Attaque VALUES (803, 'Voeu', '-', '-', '10', 'Normal', 'Statut', 'Le Pokémon sur le terrain à la fin du tour suivant le lancement de cette attaque récupère 50% de sa vie. Si le Pokémon est mis KO durant le tour de soin, l''attaque n''affectera pas le remplaçant.');
INSERT INTO Attaque VALUES (804, 'Voile Aurore', '-', '-', '20', 'Glace', 'Statut', 'Réduit les dégâts causés par les capacités physiques et spéciales durant cinq tours. Ne peut être utilisée que lorsqu''il grêle.');
INSERT INTO Attaque VALUES (805, 'Voile Miroir', '100', '1', '20', 'Psy', 'Spéciale', 'Si le lanceur a été touché par une attaque spéciale durant le tour, Voile Miroir inflige le double de dégâts au lanceur de cette attaque. N''affecte pas les Pokémon Ténèbres.');
INSERT INTO Attaque VALUES (806, 'Voix Enjôleuse', '-', '40', '15', 'Fée', 'Spéciale', 'Laisse s''échapper une voix enchanteresse qui inflige des dégâts psychiques à l''ennemi. Touche à coup sûr.');
INSERT INTO Attaque VALUES (807, 'Vol Magnétik', '-', '-', '10', 'Electrik', 'Statut', 'Le lanceur devient immunisé aux attaques Sol pendant 5 tours. Cette attaque échoue si le lanceur est sous l''effet de Racines, ou si la capacité spéciale du lanceur est Lévitation.');
INSERT INTO Attaque VALUES (808, 'Vol', '95', '90', '15', 'Vol', 'Physique', 'Le lanceur s''envole au premier tour et frappe au second.');
INSERT INTO Attaque VALUES (809, 'Vole-Force', '100', '-', '10', 'Plante', 'Statut', 'Rend au lanceur une quantité de PV équivalente à la stat d''Attaque de la cible, puis baisse celle-ci.');
INSERT INTO Attaque VALUES (810, 'Vole-Vie', '100', '20', '25', 'Plante', 'Spéciale', 'Le lanceur regagne 50% des PV perdus par l''adversaire. Ce nombre est arrondi à l''inférieur.');
INSERT INTO Attaque VALUES (811, 'Vortex Magma', '75', '100', '5', 'Feu', 'Spéciale', 'Vortex Magma dure entre 2 et 5 tours en tant qu''effet passif, donc le lanceur peut choisir d''autres attaques après avoir lancé Vortex Magma pendant que celle-ci est toujours active. Elle inflige 1/16 des PV Max de la cible à chaque tour et l''empêche de switcher. L''effet est annulé si le lanceur switche, même si le compteur des 2 à 5 tours n''était pas encore terminé.');
INSERT INTO Attaque VALUES (812, 'Vrille Maximum', '-', '1', '-', 'Acier', 'Physique', 'Le Pokémon utilise la Force Z pour tourner à toute vitesse et écraser l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (813, 'Vrille Maximum', '-', '1', '-', 'Acier', 'Spéciale', 'Le Pokémon utilise la Force Z pour tourner à toute vitesse et écraser l’adversaire. La puissance varie selon celle de la capacité originale.');
INSERT INTO Attaque VALUES (814, 'Yama Arashi', '100', '60', '10', 'Combat', 'Physique', 'Un coup très puissant dont l''effet est toujours critique.');
INSERT INTO Attaque VALUES (815, 'Yoga', '-', '-', '40', 'Psy', 'Statut', 'Augmente l''Attaque du lanceur d''un niveau.');
INSERT INTO Attaque VALUES (816, 'Zénith', '-', '-', '5', 'Feu', 'Statut', 'Fait briller le soleil pendant cinq tours, augmentant la puissance des capacités de type Feu et baissant celle des capacités de type Eau.');
INSERT INTO Attaque VALUES (817, 'Zone Étrange', '-', '-', '10', 'Psy', 'Statut', 'Le lanceur crée une zone mystérieuse où la Défense et la Défense Spéciale de tous les Pokémon sont inversées pendant cinq tours.');
